create schema if not exists hymns_service;

create table hymns_service.hymns(
    id                     serial primary key,
    identifier             text,
    title                  text,
    verses                 json,
    chorus                 text
);




insert into hymns_service.hymns(identifier, title, verses, chorus) values

        ('1'::text, ' Doxologia'::text, '["Justo \u00e9 o Senhor em seus santos caminhos,\nBenigno em todas as suas obras. (bis)\nPerto est\u00e1 o Senhor,\nPerto est\u00e1 dos que o invocam,\nDe todos que o invocam\nEm verdade. Aleluia! Aleluia!"]'::json, NULL)
        ,
        ('10'::text, ' A cria��o e seu Criador'::text, '["V\u00f3s criaturas de Deus Pai,\nTodos erguei a voz, cantai!\nOh, louvai-o! Aleluia!\nTu sol dourado a refulgir,\nTu, lua em prata a reluzir,\nOh, louvai-o! Oh, louvai-o!\nAleluia! Aleluia! Aleluia!", "Oh, boa terra que nos d\u00e1s\nInfindas b\u00ean\u00e7\u00e3os, canta j\u00e1!\nOh, louvai-o! Aleluia!\nFrutos e flores, juntos dai\nA gl\u00f3ria a Deus, Senhor e Pai.\nOh, louvai-o! Oh, louvai-o!\nAleluia! Aleluia! Aleluia!", "V\u00f3s, homens s\u00e1bios e de bem,,\nA todos proclamai tamb\u00e9m!\nOh, louvai-o! Aleluia!\nLouvor ao Filho, gl\u00f3ria ao Pai,\nE ao Santo Esp\u00edrito louvai!\nOh, louvai-o! Oh, louvai-o!\nAleluia! Aleluia! Aleluia!"]'::json, NULL)
        ,
        ('100'::text, ' Louvores a Cristo'::text, '["\u00d3 minha alma sem demora,\nVem a Cristo celebrar,\nE os louvores do seu Nome\nExultante publicar.\nVem, minha alma! Vem minha alma\nSua gra\u00e7a proclamar!", "Minha condi\u00e7\u00e3o t\u00e3o triste\nConheceu o Salvador,\nQue o c\u00e9u desceu a terra\nPara ser meu Redentor!\nOh, sublime, oh, sublime\n\u00c9 seu grande e santo amor!", "Meus pecados carregando\nNo madeiro em meu lugar,\nFoi Jesus crucificado\nE me pode, assim, salvar.\nPara sempre, para sempre\nSeus louvores vou cantar."]'::json, NULL)
        ,
        ('101'::text, ' A voz da salva��o'::text, '["Ouvi a doce voz\nDe Cristo, o Redentor,\nChamar-me para a salva\u00e7\u00e3o\nQue vem do seu amor.", "Fui ao meu Senhor\nQue me convidou;\nBem nenhum havia em mim,\nMas ele me salvou.", "Nas trevas eu vivi,\nIndigno e sem valor;\nPor\u00e9m agora em seu poder,\nEu ando \u00f3 Salvador!", "A Deus eu louvarei\nPor essa reden\u00e7\u00e3o,\nPois tendo Cristo, o Redentor,\nEu tenho a salva\u00e7\u00e3o."]'::json, NULL)
        ,
        ('102'::text, ' O c�u com Cristo'::text, '["Depois que Cristo me salvou,\nEm c\u00e9u o mundo se tornou;\nAt\u00e9 nos meio do sofrer\nEu tenho paz no meu viver.", "Oh! Aleluia! Sim, \u00e9 C\u00e9u.\n\u00c9 C\u00e9u fruir perd\u00e3o aqui!\nNa terra ou mar, por onde for,\nCom o Senhor \u00e9 C\u00e9u ali.", "Mui longe eu via outrora o C\u00e9u;\nMas quando Cristo me valeu,\nEnt\u00e3o senti meu cora\u00e7\u00e3o\nEntrar no c\u00e9u da retid\u00e3o.\nBem pouco importa eu habitar\nEm alto monte, \u00e0 beira mar,\nEm casa ou gruta, boa ou ruim,\n\u00c9 C\u00e9u ali, com Cristo em mim."]'::json, NULL)
        ,
        ('103'::text, ' Salva��o em Cristo'::text, '["Jesus, agora eu bem sei\nQu\u00e3o grande \u00e9 teu amor!\nPois salva\u00e7\u00e3o me deste aqui,\nAceita o meu louvor.", "\u00d3 Cristo, \u00e9s meu Salvador,\nPor eu tenho paz!\nJesus, a ti louvor darei\nPor tudo o que me d\u00e1s.", "Jamais descanso conheci,\nPor ser um pecador;\nMas tu olhaste para mim\nCom teu divino amor.", "Louvor, louvor darei a ti,\n\u00d3 Cristo, meu Senhor!\nProfeta, Sacerdote, Rei,\nDo mundo o Salvador."]'::json, NULL)
        ,
        ('104'::text, ' Linda melodia'::text, '["Mais do que milhares \u00e9 Cristo, meu bom Mestre,\nEle \u00e9 a luz do mundo, a estrela da manh\u00e3!\n\u00c9 o Rei da gl\u00f3ria, e, em meu cora\u00e7\u00e3o,\nContente vou cantando a divinal can\u00e7\u00e3o!", "Cantarei em meu cora\u00e7\u00e3o, esta linda melodia,\nPois eu tenho em meu cora\u00e7\u00e3o\nGozo, paz e alegria.\nCantarei em meu cora\u00e7\u00e3o,\nCantarei, sim, noite e dia.\nAleluia! Aleluia! Feliz sempre cantarei.", "Cristo libertou-me dos males do pecado,\nE agora alegre canto, pois para o c\u00e9u irei.\nCristo resgatou-me, ele \u00e9 o meu Salvador;\nDou toda gl\u00f3ria a ele, meu Mestre e bom Senhor."]'::json, NULL)
        ,
        ('105'::text, ' A certeza do crente'::text, '["N\u00e3o sei porque de Deus o amor\nA mim se revelou,\nPorque Jesus, meu Salvador,\nNa cruz me resgatou.", "Mas eu sei em quem tenho crido\nE estou bem certo que \u00e9 poderoso!\nGuardar\u00e1, pois, o meu tesouro\nAt\u00e9 o dia final.", "N\u00e3o sei o modo como agiu\nO Esp\u00edrito eternal\nQue, um dia, a Cristo me atraiu\nEm convic\u00e7\u00e3o real.", "N\u00e3o sei o que de mal ou bem\n\u00c9 destinado a mim;\nSe maus ou \u00e1ureos dias v\u00eam,\nAt\u00e9 da vida o fim.", "N\u00e3o sei se ainda longe est\u00e1,\nOu muito perto vem\nA hora em que Jesus vir\u00e1\nNa gl\u00f3ria que ele tem."]'::json, NULL)
        ,
        ('106'::text, ' Fonte carmesim'::text, '["H\u00e1 uma fonte carmesim\nQue meu Jesus abriu,\nNo dia em que na cruz, por mim\nMorreu e me remiu.", "Eu creio, sim, eu creio,\nJesus por mim morreu!\nFoi sobre a cruz, com grande amor,\nQue Cristo padeceu.", "E desde que por seu amor,\nO rumo me apontou,\nConfio nele, com fervor,\nPois que me resgatou.", "A sua morte sobre a cruz\nTraz vida imortal!\nE vou morar com Cristo em luz\nNa gl\u00f3ria celestial."]'::json, NULL)
        ,
        ('107'::text, ' Ao p� da cruz'::text, '["Quero estar ao p\u00e9 da cruz,\nQue t\u00e3o rica fonte\nCorre franca, salutar,\nDe Si\u00e3o no monte.", "Sim, na cruz, sim, na cruz\nSempre me glorio!\nE por fim descansarei\nSalvo al\u00e9m do rio.", "A tremer ao p\u00e9 da cruz,\nGra\u00e7a, amor achou-me;\nMatutina estrela, ali,\nRaios seus mandou-me.", "Desta cruz desejo aqui\nSempre recordar-me;\nDela \u00e0 sombra, Salvador,\nQueiras abrigar-me!", "Junto \u00e0 cruz ardendo em f\u00e9,\nSem temor vigio;\nFirme, at\u00e9 a P\u00e1tria ver,\nSanta, al\u00e9m do rio."]'::json, NULL)
        ,
        ('108'::text, ' Afli��o e paz'::text, '["Se paz a mais doce me deres gozar\nSe dor a mais forte sofrer,\nOh, seja o que for, Tu me fazes saber\nQue feliz com Jesus sempre sou!", "Sou feliz com Jesus\nSou feliz com Jesus, meu Senhor!", "Embora me assalte o cruel Satan\u00e1s,\nE ataque com vis tenta\u00e7\u00f5es;\nOh! Certo eu estou, apesar de afli\u00e7\u00f5es,\nQue feliz eu serei com Jesus!", "Meu triste pecado, por meu Salvador\nFoi pago de um modo cabal!\nValeu-me o Senhor, oh, merc\u00ea sem igual!\nSou feliz, gra\u00e7as dou a Jesus!", "A vinda eu anseio do meu Salvador,\nEm breve vir\u00e1 me levar\nAo c\u00e9u, onde eu vou para sempre morar\nCom remidos na luz do Senhor!"]'::json, NULL)
        ,
        ('109'::text, ' O bom Pastor'::text, '["Quero o Salvador comigo,\nAo seu lado sempre andar.\nQuero t\u00ea-lo muito perto,\nNo seu bra\u00e7o descansar.", "Confiado no Senhor,\nProtegido em seu amor,\nSeguirei o meu caminho\nSem tristeza e sem temor.", "Quero o Salvador comigo,\nPorque fraca \u00e9 minha f\u00e9;\nSua voz me d\u00e1 conforto\nQuando me vacila o p\u00e9.", "Quero o Salvador comigo,\nDia a dia, em meu viver;\nAtrav\u00e9s de luz ou sombras,\nNo desgosto e no prazer.", "Quero o Salvador comigo,\nS\u00e1bio guia e Bom Pastor\nNessa vida e al\u00e9m da morte,\nLonge de perigo e dor."]'::json, NULL)
        ,
        ('11'::text, ' Trindade sant�ssima'::text, '["Santo! Santo! Santo! Deus Onipotente!\nLouvam nossas vozes teu nome com fervor!\nSanto! Santo! Santo! Justo e compassivo!\n\u00c9s Deus Tri\u00fano, Excelso Criador!", "Santo! Santo! Santo! N\u00f3s os pecadores,\nN\u00e3o podemos ver tua gl\u00f3ria sem temor.\nTu somente \u00e9s Santo! S\u00f3 tu \u00e9s perfeito,\nDeus Soberano, imenso em teu amor.", "Santo! Santo! Santo! Todos os remidos,\nJuntos com os anjos, proclamam teu louvor!\nAntes de formar-se o firmamento e a terra\nEras, e sempre \u00e9s, e h\u00e1s de ser, Senhor!", "Santo! Santo! Santo! Deus Onipotente!\nTuas obras louvam teu nome com fervor.\nSanto! Santo! Santo! Justo e compassivo!\nDeus Soberano, Excelso Criador! Am\u00e9m"]'::json, NULL)
        ,
        ('110-A'::text, ' Crer e observar'::text, '["Em Jesus confiar, sua lei observar,\nOh, que gozo, que b\u00ean\u00e7\u00e3o, que paz!\nSatisfeito guardar tudo quanto ordenar\nAlegria perene nos traz.", "Crer e observar tudo quanto ordenar\nO fiel obedece ao que Cristo mandar!", "O inimigo falaz, a cal\u00fania mordaz\nCristo sabe desprestigiar;\nNem tristeza, nem dor, nem intriga, a maior,\nPode o crente fiel abalar.", "Grande prova de amor, comunh\u00e3o no Senhor\nTem o crente zeloso e leal!\nO seu rosto mirar, seus segredos privar:\n\u00c9 consolo que n\u00e3o tem igual.", "Resoluto, Senhor, e com zelo e fervor,\nOs teus passos queremos seguir!\nTeus preceitos guardar, o teu nome exaltar,\nSempre a tua vontade cumprir."]'::json, NULL)
        ,
        ('110'::text, ' A vida com Jesus'::text, '["Um dia Cristo achou-me\nMui longe do meu lar,\nPerdido j\u00e1 no mundo,\nSem mais poder voltar!\nTomando-me em seus bra\u00e7os,\nFirmou-me nos meus passos,\nE agora andamos juntos,\nVoltando para o lar.", "A presen\u00e7a de Jesus\nEnche o cora\u00e7\u00e3o de luz!\nMui preciosa fica,\nE tamb\u00e9m mais rica\nEsta vida com Jesus", "Passamos pelo vale\nA fim de me provar,\nEm meio a densas trevas\nQue impedem-me o andar!\nN\u00e3o h\u00e1 nenhum perigo,\nPois j\u00e1 est\u00e1 comigo\nO excelso Companheiro,\nVoltando para o lar.", "Entramos na atmosfera\nQue envolve a aquele lar,\nNo qual meu Pai me aguarda\nE \u00e9 onde vou morar!\nN\u00e3o h\u00e1 no mundo inteiro\nUm outro companheiro\nQue mostre tanto zelo\nVoltando para o lar."]'::json, NULL)
        ,
        ('111'::text, ' Comunh�o Divina'::text, '["Comigo est\u00e1s, \u00f3 Deus, a noite vem!\nAs trevas crescem e o temor tamb\u00e9m!\nSocorro tenho e vera prote\u00e7\u00e3o,\nPorque em mim fizeste habita\u00e7\u00e3o.", "Vem revelar a mim, \u00f3 meu Senhor\nDivino Mestre, Rei, Consolador,\nO teu amparo em toda a tenta\u00e7\u00e3o,\nFazendo em mim constante habita\u00e7\u00e3o.", "Andando aqui, nas trevas ou na luz,\nN\u00e3o h\u00e1 perigo estando com Jesus!\nMedo e pavor n\u00e3o prevalecer\u00e3o,\nPois em meu ser fizeste habita\u00e7\u00e3o.", "\u00d3 morte os teus poderes v\u00e3o findar!\nEm Cristo os mortos v\u00e3o ressuscitar!\nNo Reino eterno h\u00e1 gozo e exulta\u00e7\u00e3o,\nE com Jesus eterna habita\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('112'::text, ' Rica promessa'::text, '["\u201cN\u00e3o temas! Contigo eu sempre estarei!\u201d\nOh, rica promessa do bondoso Rei!\nQual astro que brilha na escurid\u00e3o,\nA mais linda promessa brilha no meu cora\u00e7\u00e3o!", "Comigo estar! Comigo estar!\nSim, Jesus me promete comigo estar!", "As luzes do mundo se apagar\u00e3o,\nOs dias mais belos breve passar\u00e3o;\nJesus, Luz do mundo, n\u00e3o se extinguir\u00e1,\nE essa Luz resplendente sempre em mim brilhar\u00e1.", "E se pelas \u00e1guas tiver de passar,\nSeus bra\u00e7os eternos h\u00e3o de me guardar!\nSim, mesmo no fogo que me vem provar,\nMeu Senhor me promete sempre comigo estar."]'::json, NULL)
        ,
        ('113'::text, ' Achei um bom amigo'::text, '["Achei um bom Amigo, Jesus, o Salvador,\nDos milhares o escolhido para mim.\nEle \u00e9 a luz do mundo, o forte Mediador,\nQue me purifica e guarda at\u00e9 o fim!\nConsolador amado, meu protetor do mal,\nEle pode dar al\u00edvio ao meu pesar.", "Ele \u00e9 a luz do mundo, a Estrela da manh\u00e3,\nDos milhares o escolhido para mim.", "Levou-me as dores todas, as m\u00e1goas lhe entreguei.\nNele tenho firme abrigo em tenta\u00e7\u00e3o!\nDeixei por ele tudo, os \u00eddolos queimei!\nEle faz-me puro e santo o cora\u00e7\u00e3o!\nQue o mundo me abandone, persiga o tentador,\nMeu Jesus me guarda at\u00e9 da vida ao fim.", "Jamais me desampara, nem me abandonar\u00e1,\nSe fiel e obediente aqui viver!\nEst\u00e1 sempre ao meu lado, e me proteger\u00e1,\nAt\u00e9 quando face a face o possa ver!\nEnt\u00e3o, aos c\u00e9us subindo, na gl\u00f3ria eu me verei\nCom Jesus, meu Salvador, morando, enfim."]'::json, NULL)
        ,
        ('114'::text, ' Brilho celeste'::text, '["Peregrinando por sobre os montes,\nE pelos vales, sempre na luz,\nCristo promete nunca deixar-me,\n\u201cEis-me convosco\u201d - disse Jesus.", "Brilho celeste! Brilho celeste!\nEnche a minha alma, gl\u00f3ria do c\u00e9u!\nAleluia! Sigo cantando,\nDando louvores, pois Cristo \u00e9 meu!", "Se vejo sombras por toda parte,\nO Salvador n\u00e3o h\u00e1 de ocultar!\nPois Cristo \u00e9 luz que nunca se apaga,\nBem ao seu lado sempre hei de andar.", "A luz bendita me vai guiando\nEm meu caminho para a Mans\u00e3o;\nMais e mais perto seguindo o Mestre,\nPossuo o gozo da salva\u00e7\u00e3o"]'::json, NULL)
        ,
        ('115'::text, ' Unido com Cristo'::text, '["Com Cristo unido na morte da cruz,\nEu gozo as b\u00ean\u00e7\u00e3os do reino da luz!\nCheio da gra\u00e7a que ali corre a flux,\nCada momento, com Cristo Jesus.", "Cada momento me guia o Senhor\nCada momento dispensa favor;\nSua presen\u00e7a me outorga vigor;\nCada momento sou teu, \u00f3 Senhor!", "Com Cristo unido na luta moral\nResisto ao erro, ao pecado fatal,\nBem alto erguendo a bandeira real.\nCada momento mais firme e leal!", "Com Cristo unido na ressurrei\u00e7\u00e3o,\nEu j\u00e1 desfruto eternal reden\u00e7\u00e3o;\nNele jamais esperamos em v\u00e3o;\nCada momento concede perd\u00e3o!", "Nas minhas lutas me pode amparar\nE do maligno tamb\u00e9m me livrar!\nCada momento, por onde eu andar,\nCristo, meu Mestre, me pode guiar!"]'::json, NULL)
        ,
        ('116'::text, ' Uni�o com Deus'::text, '["Mais perto quero estar, meu Deus de ti,\nInda que seja a dor que me una a ti!\nSempre hei de suplicar: \u201cMais perto quero estar,\nMais perto quero estar, meu Deus de ti!\u201d", "Mesmo vagando aqui, na solid\u00e3o,\nDe noite, a descansar, dormindo ao ch\u00e3o,\nEm sonhos vou clamar: \u201cMais perto quero estar,\nMais perto quero estar, meu Deus de ti!\u201d", "Minha alma cantar\u00e1 a ti, Senhor!\nBetel aqui verei por teu favor.\nEu sempre hei de rogar: \u201cMais perto quero estar,\nMais perto quero estar, meu Deus de ti!\u201d", "E quando a morte, enfim, me vier chamar,\nNos c\u00e9us, com o Senhor, irei morar!\nEnt\u00e3o me alegrarei, perto de ti meu Rei!\nPerto de ti meu Rei, meu Deus, de ti! Am\u00e9m."]'::json, NULL)
        ,
        ('117'::text, ' Pastor divino'::text, '["Eis-nos, \u00f3 Pastor divino,\nTodos juntos num lugar,\nComo ovelhas congregados,\nTeu aux\u00edlio a suplicar.\n\u00c9s presente, \u00e9s presente\nO rebanho a apascentar.", "Guia os tristes fatigados\nAo teu bom redil Senhor.\nLeva os tenros cordeirinhos\nNos teus bra\u00e7os, Bom Pastor.\n\u00c0s pastagens, \u00e0s pastagens\nDe celeste e doce amor.", "\u00d3 Jesus bondoso, escuta\nNossa humilde peti\u00e7\u00e3o!\nVem encher o teu rebanho\nDe sincera gratid\u00e3o!\nCantaremos, cantaremos\nTua imensa compaix\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('118'::text, ' Esperando em Deus'::text, '["Qual suspira a cor\u00e7a inquieta\nPelas \u00e1guas a bramir,\nTua divinal presen\u00e7a\nQuer minha alma, \u00f3 Deus, sentir.\nSede intensa me angustia!\nQuando \u00f3 Deus, vir\u00e1 o dia\nDe cont\u00ednuo me alegrar\nPor teu rosto contemplar?", "Pranteando amargamente,\nVou vivendo os dias meus,\nPois os \u00edmpios me atormentam\nPerguntando por meu Deus.\nChoro ent\u00e3o os velhos dias,\nQuando, em santas alegrias,\nProclamava o teu louvor\nJunto a ti, \u00f3 meu Senhor.", "Em rajadas tormentosas\nTeu ju\u00edzo me alcan\u00e7ou!\nE minha alma, bem ferida,\nPenitente se humilhou.\nTu, ent\u00e3o, meus p\u00e9s guiando,\nCom ternura me amparando,\nAlto abrigo me vens dar\nE na Rocha me firmar!", "Por que tremes, \u00f3 minha alma,\nE te abates dentro em mim?\nPara longe os teus receios!\nDeus ao teu penar p\u00f5e fim!\nEle as ondas m\u00e1s quebranta,\nNos seus bra\u00e7os te levanta.\n\u00d3 minha alma, sem temor,\nCanta a Deus o teu louvor. Am\u00e9m."]'::json, NULL)
        ,
        ('119'::text, ' S�plica pelo culto'::text, '["Fala, \u00f3 Deus, agora pela prega\u00e7\u00e3o,\nDespertando em todos santa inspira\u00e7\u00e3o!", "Vem, pelo Evangelho, almas despertar!\nOs remidos todos vem santificar!", "Manda \u00e0 tua Igreja mais poder, Senhor,\nPela boa-nova do teu pregador!", "Torna-a instrumento da verdade e luz,\nDo amor e gra\u00e7a que v\u00eam de Jesus. Am\u00e9m."]'::json, NULL)
        ,
        ('12'::text, ' Gl�ria a Deus'::text, '["Santo! Santo! Santo!\nDeus dos Ex\u00e9rcitos,\nA terra e os c\u00e9us\nProclamam tua gl\u00f3ria.\nGl\u00f3ria a ti, gl\u00f3ria a ti, \u00f3 Deus,\nEternamente. Am\u00e9m."]'::json, NULL)
        ,
        ('120'::text, ' Depend�ncia'::text, '["Care\u00e7o de Jesus, de ti, meu Salvador!\nSomente a tua voz tem para mim valor.", "De ti, Senhor care\u00e7o!\nDo teu amparo sempre!\nOh, d\u00e1-me tua b\u00ean\u00e7\u00e3o!\nAspiro a ti.", "Care\u00e7o de Jesus! Unido a ti Senhor,\nPecado e tenta\u00e7\u00e3o n\u00e3o mais ter\u00e3o vigor", "Care\u00e7o de Jesus, nas trevas e nas luz!\nSem ti a vida \u00e9 v\u00e3, sou pobre sem Jesus.", "Care\u00e7o de Jesus! Viver desejo aqui,\nLigado mais e mais, \u00f3 Salvador a ti! Am\u00e9m."]'::json, NULL)
        ,
        ('121'::text, ' Perfei��o'::text, '["Mais pureza d\u00e1-me, mais horror ao mal,\nMais calma em pesares, mais alto ideal;\nMais f\u00e9 no meu Mestre, mais consagra\u00e7\u00e3o,\nMais gozo em servi-lo, mais grata ora\u00e7\u00e3o.", "Mais Prud\u00eancia d\u00e1-me, mais paz, meu Senhor,\nMais firmeza em Cristo, mais for\u00e7a na dor;\nMais reto me torna, mais triste ao pecar,\nMais humilde filho, mais pronto em te amar", "Mais confian\u00e7a d\u00e1-me, mais for\u00e7a em Jesus,\nMais do seu dom\u00ednio, mais da sua luz;\nMais rica esperan\u00e7a, mais obras aqui,\nMais \u00e2nsias da Gl\u00f3ria, mais vida em ti. Am\u00e9m."]'::json, NULL)
        ,
        ('122'::text, ' Necessitado'::text, '["\u00c0 minha voz, \u00f3 Deus, atende,\nPois noite e dia clamo a ti!\nT\u00e3o d\u00e9bil sou, t\u00e3o pobre aqui,\nA meus queixumes e gemidos\nVem dar ouvidos!", "Da vida e luz Tu \u00e9s a fonte!\nEm mim opera o teu poder!\nMinha ora\u00e7\u00e3o vem receber,\nPois de meu leito o sol vigio\nE em ti confio.", "N\u00e3o \u00e9s um Deus que te comprazes\nNas transgress\u00f5es do pecador\nBondoso e justo \u00e9s Tu Senhor!\nE teu favor jamais consentes\nAos maldizentes.", "Meus p\u00e9s, \u00e0 luz de teus caminhos,\nHumilde e fiel dirigirei.\nTu \u00e9s meu Deus, Tu \u00e9s meu Rei!\n\u00c0 tua vista andar eu quero,\nPuro e sincero", "Em ti exultar\u00e3o os justos,\nRejubilando em tua paz.\nDe todo o mal os guardar\u00e1s\nPois tua Lei, \u00f3 Deus, conhecem\nE te obedecem. Am\u00e9m."]'::json, NULL)
        ,
        ('123'::text, ' Grande amor'::text, '["\u00d3 Senhor, que a tudo excedes,\nDom celeste, amor sem par,\nOh, coroa de favores\nNosso fr\u00e1gil caminhar.\nGrande amor, amor bendito,\n\u00d3 divina compaix\u00e3o,\nVem socorre ao que padece,\nE estende-lhe a m\u00e3o.", "\u00d3 Senhor, a cada dia\nD\u00e1-nos tua inspira\u00e7\u00e3o!\nOh, remove o mau desejo\nQue nos tenta o cora\u00e7\u00e3o.\nTu, somente, nos conheces\nE nos podes proteger.\nD\u00e1-nos, pois, a tua gra\u00e7a\nE com ela o teu poder.", "\u00d3 Senhor, n\u00e3o te separes\nDo rebanho terreal!\nUne a Igreja, estreitamente,\nD\u00e1-lhe vida fraternal.\nAben\u00e7oa todo crente,\nIlumina-lhe o andar,\nE que todos se comprazam\nEm teu Nome proclamar. Am\u00e9m."]'::json, NULL)
        ,
        ('124'::text, ' Benigna luz'::text, '["Benigna luz, os passos vacilantes\nMe vem guiar!\n\u00c9 noite escura, e vejo os c\u00e9us distantes,\nMeu doce lar.\nN\u00e3o pe\u00e7o ver da estrada as longes cenas;\nOh! Basta um passo, um passo mais, apenas.", "Nem sempre humilde supliquei outrora\nA tua luz,\nPor\u00e9m minha alma o teu perd\u00e3o implora,\n\u00d3 meu Jesus!\nO orgulho v\u00e3o minha alma endurecia;\nOh! D\u00e1-me aqui vis\u00e3o de um novo dia.", "O teu poder conduzir\u00e1 seguro\nMeu caminhar\nDescansarei, vencido o trilho escuro,\nNo doce lar.\nAli verei, na luz da eterna aurora,\nA tua face que n\u00e3o vejo agora. Am\u00e9m."]'::json, NULL)
        ,
        ('125'::text, ' Sempre veraz'::text, '["Sempre veraz serei, pois me acreditem;\nForte serei, pois muito h\u00e1 que sofrer;\nPuro serei, no exemplo do meu Mestre,\nNas prova\u00e7\u00f5es ousado espero ser! (bis)", "De todos quero sempre ser amigo,\nSer generoso e o bem n\u00e3o alegar;\nSendo imperfeito, eu hei de ser humilde,\nSempre hei de crer, confiar, servir e amar. (bis)", "Para que eu seja sempre assim, \u00f3 Cristo,\nVem dominar, mudar meu cora\u00e7\u00e3o.\nPois eu bem sei dos la\u00e7os em que caio,\nSe n\u00e3o contar com tua prote\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('126'::text, ' Depend�ncia'::text, '["De ti, meu bom Senhor,\nEu tenho precis\u00e3o;\nS\u00f3 teu divino amor\nD\u00e1 paz ao cora\u00e7\u00e3o.", "\u00d3 meu Jesus, comigo\nTu sempre h\u00e1s de ficar,\nAt\u00e9 que eu v\u00e1 contigo,\nSenhor, no c\u00e9u morar!", "Concede-me, Jesus,\nFruir teu rico amor!\nE andar em tua luz\nSubmisso a ti, Senhor.", "Vencendo a tenta\u00e7\u00e3o\nContente viverei\nNa tua prote\u00e7\u00e3o,\n\u00d3 meu bendito Rei! Am\u00e9m."]'::json, NULL)
        ,
        ('127'::text, ' Hora bendita'::text, '["Bendita hora de ora\u00e7\u00e3o,\nQue acalma o aflito cora\u00e7\u00e3o,\nO qual por meio de Jesus,\nAo Pai suplica aux\u00edlio e luz.\nEm tempos de cuidado e dor,\nRef\u00fagio tenho em meu Senhor;\nVencendo o ardil da tenta\u00e7\u00e3o,\nBendigo a hora de ora\u00e7\u00e3o", "Bendita hora de ora\u00e7\u00e3o,\nQuando a fervente peti\u00e7\u00e3o\nSe eleva ao trono do Senhor,\nQue atende a voz do meu clamor!\nPois ele ordena recorrer\nAo seu amor, ao seu poder;\nContente e sem perturba\u00e7\u00e3o,\nEu busco a hora de ora\u00e7\u00e3o.", "Bendita hora de ora\u00e7\u00e3o,\nDe santa paz e comunh\u00e3o!\nDesejo, enquanto aqui me achar,\nCom f\u00e9 constante, humilde, orar.\nE, enfim, no resplendor de Deus,\nNa gl\u00f3ria dos mais altos c\u00e9us,\nLembrar-me-ei, com gratid\u00e3o,\nDas horas suaves de ora\u00e7\u00e3o."]'::json, NULL)
        ,
        ('128'::text, ' Comunh�o preciosa'::text, '["Preciosas s\u00e3o as horas\nNa presen\u00e7a de Jesus!\nComunh\u00e3o deliciosa\nDa minha alma com a luz\nOs cuidados deste mundo\nN\u00e3o me podem abalar,\nPois \u00e9 ele o meu abrigo\nQuando o tentador chegar. (bis)", "Ao sentir-me rodeado\nDe cuidados terreais,\nIrritado e abatido,\nOu em d\u00favidas fatais,\nA Jesus eu me dirijo\nNesses tempos de afli\u00e7\u00e3o;\nAs palavras que ele fala\nTrazem paz consola\u00e7\u00e3o. (bis)", "Se confesso meus temores,\nToda a minha imperfei\u00e7\u00e3o,\nEle escuta com paci\u00eancia\nEssa triste confiss\u00e3o.\nCom ternura repreende\nMeu pecado e todo o mal;\n\u00c9 Jesus o meu Amigo,\nO melhor e o mais leal. (bis)", "Se quereis saber qu\u00e3o doce\n\u00c9 a divina comunh\u00e3o,\nPodereis mui bem prov\u00e1-la\nE tereis compensa\u00e7\u00e3o.\nProcurai estar sozinhos\nEm conversa com Jesus,\nProvareis na vossa vida\nO poder que vem da cruz. (bis)"]'::json, NULL)
        ,
        ('129'::text, ' Vigil�ncia e ora��o'::text, '["Bem de manh\u00e3, embora o c\u00e9u sereno\nPare\u00e7a um dia calmo anunciar,\nVigia e ora: o cora\u00e7\u00e3o pequeno\nUm temporal pode abrigar.", "Bem de manh\u00e3, e sem cessar,\nVigiar e orar!", "Ao meio dia, quando os sons da terra\nAbafam mais de Deus a voz de amor,\nRecorre \u00e0 ora\u00e7\u00e3o, evita a guerra,\nE goza paz com o Senhor.", "Do dia ao fim, ap\u00f3s os teus lidares,\nRelembra as b\u00ean\u00e7\u00e3os do celeste amor,\nE conta a Deus prazeres e pesares,\nDepondo em suas m\u00e3os a dor.", "E sem cessar vigia a todo instante,\nPois o inimigo ataca sem parar;\nS\u00f3 com Jesus, em comunh\u00e3o constante,\nPodemos sempre descansar."]'::json, NULL)
        ,
        ('13'::text, ' Contempla��o'::text, '["Se nos cega o sol ardente\nQuando visto em seu fulgor,\nQuem contemplar\u00e1 Aquele\nQue do sol \u00e9 Criador?\nPatriarcas n\u00e3o puderam\nO seu rosto contemplar,\nNem Ad\u00e3o chegou a v\u00ea-lo\nAntes mesmo de pecar!", "Luz perante a qual \u00e9 trevas\nMesmo o sol a fulgurar!\nNossos olhos pecadores\nN\u00e3o te podem contemplar!\nFogo em cima da arca santa,\nSar\u00e7a ardente do Sinai\nS\u00e3o figuras desta gl\u00f3ria\nDo Senhor e Eterno Pai.", "Para termos n\u00f3s com ele\nFranca e doce comunh\u00e3o,\nCristo, o Filho fez-se carne,\nFez-se nossa Reden\u00e7\u00e3o!\nPara que na gl\u00f3ria eterna\nO vejamos j\u00e1 sem v\u00e9u,\nCristo padeceu a morte,\nO caminho abrindo ao c\u00e9u."]'::json, NULL)
        ,
        ('130'::text, ' Ora��o ao Senhor'::text, '["Dirijo a ti,Senhor, minha ora\u00e7\u00e3o;\nEu sei que tudo v\u00eas no cora\u00e7\u00e3o.\nA ti venho adorar, a gra\u00e7a suplicar,\nO teu favor buscar, Senhor meu Deus.", "Dirijo a ti, Senhor, minha ora\u00e7\u00e3o;\nDo mal que pratiquei, a confiss\u00e3o!\nS\u00ea Tu, \u00f3 meu Senhor, prop\u00edcio ao pecador,\nConcede, em teu amor, pleno perd\u00e3o.", "Dirijo a ti, Senhor, minha ora\u00e7\u00e3o,\nDivino amparo \u00e9s em afli\u00e7\u00e3o.\nTu podes consolar, minha alma confortar,\nE sempre me guardar de todo o mal.", "Atende, \u00f3 meu Senhor, minha ora\u00e7\u00e3o,\nQue humilde sobe a ti com gratid\u00e3o;\nImploro, meu Senhor, por Cristo, o Mediador,\nQue sempre em teu amor possa eu viver. Am\u00e9m."]'::json, NULL)
        ,
        ('131'::text, ' Vida santificada'::text, '["Para seres santo, tempo h\u00e1s de tomar;\nCom o grande Mestre, seu Livro estudar;\nH\u00e1s de sempre am\u00e1-lo, aos fi\u00e9is valer,\nE as b\u00ean\u00e7\u00e3os celestes de Deus receber.", "Para seres puro, tempo h\u00e1s de encontrar;\nA s\u00f3s com teu Mestre mui freq\u00fcente estar.\nTeu olhar bem fito nele sempre ter,\nTeus atos provando seu grande poder.", "Para seres forte, tempo h\u00e1s de buscar;\nAo Mestre seguindo por onde guiar;\nNo gozo ou tristeza, sempre obedecer,\nAo seu bom conselho com f\u00e9 recorrer.", "Para seres \u00fatil, tempo h\u00e1s de guardar!\nSer calmo e paciente, em todo lugar.\nTer a sua gra\u00e7a, e mostrar amor,\nContente e submisso aos p\u00e9s do Senhor,"]'::json, NULL)
        ,
        ('132'::text, ' Vivifica��o'::text, '["Tu, que sobre a amarga cruz\nRevelaste teu amor,\nTu que vives, \u00f3 Jesus,\nVivifica-nos, Senhor!", "Vem, oh, vem, Jesus Senhor\nNossas almas despertar!\nCom teu santo e puro amor\nVem, Senhor, nos inflamar!\nOh, vem! Oh, vem\nNossas almas inflamar!", "Eis o mundo tentador\nA querer nos atrair,\nSem teu fogo abrasador,\nN\u00e3o podemos resistir.", "Quantos que corriam bem,\nDe ti longe agora v\u00e3o!\nOutros seguem, mas, tamb\u00e9m,\nSem fervor vivendo est\u00e3o.", "Vem agora consumir\nTudo quanto, \u00f3 Salvador,\nQuer altivo resistir\nAo teu brando e santo amor. Am\u00e9m."]'::json, NULL)
        ,
        ('133'::text, ' Aspira��o � santidade'::text, '["Puro, sim, mais puro eu desejo ser!\nSanto, sim, mais santo, sempre em meu viver!\n\u00d3 Senhor, te pe\u00e7o nesta ora\u00e7\u00e3o:\nSatisfaz o anseio do meu cora\u00e7\u00e3o.", "Calmo, sim, mais calmo sempre eu quero estar!\nFirme, sim, mais firme eu desejo andar!\nNunca se enfraque\u00e7a minha f\u00e9, Senhor,\nE que eu, sempre alegre, cante o teu louvor.", "Justo, sim, mais justo quem me dera ser!\nReto, sim, mais reto em meu proceder.\nCristo \u00e9 meu modelo, sempre o seguirei;\nTudo quanto almejo nele encontrarei."]'::json, NULL)
        ,
        ('134'::text, ' Riquezas do c�u'::text, '["Oh! Buscai, n\u00e3o as riquezas\nDeste mundo de incertezas!\nAs do c\u00e9u n\u00e3o t\u00eam tristezas:\nOh! Buscai-as! Sim, buscai!", "Oh! Buscai, n\u00e3o as loucuras\nQue s\u00f3 trazem amarguras,\nMas del\u00edcias santas, puras:\nOh! Buscai-as! Sim, buscai!", "A Jesus buscai primeiro,\nAo real, fiel Cordeiro,\nAo Amigo verdadeiro:\nOh! Buscai-o! Sim, buscai", "Sim, buscai o Deus Bendito,\nCujo amor \u00e9 inaudito,\nInef\u00e1vel, infinito:\nOh, buscai-o! Sim, buscai!"]'::json, NULL)
        ,
        ('135'::text, ' Mais de Cristo'::text, '["Mais de Cristo eu quero ter,\nSeu ensino receber,\nTer da sua compaix\u00e3o\nE da sua mansid\u00e3o.", "Mais,mais de Cristo!\nMais,mais de Cristo!\nMais do seu puro e santo amor\nMais do bondoso Salvador.", "Mais de Cristo eu quero ouvir\nNos seu passos prosseguir,\nSempre perto dele andar,\nSeu amor manifestar."]'::json, NULL)
        ,
        ('136'::text, ' Rocha eterna'::text, '["Rocha eterna, meu Jesus,\nQuero em ti me refugiar!\nO teu sangue l\u00e1 na cruz,\nDerramado em meu lugar,\nTraz as b\u00ean\u00e7\u00e3os do perd\u00e3o:\nGozo, paz e salva\u00e7\u00e3o.", "N\u00e3o por obras nem penar,\nPlena paz terei aqui.\nS\u00f3 Tu podes consolar,\nH\u00e1 perd\u00e3o somente em ti.\nRocha eterna, s\u00f3 na cruz\nEu confio, \u00f3 meu Jesus!", "Quando o derradeiro olhar\nA este mundo aqui volver,\nE no Trono eu te encontrar,\nTeu chamado a responder;\nRocha eterna, espero ali\nAbrigar-me, salvo em ti! Am\u00e9m."]'::json, NULL)
        ,
        ('137'::text, ' Abrigo no temporal'::text, '["Rochedo forte \u00e9 o Senhor,\nRef\u00fagio na tribula\u00e7\u00e3o!\nConstante e firme amparador,\nRef\u00fagio na tribula\u00e7\u00e3o!", "Oh! Cristo \u00e9 nosso abrigo no temporal,\nNa tenta\u00e7\u00e3o, em todo mal\nSim, Cristo \u00e9 nosso abrigo no temporal,\nRef\u00fagio na tribula\u00e7\u00e3o!", "\u00c9 como sombra no calor,\nDescanso na tribula\u00e7\u00e3o!\nRefaz as for\u00e7as, d\u00e1 vigor:\nDescanso na tribula\u00e7\u00e3o!", "Piloto bom no bravo mar,\nFirmeza na tribula\u00e7\u00e3o!\nAncoradouro singular,\nFirmeza na tribula\u00e7\u00e3o!", "Leal Amigo e Benfeitor,\nAux\u00edlio na tribula\u00e7\u00e3o!\nFiel e eterno Salvador,\nAux\u00edlio na tribula\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('138'::text, ' Ref�gio'::text, '["\u00d3 Bondoso Salvador!\nS\u00ea Tu meu amparador!\nNegras ondas de afli\u00e7\u00e3o,\nFortes ventos perto est\u00e3o.\nDeste espanto e do terror,\nVem salvar-me, \u00f3 bom Senhor!\nE no porto faze entrar\nMinha barca sem quebrar!", "Consternado nesta dor,\nSem ref\u00fagio, sem vigor,\nMeu medroso cora\u00e7\u00e3o\nVem rogar-te prote\u00e7\u00e3o.\nMostra o teu imenso amor,\n\u00d3 benigno Salvador!\nPoderosa e clara luz,\nN\u00e3o me deixes, \u00f3 Jesus!", "Compassivo Redentor,\nVale a um triste pecador!\nVida e gozo Tu me d\u00e1s,\nGra\u00e7a infinda, eterna paz.\nEnche o d\u00e9bil cora\u00e7\u00e3o,\nCom os dons da salva\u00e7\u00e3o,\nE seguro, sem temor,\nGozarei do teu favor. Am\u00e9m."]'::json, NULL)
        ,
        ('139'::text, ' O socorro do crente'::text, '["Para os altos montes olharei,\nQuem me socorrer\u00e1?\nDo meu divino protetor\nAux\u00edlio me vir\u00e1.", "No bra\u00e7o forte esperarei\nDo meu amparador!\nPor ele o mundo feito est\u00e1,\nDe tudo ele \u00e9 Senhor.", "O p\u00e9 do servo de Jesus\nJamais vacilar\u00e1!\nO Deus que guarda os filhos seus\nN\u00e3o adormecer\u00e1.", "Do crente \u00e0 m\u00e3o direita est\u00e1\nQuem o protege bem!\nNem sol nem lua o ferir\u00e1,\nSocorro sempre tem.", "Os inimigos dos fi\u00e9is\nOs querem assustar!\nO protegido por Jesus\nSem medo deve andar."]'::json, NULL)
        ,
        ('14'::text, ' Louvor'::text, '["Vamos n\u00f3s louvar a Deus,\nVamos! Vamos!\nAo Senhor de toda a luz,\nSanto! Santo!\nCantem louvem l\u00e1 nos c\u00e9us\nNosso Deus e Rei Jesus!\nExaltado seja Deus,\nSanto! Santo!", "Exaltado seja nosso Deus e Pai!\nExaltado! Para sempre o exaltai!\nCantem, louvem l\u00e1 nos c\u00e9us\nNosso Deus e Rei Jesus!\nExaltado seja Deus,\nSanto! Santo!", "Nosso Deus, eterno Pai,\nSanto! Santo!\nDeu-nos b\u00ean\u00e7\u00e3os por Jesus,\nVede! Vede!\nAo Senhor glorificai,\nV\u00f3s, os salvos pela cruz,\nSim, conosco gl\u00f3ria dai,\nVinde! Vinde!", "Exaltemos nosso Deus,\nSanto! Santo!\nExaltemos com fervor,\nHoje! Hoje!\nTributemos, todos n\u00f3s,\nHinos santos de louvor,\nSim, louvor em alta voz,\nHoje! Hoje!"]'::json, NULL)
        ,
        ('140'::text, ' Jornada do crente'::text, '["Guia, \u00f3 Deus, a minha sorte\nNesta peregrina\u00e7\u00e3o.\nFr\u00e1gil sou mas Tu \u00e9s forte,\nN\u00e3o me largue a tua m\u00e3o", "Quando em meio de inimigos\nAndo cheio de temor,\nOu por entre mil perigos,\nVem guiar-me, \u00f3 Salvador.", "Com o teu man\u00e1 divino\nVem nutrir meu cora\u00e7\u00e3o!\nGuie a nuvem meu destino\nNesta imensa solid\u00e3o.", "Fende a rocha milagrosa,\nD\u00e1-me puro manancial.\nA coluna luminosa\nSeja sempre o meu fanal.", "Ao Jord\u00e3o, enfim, chegado,\nD\u00e1-me tua m\u00e3o, Senhor,\nE seguro, no outro lado,\nCantarei o teu louvor. Am\u00e9m."]'::json, NULL)
        ,
        ('141'::text, ' Guia divino'::text, '["Guia Cristo a minha nau\nSobre o revoltoso mar\nQue, enfurecido e mau,\nQuer faz\u00ea-la naufragar.\nBom Jesus \u00f3 vem guiar,\nMinha nau vem pilotar!", "Como sabe serenar\nBoa m\u00e3e o filho seu,\nOh acalma assim o mar\nProceloso e mui rebel.\nBom Jesus, oh, vem guiar,\nMinha nau vem pilotar!", "Se no porto, quando entrar,\nMais o mar se enfurecer,\nQue me possa deleitar\nE ouvir-te assim dizer:\n\u201cEntra, pobre viajor,\nNo descanso do Senhor.\u201d Am\u00e9m."]'::json, NULL)
        ,
        ('142'::text, ' O fiel pastor'::text, '["Fiel e bom Pastor \u00e9 meu Senhor Jesus,\nE nada poder\u00e1 faltar por onde me conduz.\nA pasto verde e bom conduz meu caminhar,\nE junto \u00e0s \u00e1guas puras vem fazer-me descansar.", "O bom Pastor fiel dirige em retid\u00e3o,\nCom terno e mui paciente amor, meu fr\u00e1gil cora\u00e7\u00e3o.\nE quando o fim chegar da vida temporal,\nCom ele, firme, eu estarei sem recear o mal.", "Eu tenho prote\u00e7\u00e3o no bra\u00e7o do Senhor,\nE sempre me consolar\u00e1 Jesus, o Salvador.\nBondade e Gra\u00e7a, pois, aqui me seguir\u00e3o,\nE na presen\u00e7a do Senhor terei habita\u00e7\u00e3o."]'::json, NULL)
        ,
        ('143'::text, ' Salmo 23'::text, '["Deus \u00e9 meu Pastor mui terno,\nNada, pois, me faltar\u00e1.\nCom seu santo amor eterno\nSempre me sustentar\u00e1.\n\u00c0s pastagens verdejantes\nEle vem me conduzir,", "E nas relvas abundantes\nVou descanso ent\u00e3o fruir.", "Vem, com suas m\u00e3os divinas,\nMansamente me guiar\nRumo \u00e0s \u00e1guas cristalinas\nQue me v\u00e3o dessedentar.\nPor veredas da justi\u00e7a,\nPrazeroso, me conduz,", "E depois da dura li\u00e7a\nVou gozar a eterna luz.", "Se da morte o vale escuro\nTraz-me aperto ao cora\u00e7\u00e3o,\nCaminhando vou, seguro,\nApoiado em seu bord\u00e3o!\nNa presen\u00e7a de inimigos\nEu terei, da Salva\u00e7\u00e3o,", "Um banquete com amigos,\nCom Jesus meu vero Irm\u00e3o.", "\u00d3leo santo, derramado,\nA cabe\u00e7a vem me ungir.\nVai meu c\u00e1lice transbordando\nPor grandioso bem fruir.\nCertamente que a bondade\nSempre a me seguir terei,", "E no lar da eternidade\nMui feliz habitarei."]'::json, NULL)
        ,
        ('144'::text, ' Seguran�a e alegria'::text, '["Que seguran\u00e7a tenho em Jesus,\nPois nele gozo paz, vida e luz!\nCom Cristo herdeiro, Deus me aceitou\nMediante o Filho que me salvou!", "Conto esta hist\u00f3ria, cantando assim:\nCristo, na cruz, foi morto por mim!\nConto esta hist\u00f3ria, cantado assim:\nCristo, na cruz, foi morto por mim!", "Inteiramente me submeti,\nPlena alegria nele senti.\nAnjos, descendo, trazem dos c\u00e9us\nProvas da gra\u00e7a que vem de Deus.", "Firmado em Cristo, no seu amor,\nEstou contente em meu Salvador!\nEsperan\u00e7oso hei de viver\nPor Jesus Cristo, por seu poder."]'::json, NULL)
        ,
        ('145'::text, ' Ref�gio verdadeiro'::text, '["Seguro estou, n\u00e3o tenho temor do mal!\nSim, guardado pela f\u00e9 em meu Jesus.\nN\u00e3o posso duvidar desse amor leal\nEle em seu caminho sempre me conduz.\nN\u00e3o me deixar\u00e1, mas me abrigar\u00e1,\nDo pecado vil me vem livrar!\nA sua gra\u00e7a n\u00e3o me recusar\u00e1,\nSim, Jesus \u00e9 quem me pode sustentar.", "No poder de Cristo Mestre,\nMinha vida salva est\u00e1!\nDo perigo que cerc\u00e1-la\nEle poder\u00e1 livr\u00e1-la!\nSeu poder eterno sempre a suster\u00e1!", "Abrigo eterno tenho no Salvador,\nEle esconde a minha vida em seu poder;\nN\u00e3o posso ter receio do malfeitor\nQue procura, pertinaz, me enfraquecer.\nConfiado, ent\u00e3o, nessa prote\u00e7\u00e3o,\nSigo a Cristo e quero ser fiel\nNa minha vida cheio de gratid\u00e3o,\nSim, a meu Senhor e Rei Emanuel.", "Perigo algum me pode causar temor,\nPois meu Salvador n\u00e3o me abandonar\u00e1!\nCom sua prote\u00e7\u00e3o e com seu amor,\nDirigindo a minha vida ele estar\u00e1.\nNunca o deixarei, mas fiel serei,\nSempre firme, cheio de fervor!\nEstando bem firmado em Jesus, meu Rei,\nSigo avante, confiando em seu amor."]'::json, NULL)
        ,
        ('146'::text, ' Seguran�a do crente'::text, '["Oh! Qu\u00e3o preciosa e rica promessa\nDe Jesus Cristo, celeste Rei.\nAo que confia na sua gra\u00e7a\nDiz ele: \u201cNunca te deixarei!\u201d", "Oh! N\u00e3o temas! Oh n\u00e3o temas!\nPois eu contigo sempre serei.\nOh n\u00e3o temas! Oh n\u00e3o temas!\nPorque eu nunca te deixarei!", "Para remir-te dei o meu sangue.\nVem sem demora, pois te chamei.\nMeu, para sempre, tu \u00e9s agora;\nNunca, sim nunca te deixarei.", "Inda que indigno foste escolhido,\nJamais vacile, porque te amei.\nQuem dos meus bra\u00e7os pode arrancar-te?\nSeguro sempre te guardarei!"]'::json, NULL)
        ,
        ('147'::text, ' Vencendo vem Jesus'::text, '["Quando a alma sequiosa\nChega \u00e0 voz do Salvador,\nEis que logo reconhece\nSer Jesus o seu Senhor!\nMas se o \u201ceu\u201d quer levantar-se\nE mostrar algum valor,\nVencendo vem Jesus!", "Gl\u00f3ria, gl\u00f3ria, aleluia!\nGl\u00f3ria, gl\u00f3ria, aleluia!\nGl\u00f3ria, gl\u00f3ria, aleluia!\nVencendo vem Jesus!", "Neste mundo havemos, crentes,\nDe ter sempre algum pesar!\nMesmo lutas, dissabores,\nNossa paz vem perturbar.\nMas se o mal nos amea\u00e7a\nDe alegria nos roubar,\nVencendo vem Jesus", "Da vaidade, fi\u00e9is servos\nLutam por fazer-nos seus!\nMuitas vezes nos assaltam\nOs modernos fariseus.\nMas se algu\u00e9m procura ver-nos\nSem a gra\u00e7a do bom Deus,\nVencendo vem Jesus!"]'::json, NULL)
        ,
        ('148'::text, ' Ora��o noturna'::text, '["Finda-se este dia que meu Pai me deu,\nSombras vespertinas cobrem j\u00e1 o c\u00e9u.\n\u00d3 Jesus bendito, se comigo est\u00e1s,\nEu n\u00e3o temo a noite, vou dormir em paz.", "Com pecados hoje, eu te entristeci,\nMas perd\u00e3o te pe\u00e7o por amor de ti.\nSou pequeno e fr\u00e1gil, livra-me do mal!\nQue em ti eu tenha prote\u00e7\u00e3o final.", "Guarda o marinheiro no violento mar,\nE ao que sofre dores queiras confortar.\nAo tentado estende tua m\u00e3o, Senhor!\nManda ao triste, aflito, o Consolador.", "Pelos pais e amigos, pela santa Lei,\nPelo amor divino, gra\u00e7as te darei!\n\u00d3 Jesus, aceita minha peti\u00e7\u00e3o,\nE, seguro durmo sem perturba\u00e7\u00e3o Am\u00e9m."]'::json, NULL)
        ,
        ('149'::text, ' Verdadeira reden��o'::text, '["Eu confio em meu Jesus\nE seguro estou!\nPois morrendo sobre a cruz,\nEle me salvou.", "Cristo me salvou\nE me transformou!\nPor sua morte sobre a cruz\nNos deu vida eterna e luz.", "Veio, assim, o bom Senhor\nSalva\u00e7\u00e3o trazer\nA t\u00e3o pobre pecador,\nPelo seu poder.", "Verdadeira reden\u00e7\u00e3o\nTenho em meu Jesus!\nPlena paz, consola\u00e7\u00e3o,\nV\u00eam da sua cruz.", "L\u00e1 nos c\u00e9us eu viverei\nCom o Salvador;\nPlenamente fruirei\nDo seu grande amor."]'::json, NULL)
        ,
        ('15'::text, ' Doxologia'::text, '["Tua, \u00f3 Deus, \u00e9 toda a grandeza\nE o poder e a gl\u00f3ria e a vit\u00f3ria\nE os louvores; vit\u00f3ria e louvores!\nTua, \u00f3 Deus, tua, \u00f3 Deus, \u00e9 a grandeza\nE o poder, a grandeza e o poder\nE a gl\u00f3ria e a vit\u00f3ria e os louvores eternos.\nPois tudo o que est\u00e1 no mar,\nE est\u00e1 no c\u00e9u e na terra \u00e9 teu!\nTeu \u00e9 o dom\u00ednio, teu \u00e9 o dom\u00ednio,\n\u00d3 Senhor! E Tu \u00e9s acima de todos os reis,\nDe todos os reis. Am\u00e9m. Am\u00e9m. Aleluia!"]'::json, NULL)
        ,
        ('150'::text, ' Salva��o perfeita'::text, '["Livres do medo! J\u00e1 resgatados!\nCristo morreu por nossos pecados!\nNa sua cruz o pacto se fez,\nFomos remidos de uma vez.", "Sim, de uma vez! Amigo acredita,\nNo Salvador tens sorte bendita!\nCristo, na cruz, a lei satisfez\nE redimiu-nos de uma vez!", "Ao malfeitor, que a pena merece,\nVida, perd\u00e3o e paz oferece.\nVem a Jesus com santa avidez,\nPois te recebe de uma vez!", "Gra\u00e7a real! N\u00e3o h\u00e1 mais castigo!\nN\u00e3o mais temor, nem sombra ou perigo!\nVestes reais, n\u00e3o triste nudez,\nCristo enriquece de uma vez!", "Filhos de Deus - oh, gozo inaudito!\nDeus nos amou em grau infinito.\nNesta clem\u00eancia n\u00e3o h\u00e1 dobrez,\nH\u00e1 seguran\u00e7a de uma vez!"]'::json, NULL)
        ,
        ('151'::text, ' O bom Pastor'::text, '["Pelos bosques e campinas\nPastoreia o bom Jesus.\nJunto \u00e0s \u00e1guas cristalinas\nSeu rebanho em paz conduz.\nEle guarda assim minha alma\nE a protege em seu calor.\nNas ang\u00fastias ou na calma\nVai comigo o Bom Pastor", "Sempre am\u00e1vel e clemente,\nVai comigo o Bom Pastor.", "N\u00e3o me afligem os perigos\nE n\u00e3o temo a tenta\u00e7\u00e3o!\nEle sempre est\u00e1 comigo\nE sust\u00e9m-me o seu bord\u00e3o.\nN\u00e3o me aflige nem a morte,\nCuja sombra hei de enfrentar.\nEle \u00e9 meu amparo forte\nEm que posso confiar.", "Sua mesa me prepara,\nPois minha alma quer nutrir.\nMinhas m\u00e1goas ele sara,\nCom seu \u00f3leo vem me ungir.\nCertamente que a bondade\nToda a vida fruirei.\nE no c\u00e9u felicidade\nPara sempre gozarei!"]'::json, NULL)
        ,
        ('152'::text, ' Onisci�ncia divina'::text, '["Tu me sondaste a mim, \u00f3 Deus,\nN\u00e3o h\u00e1 segredo aos olhos teus!\nPrev\u00eas por onde quero andar,\nE sabes como vou falar.", "Contemplas tu meu interior!\nQuem compreender\u00e1, Senhor\nTua ci\u00eancia e o teu poder?\nPois infinito \u00e9 teu saber.", "\u00d3 Deus da minha salva\u00e7\u00e3o,\nVem dominar meu cora\u00e7\u00e3o;\nVem controlar o meu sentir,\nE minha vida conduzir. Am\u00e9m."]'::json, NULL)
        ,
        ('153'::text, ' Amparo divino'::text, '["Com tua m\u00e3o segura bem a minha,\nPois eu t\u00e3o fr\u00e1gil sou, \u00f3 Salvador,\nQue n\u00e3o me atrevo a dar jamais um passo\nSem teu amparo, Cristo, meu Senhor!", "Com tua m\u00e3o segura bem a minha,\nE meu caminho, alegre, seguirei!\nMesmo onde as sombras caem mais escuras,\nTeu rosto vendo, nada temerei.", "E no momento de transpor o rio\nQue Tu, por mim, vieste atravessar,\nCom tua m\u00e3o segura bem a minha,\nE sobre a morte eu hei de triunfar.", "Quando voltares l\u00e1 dos c\u00e9us descendo,\nSegura bem a minha m\u00e3o, Senhor!\nE, meu Jesus, conduze-me contigo\nPara onde eu goze teu eterno amor. Am\u00e9m."]'::json, NULL)
        ,
        ('154'::text, ' Seguran�a e paz'::text, '["Pelo vale escuro seguirei, Jesus,\nMas por ti seguro, vendo a tua luz,\nO meu passo incerto Tu dirigir\u00e1s!\nAo sentir-te perto nunca perco a paz!", "Os espinhos tantos, que nos vem sangrar,\nS\u00e3o rem\u00e9dios santos para nos curar.\nOnde existe a gra\u00e7a que nos vem dos c\u00e9us,\nTudo o que se passa mostra o amor de Deus.", "N\u00e3o h\u00e1 dor que seja sem divino fim;\nFaze, \u00f3 Deus, que a Igreja compreenda assim!\nE apesar das trevas possa ver, Senhor,\nQue Tu mesmo as levas com imenso amor.", "Breve a noite desce, noite de Ema\u00fas!\nE meu ser carece de te ver, Jesus!\nCompanheiro amigo, que ao meu lado vens,\nSim est\u00e1s comigo, sempre me sust\u00e9m. Am\u00e9m."]'::json, NULL)
        ,
        ('155'::text, ' Castelo forte'::text, '["Castelo forte \u00e9 nosso Deus,\nEspada e bom escudo!\nCom seu poder defende os seus\nEm todo transe agudo.\nCom f\u00faria pertinaz\nPersegue Satan\u00e1s\nCom \u00e2nimo cruel!\nMui forte \u00e9 o Deus fiel,\nIgual n\u00e3o h\u00e1 na terra.", "A for\u00e7a do homem nada faz,\nSozinho est\u00e1 perdido!\nMas nosso Deus socorro traz\nEm seu Filho escolhido.\nSabeis quem \u00e9? Jesus,\nO que venceu na cruz,\nSenhor dos altos c\u00e9us,\nE sendo o pr\u00f3prio Deus,\nTriunfa na batalha.", "Se nos quisessem devorar\nDem\u00f4nios n\u00e3o contados,\nN\u00e3o nos iriam derrotar\nNem ver-nos assustados.\nO pr\u00edncipe do mal,\nCom seu plano infernal,\nJ\u00e1 condenado est\u00e1!\nVencido cair\u00e1\nPor uma s\u00f3 palavra.", "De Deus o verbo ficar\u00e1\nSabemos com certeza,\nE nada nos assustar\u00e1\nCom Cristo por defesa!\nSe temos de perder\nFam\u00edlia., bens, prazer,\nSe tudo se acabar\nE a morte enfim chegar,\nCom ele reinaremos!"]'::json, NULL)
        ,
        ('156'::text, ' Confian�a em Deus'::text, '["Descansa, \u00f3 alma, eis o Senhor ao lado;\nPaciente leva, e sem queixar-te, a cruz;\nDeixa o Senhor tomar de ti cuidado,\n\u00c9 imut\u00e1vel teu fiel Jesus!\nProssegue, \u00f3 alma, o Amigo celestial\nProteger\u00e1 teus passos no espinhal!", "Prossegue, \u00f3 alma; o trilho \u00e9 estreito e escuro,\nMas no passado Deus guiou-te assim!\nConfia agora a Deus o teu futuro,\nE o que \u00e9 mist\u00e9rio h\u00e1 de aclarar-se enfim.\nConfia, \u00f3 alma, a sua mansa voz\nAinda acalma o vento e o mar feroz!", "Confia, \u00f3 alma! A hora vem chegando!\nIr\u00e1s com Cristo, o teu Senhor, morar.\nSem dor, nem m\u00e1goas gozar\u00e1s cantando\nAs alegrias do celeste lar!\nDescansa, \u00f3 alma; agora h\u00e1 pranto e dor\nDepois o gozo, a paz, o c\u00e9u de amor. Am\u00e9m."]'::json, NULL)
        ,
        ('157'::text, ' Seguran�a em Cristo'::text, '["Por meus delitos expirou\nJesus, a vida e luz!\nDo meu pecado me livrou\nNa ensang\u00fcentada cruz.", "Faze-me forte em confessar\nA ti, Jesus, Senhor!\nFaze-me pronto a confiar\nNo teu eterno amor.", "Jamais terei t\u00e3o fraca voz\nQue trema ao confessar\nA quem, por morte t\u00e3o atroz,\nMinha alma quis salvar.", "Sim, eu desejo aqui cantar\nT\u00e3o grande Salvador,\nE quando l\u00e1 no c\u00e9u chegar\nLouv\u00e1-lo-ei melhor. Am\u00e9m."]'::json, NULL)
        ,
        ('158'::text, ' Conforto e luz'::text, '["Vai, alma tristonha, teu pranto depor!\nP\u00f5e os teus cuidados nas m\u00e3os do Senhor!\nAo Mestre confia toda essa afli\u00e7\u00e3o,\nPois sempre oferece plena compaix\u00e3o.", "Teus sustos e medos vai, mostra ao Senhor!\nSeu mando transforma a noite em fulgor.\nErgue a tua fronte! Logo h\u00e1 de raiar.\nO sol que dissipa nuvens de pesar.", "H\u00e1 tantos que choram dor muito maior;\nH\u00e1 cora\u00e7\u00f5es tristes por culpa e temor.\nVai! Leva a mensagem de perd\u00e3o e luz!\nVai! Deixa as tristezas aos p\u00e9s de Jesus!"]'::json, NULL)
        ,
        ('159'::text, ' Bondoso amigo'::text, '["Qu\u00e3o bondoso Amigo \u00e9 Cristo,\nRevelou-nos seu amor;\nE nos diz que lhe entreguemos\nOs cuidados, sem temor.\nFalta ao cora\u00e7\u00e3o dorido\nGozo, paz, consola\u00e7\u00e3o?\n\u00c9 porque n\u00f3s n\u00e3o levamos\nTudo a ele, em ora\u00e7\u00e3o.", "Andas triste e carregado\nDe pesares e de dor?\nA Jesus, eterno abrigo,\nVai, com f\u00e9, teu mal expor.\nTeus amigos te desprezam?\nConta-lhe isso em ora\u00e7\u00e3o,\nE, por seu amor t\u00e3o terno,\nPaz ter\u00e1s no cora\u00e7\u00e3o.", "Cristo \u00e9 verdadeiro Amigo!\nDisso prova nos mostrou,\nQuando, para resgatar-nos,\nEle, humilde, se encarnou.\nDerramou precioso sangue\nPara nos purificar!\nGozo, em vida e no futuro,\nJ\u00e1 podemos alcan\u00e7ar!"]'::json, NULL)
        ,
        ('16'::text, ' Louvor a Deus'::text, '["Louvai a Deus,\nSoberano Senhor do que \u00e9 feito.\nLouvai-o sim,\nDe vossa alma, tesouro perfeito!\nA Deus cantai\nE, com fervor, tributai\nProfundo amor e respeito.", "Louvai a Deus\nQue vos faz prosperar dia a dia;\nE, com amor,\nVos defende e aben\u00e7oa a porfia.\nLembrai tamb\u00e9m,\nQue o poderoso vos vem\nFazer feliz companhia."]'::json, NULL)
        ,
        ('160'::text, ' Cristo � tudo'::text, '["Nome algum, no c\u00e9u, na terra,\nSe compara ao de Jesus!\nEle sobre tudo reina,\nEle \u00e9 minha eterna luz.", "Minhas dores ele sofre,\nSara o enfermo cora\u00e7\u00e3o.\nSeu amor me d\u00e1 al\u00edvio\nNa tristeza e na afli\u00e7\u00e3o.", "\u00c9 Jesus o meu tesouro,\nNele encontro todo o bem.\nValem mais que o mundo inteiro\nAs riquezas que ele tem.", "\u00c9 Jesus meu alimento,\nO meu P\u00e3o celestial.\nDo mais vero e santo gozo\nEle \u00e9 meu manancial.", "Infinita \u00e9 sua gra\u00e7a,\nImposs\u00edvel de sondar!\nCom os santos anjos quero\nA Jesus louvores dar."]'::json, NULL)
        ,
        ('161'::text, ' O melhor amigo'::text, '["Sei que o melhor Amigo \u00e9 Cristo!\nQuando a tempestade assalta a f\u00e9,\nPronto estende a sua m\u00e3o,\nTranq\u00fciliza o cora\u00e7\u00e3o!\nSim, o melhor Amigo \u00e9 Cristo!", "Jesus \u00e9 o melhor Amigo!\nJesus \u00e9 o melhor Amigo!\nRepreende com dul\u00e7or\nE me anima com vigor!\nSim, o melhor Amigo \u00e9 Cristo!", "Oh, que fiel Amigo \u00e9 Cristo!\nNele encontro amor, consolo e paz.\nEm seu bra\u00e7o esperarei!\nMal nenhum eu temerei!\nSim, o melhor Amigo \u00e9 Cristo!", "Quando eu passar o vale escuro\nE chegar \u00e0s \u00e1guas do Jord\u00e3o,\nSem receio, com Jesus,\nEntrarei no c\u00e9u de luz.\nSim, o melhor Amigo \u00e9 Cristo!", "No para\u00edso eterno, juntos,\nN\u00f3s, os salvos, reunidos l\u00e1,\nEste canto de louvor\nEntoaremos ao Senhor:", "\u201cSim, o melhor Amigo \u00e9 Cristo!\u201d\nJesus \u00e9 o melhor Amigo!\nJesus \u00e9 o melhor Amigo!\nDo pecado me salvou,\nPara o c\u00e9u me preparou.\nSim, o melhor Amigo \u00e9 Cristo!"]'::json, NULL)
        ,
        ('162'::text, ' A voz de Deus'::text, '["Quando a tempestade ruge\nCom o seu feroz bramir,\nQuando as nuvens se acumulam,\nRaios mil a despedir,\nDo trov\u00e3o o som tremendo\nFaz-se ouvir e com pavor!\nMas por sobre a tempestade\nSoa tua voz, Senhor.", "Eis que ouvimos tua voz,\nA animar os que andam s\u00f3s\nE em ti sempre confiados,\nV\u00e3o por ti sempre a lutar,\nNesta vida tormentosa,\nQual fragor do vasto mar.", "Quando o mar vem,mansamente,\nSobre a areia se espraiar,\nQuando a brisa sussurrante\nCalmamente perpassar,\nSoa a m\u00edstica harmonia,\nOuve-se um feliz rumor;\nDesse coro, docemente,\nVem a tua voz, Senhor!", "Quando o cora\u00e7\u00e3o aflito\nQuer \u00e0 dor, ao mal fugir,\nE se agita luta e ruge,\nSem descanso e paz sentir,\nQual um som que se repete\nNas quebradas a rolar,\nAo aflito e contristado\nTua voz vem consolar."]'::json, NULL)
        ,
        ('163'::text, ' Dire��o divina'::text, '["As tuas m\u00e3os dirigem meu destino!\n\u00d3 Deus de amor que seja sempre assim!\nTeus s\u00e3o os meus poderes, minha vida;\nEm tudo, eterno Pai, disp\u00f5e de mim.\nMeus dias sejam curtos ou compridos,\nPassados em tristezas ou prazer,\nEm sombra ou luz, de acordo com teu plano,\n\u00c9 tudo bom se vem do teu querer.", "As tuas m\u00e3os dirigem meu destino,\nPor mim cravadas na sangrenta cruz!\nPor meus pecados foram traspassadas,\nBem posso nelas descansar, Jesus!\nNos c\u00e9us erguidas, sempre intercedendo,\nAs santas m\u00e3os n\u00e3o pedir\u00e3o em v\u00e3o;\nAo seu cuidado, em plena confian\u00e7a,\nEntrego a minha eterna salva\u00e7\u00e3o.", "As tuas m\u00e3os dirigem meu destino,\nAcasos para mim n\u00e3o haver\u00e1!\nO grande Pai vigia o meu caminho,\nE sem motivo n\u00e3o me afligir\u00e1!\nEncontro em seu poder constante apoio,\nForte \u00e9 seu bra\u00e7o, insone o seu amor;\nPor fim, entrando na cidade eterna,\nEu louvarei meu guia e Salvador. Am\u00e9m."]'::json, NULL)
        ,
        ('164'::text, ' Nome precioso'::text, '["Santo nome, incompar\u00e1vel,\nTem Jesus, o amado teu!\nRei dos reis, Senhor eterno,\nDeus na terra, Deus nos c\u00e9us.", "Nome bom, doce \u00e0 f\u00e9,\nA esperan\u00e7a do porvir.\nNome bom, doce \u00e0 f\u00e9\nA esperan\u00e7a do porvir.", "Leva tu contigo o Nome\nDe Jesus, o Salvador!\nEste Nome d\u00e1 conforto\nHoje, sempre e onde for.", "Este Nome leva sempre\nPara bem te defender!\nEle \u00e9 arma ao teu alcance,\nQuando o mal te aparecer."]'::json, NULL)
        ,
        ('165'::text, ' Cuidado divino'::text, '["N\u00e3o desanimes! Deus prover\u00e1!\nDeus velar\u00e1 por ti!\nCom suas asas te cobrir\u00e1,\nDeus velar\u00e1 por ti.", "Deus cuidar\u00e1 de ti,\nNa tua dor,\nCom todo amor,\nJamais te deixar\u00e1!\nDeus cuidar\u00e1 de ti!", "Se no teu peito vibrar a dor,\nDeus velar\u00e1 por ti.\nTu j\u00e1 provaste seu grande amor,\nDeus velar\u00e1 por ti.", "Nos desalentos, nas prova\u00e7\u00f5es;\nDeus velar\u00e1 por ti.\nNas desventuras, nas tenta\u00e7\u00f5es,\nDeus velar\u00e1 por ti.", "Como estiveres, n\u00e3o temas, vem!\nDeus velar\u00e1 por ti.\nTeu Pai bondoso te espera al\u00e9m,\nDeus velar\u00e1 por ti."]'::json, NULL)
        ,
        ('166'::text, ' Luz e vida'::text, '["\u00c9s da minha alma a luz Senhor!\nVem dissipar o meu temor!\nNoite n\u00e3o h\u00e1 se perto est\u00e1s,\nEm ti alcan\u00e7o plena paz.", "Sempre comigo est\u00e1s, Senhor!\nS\u00f3 tenho vida em teu amor.\nDo dia esvai-se toda a luz,\nSigo bem junto a ti Jesus.", "Com os enfermos vem estar,\nOs pobres vem alimentar!\nAos que no leito sofrem dor\nD\u00e1 teu conforto e paz, Senhor.", "E desde cedo, ao despertar,\nO nosso passo vem guiar!\nNa imensid\u00e3o do teu amor\nVem nos guardar, \u00f3 Salvador. Am\u00e9m."]'::json, NULL)
        ,
        ('167'::text, ' Cristo, esperan�a nossa'::text, '["\u201cCristo esperan\u00e7a nossa!\u201d\nVinde, \u00f3 salvos proclamar!\nEle \u00e9 eterno Deus bendito,\n\u00c9 ref\u00fagio singular.\n\u00c9 quem guarda, quem protege,\nQuem aos homens quer salvar,\nQuem aos homens quer salvar,", "\u201cCristo, esperan\u00e7a nossa!\u201d\nPois da culpa nos livrou;\nCom seu sangue precioso\nL\u00e1 na cruz j\u00e1 nos comprou.\nDeu nos vida em sua morte,\nPois que j\u00e1 ressuscitou,\nPois que j\u00e1 ressuscitou.", "\u201cCristo esperan\u00e7a nossa!\u201d\nEis o lema vencedor.\nAo seu lado, contra as trevas,\nCombatamos sem temor.\nNossa grande esperan\u00e7a\n\u00c9 o Cristo Salvador,\n\u00c9 o Cristo salvador!"]'::json, NULL)
        ,
        ('168'::text, ' Jesus amado'::text, '["Qu\u00e3o doce fala ao cora\u00e7\u00e3o\nDo pobre pecador\nO nome que lhe traz perd\u00e3o:\nJesus, o Redentor!", "Bendito sejas, \u00f3 Jesus!\nEm ti confiarei.\nPor mim morreste sobre a cruz,\nJamais te deixarei!", "Jesus, eu quero em ti pensar,\nPois teu amor me apraz.\nE ajoelhando-me a orar,\nEu sinto a tua paz!", "Jesus, meu Rei, meu Salvador,\nTeu Nome quero ter\nN\u00e3o s\u00f3 nos l\u00e1bios, com fervor,\nMas sempre no viver! Am\u00e9m."]'::json, NULL)
        ,
        ('169'::text, ' Grata confian�a'::text, '["No poder do Deus bendito,\nVai tudo bem!\nSe amor \u00e9 infinito,\nVai tudo bem!\nEsse amor nos foi provado\nEm seu Filho muito amado\nQue por n\u00f3s foi imolado!\nVai tudo bem.", "Reina Cristo em gl\u00f3ria eterna!\nVai tudo bem!\nRessurgiu, e j\u00e1 governa!\nVai tudo bem!\nSeu amor \u00e9 imut\u00e1vel,\nSeu poder inabal\u00e1vel,\nSeu cuidado \u00e9 incans\u00e1vel!\nVai tudo bem!", "Na doen\u00e7a ou na tristeza,\nVai tudo bem!\nProclamamos com certeza:\nVai tudo bem!\nPois se Deus \u00e9 quem nos guia,\nTernamente nos vigia,\nCom bondade, noite e dia,\nVai tudo bem!", "Em caminhos escabrosos,\nVai tudo bem!\nOu por mares tormentosos,\nVai tudo bem!\nA Jesus tudo obedece,\nEle o mesmo permanece\nNem um s\u00f3 dos seus esquece,\nVai tudo bem!", "Quer na vida, quer na morte,\nVai tudo bem!\nMui feliz \u00e9 nossa sorte,\nVai tudo bem!\nPelo sangue resgatados,\nE do mundo separados,\nSempre por Jesus guardados,", "Vai tudo bem!"]'::json, NULL)
        ,
        ('17'::text, ' Deus seja louvado'::text, '["Seja louvado o Deus supremo,\nDeus revelado em Israel!\nOnipotente, prod\u00edgios obra,\nSempre clemente, sempre fiel.", "Seja louvado o Deus supremo,\nDeus revelado em Israel", "Louvor perene s\u00f3 Deus merece!\nCantai, n\u00e3o cesse o seu louvor!\nDe todos seja sempre exaltado,\nE cante a Igreja com terno amor."]'::json, NULL)
        ,
        ('170'::text, ' Redentor onipotente'::text, '["Redentor Onipotente\nPoderoso Salvador,\nAdvogado Onisciente\n\u00c9 Jesus, meu bom Senhor.\nUm abrigo sempre perto\nPara todo pecador!\nUm amigo insepar\u00e1vel\n\u00c9 Jesus, meu Salvador!", "\u00c1 \u00e1gua viva, P\u00e3o da vida,\nDoce sombra no calor\nQue ao descanso nos convida\n\u00c9 Jesus, meu Salvador!\nSol que extingue densas trevas,\nRefulgindo em plena luz,\nNegra noite desfazendo,\n\u00c9 meu bom Senhor, Jesus.", "Fundamento inabal\u00e1vel!\nEm que posso confiar!\nInfal\u00edvel, imut\u00e1vel,\nRocha firme e secular!\nPorta aberta, sempre aberta,\nConduzindo \u00e0 salva\u00e7\u00e3o,\nRica fonte donde emana\nGozo, paz, consola\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('171'::text, ' Deus prover�'::text, '["Na forte afli\u00e7\u00e3o, perigo e dor,\nNa vil trai\u00e7\u00e3o, no horrendo terror,\nCom toda a certeza vit\u00f3ria vir\u00e1!\n\u00c9 firme a esperan\u00e7a: \u201cMeu Deus prover\u00e1.\u201d", "Em tudo o Senhor nos pode valer,\nDo mal, com amor, nos quer defender!\nCom Cristo bem perto, em n\u00f3s se achar\u00e1\nA viva esperan\u00e7a: \u201cMeu Deus prover\u00e1.\u201d", "Se vem Satan\u00e1s nos amedrontar\nE, astuto quiser a f\u00e9 nos roubar\nN\u00e3o pode; \u00e9 nossa e sempre ser\u00e1\nA rica esperan\u00e7a: \u201cMeu Deus prover\u00e1.\u201d", "No rude labor, no esfor\u00e7o tenaz,\nSomente o Senhor vit\u00f3ria nos traz!\nEm sua bondade nos socorrer\u00e1,\nConforme a esperan\u00e7a: \u201cMeu Deus prover\u00e1.\u201d", "Na hora final, a morte a chegar,\nA voz divinal nos h\u00e1 de alegrar!\nMinha alma, segura, ent\u00e3o cantar\u00e1\nA doce esperan\u00e7a: \u201cMeu Deus prover\u00e1.\u201d"]'::json, NULL)
        ,
        ('172'::text, ' Chuvas de ben��os'::text, '["Chuvas de b\u00ean\u00e7\u00e3os teremos\nPelas promessas de Deus;\nTempos benditos trazendo\nChuvas de b\u00ean\u00e7\u00e3os dos c\u00e9us.", "Chuvas de b\u00ean\u00e7\u00e3os,\nChuvas de b\u00ean\u00e7\u00e3os dos c\u00e9us !\nTantas n\u00f3s j\u00e1 recebemos,\nOutras rogamos a Deus!", "Chuvas de b\u00ean\u00e7\u00e3os teremos,\nQue do Senhor nos vir\u00e3o.\nOs pecadores indignos\nGra\u00e7as dos c\u00e9us obter\u00e3o.", "Chuvas de b\u00ean\u00e7\u00e3os teremos,\nManda-nos,pois, o Senhor.\nD\u00e1-nos o gozo dos frutos\nDos teus ensinos de amor!", "Chuvas de b\u00ean\u00e7\u00e3os teremos,\nChuvas mandadas dos c\u00e9us,\nB\u00ean\u00e7\u00e3os a todos os crentes,\nB\u00ean\u00e7\u00e3os do nosso bom Deus."]'::json, NULL)
        ,
        ('173'::text, ' Ora��o vespertina'::text, '["Vai fugindo o dia, breve a noite vem;\nVespertina estrela j\u00e1 se avista al\u00e9m.", "Ao que mui cansado na tristeza jaz,\nD\u00e1, Jesus bendito, teu descanso e paz!", "Noite de sossego vimos te pedir;\nEm tuas m\u00e3os entregues, hemos de dormir.", "E quando acordarmos, faze, \u00f3 bom Senhor,\nQue n\u00f3s te sirvamos com maior vigor. Am\u00e9m."]'::json, NULL)
        ,
        ('174'::text, ' Poderoso salvador'::text, '["Confio em ti, meu Senhor,\nContigo seguro eu estou\nOs \u00edmpios vieste buscar!\nTu \u00e9s poderoso, Tu \u00e9s poderoso\nE podes salvar!", "Oh! Livra minha alma, Senhor,\nDas malhas do vil tentador!\nSeus la\u00e7os vem despeda\u00e7ar!\nTu \u00e9s poderoso, Tu \u00e9s poderoso\nE podes livrar!", "No mundo sofreste, \u00f3 Jesus,\nPor mim padeceste na cruz!\nTeu sangue me pode livrar!\nTu \u00e9s poderoso, Tu \u00e9s poderoso,\nE podes guardar! Am\u00e9m."]'::json, NULL)
        ,
        ('175'::text, ' O servo do Senhor'::text, '["Qu\u00e3o bem aventurado o servo do Senhor,\nQue n\u00e3o faz alian\u00e7a com o desprezador!", "Jamais o mau caminho dos \u00edmpios quer seguir,\nNem por seu vil conselho se deixa seduzir.", "Mas sumo regozijo em Deus alcan\u00e7ar\u00e1,\nE em sua lei divina, feliz, meditar\u00e1.", "Bem como a linda planta ele h\u00e1 de florescer!\nE junto \u00e0s \u00e1guas vivas, vi\u00e7oso, ir\u00e1 crescer.", "Em toda a sua vida os frutos brotar\u00e3o,\nE em tudo o que ele intente vit\u00f3rias surgir\u00e3o.", "Mas, doutra sorte, os \u00edmpios n\u00e3o podem prosperar,\nE suas esperan\u00e7as na morte ir\u00e3o findar.", "Mui breve n\u00f3s veremos Jesus do c\u00e9u descer,\nE sua Igreja inteira na gl\u00f3ria receber.", "Ent\u00e3o, os que desprezam o grande Salvador\nPerecer\u00e3o malditos diante do Senhor."]'::json, NULL)
        ,
        ('176'::text, ' Fidelidade do crist�o'::text, '["Sempre fi\u00e9is, sim a ti n\u00f3s seremos,\nPor tua gra\u00e7a, bendito Senhor!\nSempre fi\u00e9is, n\u00f3s por ti lutaremos\nCom teu poder., \u00f3 Jesus, Salvador.!", "Sempre fi\u00e9is, irm\u00e3os aqui sejamos!\nSempre fi\u00e9is a Cristo Jesus!\nQue at\u00e9 \u00e0 morte por n\u00f3s prosseguindo,\nVida nos deu, salva\u00e7\u00e3o pela cruz!", "Por ti viver, \u00f3 bendito Cordeiro,\nQuem n\u00e3o deseja se te conhecer?\nQuem, se de fato \u00e9 crist\u00e3o verdadeiro,\nN\u00e3o estar\u00e1, por ti, pronto a sofrer?", "N\u00f3s te louvamos, pois Tu, que venceste,\nSempre em triunfo nos podes levar.\nA teus fi\u00e9is, \u00f3 Jesus, prometeste\nTodos os dias com ele estar. Am\u00e9m."]'::json, NULL)
        ,
        ('177'::text, ' Firme nas promessas'::text, '["Firme nas promessas do meu Salvador,\nCantarei louvores ao meu Criador!\nFico, pelos s\u00e9culos do seu amor,\nFirme nas promessas de Jesus!", "Firme, Firme,\nFirme nas promessas\nDe Jesus me Mestre!\nFirme, firme,\nSim, firme nas promessas de Jesus!", "Firme nas promessas, hei de trabalhar,\nTendo o Verbo eterno sempre a me amparar!\nMesmo em tempestade vou sem vacilar,\nFirme nas promessas de Jesus!", "Firme nas promessas, sempre vejo, assim,\nPurifica\u00e7\u00e3o no sangue para mim!\nPlena liberdade j\u00e1 desfruto, enfim;\nFirme nas promessas de Jesus!", "Firme nas promessas do Senhor Jesus,\nEm amor ligado sempre \u00e0 sua cruz!\nCada dia mais me alegro em sua luz,\nFirme nas promessas de Jesus!"]'::json, NULL)
        ,
        ('178'::text, ' A excel�ncia do amor'::text, '["Qual o adorno desta vida?\n\u00c9 o amor, \u00e9 o amor.\nAlegria \u00e9 concedida\nPelo amor, pelo amor\n\u00c9 benigno, \u00e9 paciente,\nN\u00e3o se torna maldizente\nN\u00e3o se torna maldizente\nNem se ufana este amor.", "Com suspeitas n\u00e3o se alcan\u00e7a\nVero amor, vero amor!\nOnde houver desconfian\u00e7a,\nAi do amor, ai do amor!\nPois mostremos lealdade,\nCombatendo a falsidade\nCombatendo a falsidade\nQue destr\u00f3i este amor.", "N\u00e3o te irrites, mas tolera\nCom amor, com amor!\nTudo sofre, tudo espera\nPelo amor, pelo amor.\nO caminho excelente\nAo segui-lo vence o crente,\nAo segui-lo vence o crente\nNuma vida de amor!", "\u00d3 crist\u00e3o, ao teu vizinho\nMostra amor, mostra amor!\nO valor n\u00e3o \u00e9 mesquinho\nDeste amor, deste amor.\nO supremo Deus nos ama;\nCristo para o c\u00e9u nos chama,\nCristo para o c\u00e9u nos chama,\nOnde reina este amor."]'::json, NULL)
        ,
        ('179'::text, ' Sauda��o'::text, '["Saudamo-vos, irm\u00e3os em Cristo,\nLembrando o que temos visto:\nNesses anos, pelas lutas, tenta\u00e7\u00f5es.\nAtendidas foram nossas peti\u00e7\u00f5es.\nToda a gl\u00f3ria seja ao nome do Senhor!\nVinde todos entoar o seu louvor!", "Bem alto agora vamos n\u00f3s cantar,\nQue a terra e os c\u00e9us vir\u00e3o nos ajudar.\nAt\u00e9 aqui Deus mesmo nos guiou,\nE com a sua m\u00e3o nos ajudou.", "Um dia t\u00e3o glorioso temos,\nE ao nosso Pai agradecemos,\nPois \u00e9 ele quem nos d\u00e1 real prazer;\nE \u00e9 fiel em nos guardar e proteger!\nVinde v\u00f3s, irm\u00e3os, conosco a Deus cantar,\nDesta b\u00ean\u00e7\u00e3o vinde, pois, participar!", "Alegres todos jubilemos,\nAo grande Salvador cantemos!\nEle como filhos seus nos escolheu\nRicas b\u00ean\u00e7\u00e3os ele j\u00e1 nos concedeu.\nSeja \u201cavante\u201d o nosso lema triunfal,\nPois seguimos para o lar celestial."]'::json, NULL)
        ,
        ('18'::text, ' Deus dos antigos'::text, '["Deus dos antigos, cuja forte m\u00e3o\nRege e sust\u00e9m os astros na amplid\u00e3o!\n\u00d3 Soberano, excelso Criador,\nCom gratid\u00e3o cantamos teu louvor!", "Desde o passado foste nossa luz,\nSol que at\u00e9 hoje com fulgor reluz!\nS\u00ea nosso Esteio, Guia e Prote\u00e7\u00e3o,\nTua Palavra, lei e dire\u00e7\u00e3o.", "Da guerra atroz, do crime e assola\u00e7\u00e3o,\nDos tempos maus de um mundo em confus\u00e3o,\nSeja o teu bra\u00e7o o nosso defensor,\nPois confiamos sempre em ti Senhor!", "Teu povo, \u00f3 Deus, assiste me seu labor,\nNo testemunho do teu grande amor,\nAs nossas vidas vem fortalecer\nPara o teu nome sempre engrandecer. Am\u00e9m"]'::json, NULL)
        ,
        ('180'::text, ' Amor fraternal'::text, '["Jesus, Pastor amado! Louvamos-te hoje, aqui,\nUnidos pela gra\u00e7a, um corpo s\u00f3 em ti.\nContendas e mal\u00edcias, que longe de n\u00f3s v\u00e3o,\nNenhum desgosto impe\u00e7a a nossa santa uni\u00e3o.", "Fam\u00edlia unida somos, fam\u00edlia de Jesus!\nIluminados todos da mesma santa luz.\nA mesma f\u00e9 nos une num s\u00f3 divino amor,\nE cheios de alegria servimos ao Senhor.", "Num s\u00f3 caminho estreito Deus mesmo nos conduz,\nS\u00f3 temos esperan\u00e7a no Salvador Jesus!\nSua morte preciosa a todos vida traz;\nE pelo mesmo sangue nos vem a mesma paz.", "Pois sendo resgatados por um s\u00f3 Salvador,\nVivamos sempre unidos por mais ardente amor!\nCom simpatia olhando os erros de um irm\u00e3o,\nCuidando de ajud\u00e1-lo com branda compaix\u00e3o.", "Jesus, bondoso Amigo, ensina-nos a amar.\nE, como Tu fizeste, tamb\u00e9m a perdoar!\nPois tanto carecemos do auxilio teu, Senhor!\nUnidos, gra\u00e7as damos por teu imenso amor. Am\u00e9m."]'::json, NULL)
        ,
        ('181'::text, ' Irm�os em Jesus'::text, '["Bem-vindos, irm\u00e3os em Jesus,\nA n\u00f3s, bem unidos na paz\nQue Cristo nos d\u00e1 pela cruz,\nCom gra\u00e7a divina, eficaz.", "Saudamos com santo prazer\nAos crentes em nosso Senhor,\nPois juntos queremos viver,\nHonrando o fiel Benfeitor.", "De quanta ternura e amor\nPodemos aqui partilhar!\nE em Cristo, no riso ou na dor,\nReal comunh\u00e3o desfrutar.", "Unidos vivemos aqui;\nUnidos seremos nos c\u00e9us.\nAlegres, cantando ali\nA grande clem\u00eancia de Deus."]'::json, NULL)
        ,
        ('182'::text, ' Uni�o fraterna'::text, '["Que grande b\u00ean\u00e7\u00e3o \u00e9\nEstarem com amor,\nIrm\u00e3os, ligados pela f\u00e9,\nLouvando ao Salvador!", "O mundo observar\u00e1\nT\u00e3o santa e doce paz;\nE admirado ficar\u00e1\nCom a b\u00ean\u00e7\u00e3o que ela traz.", "Mandaste aos teus, Jesus,\nDa divinal mans\u00e3o,\nO Santo Esp\u00edrito que produz\nT\u00e3o plena comunh\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('183'::text, ' Benditos la�os'::text, '["Benditos la\u00e7os s\u00e3o\nOs do fraterno amor,\nQue assim em santa comunh\u00e3o\nNos unem no Senhor.", "Ao mesmo trono, al\u00e9m,\nV\u00e3o nossas peti\u00e7\u00f5es;\n\u00c9 m\u00fatuo o gozo, e a dor tamb\u00e9m,\nDos nossos cora\u00e7\u00f5es.", "Aqui tudo \u00e9 comum:\nTristeza e prazer;\nEm Cristo somos todos um:\n\u00c9 este o seu querer.", "Se desta santa uni\u00e3o\nNos vamos separar,\nNo c\u00e9u eterna comunh\u00e3o\nHavemos de gozar."]'::json, NULL)
        ,
        ('184'::text, ' Face a face'::text, '["Em breve a vida vou findar,\nN\u00e3o mais aqui eu cantarei!\nNos c\u00e9us, ent\u00e3o, irei morar,\nL\u00e1 na presen\u00e7a do meu Rei.", "E face a face v\u00ea-lo-ei,\nLiberto e salvo cantarei!\nE face a face v\u00ea-lo-ei,\nLiberto e salvo cantarei.", "E seja o dia quando for\nQue Deus me chame para l\u00e1,\nBem certo estou que o Salvador\nContente me receber\u00e1.", "A doce voz me soar\u00e1\nDe Cristo, am\u00e1vel Redentor!\n\u201cFiel, bom servo, bem est\u00e1,\nEntra no gozo do Senhor.\u201d", "Por meu Jesus eu vou viver\nE minha luz farei brilhar.\nDe dia em dia hei de fazer\nO que ao meu Salvador honrar."]'::json, NULL)
        ,
        ('185'::text, ' Gl�ria vindoura'::text, '["No tempo em que meu trabalho acabar\nE enfim de Deus a presen\u00e7a gozar,\nE quando a Cristo eu puder contemplar,\nOh, quanta gl\u00f3ria haver\u00e1 com Jesus!", "Sim, haver\u00e1 gl\u00f3ria sem par,\nJunto a Jesus, gl\u00f3ria sem fim!\nOh, quando a Cristo eu puder contemplar\nGl\u00f3ria, sim, gl\u00f3ria haver\u00e1 com Jesus!", "No tempo em que Cristo, o meu Redentor,\nTiver de dar-me o seu \u201cvinde!\u201d de amor,\nTransposto, enfim, o meu vale de dor,\nOh, quanta gl\u00f3ria haver\u00e1 com Jesus!", "No tempo em que meus irm\u00e3os for rever\nL\u00e1 nos fulgores do c\u00e9u - que prazer!\nSim, quando junto a Jesus for viver,\nOh, quanta gl\u00f3ria haver\u00e1 com Jesus!"]'::json, NULL)
        ,
        ('186'::text, ' O lar do c�u'::text, '["Oh, pensai nesse lar l\u00e1 do c\u00e9u,\nNas gloriosas moradas de luz,\nOnde os crentes, felizes, desfrutam\nDa presen\u00e7a de Cristo Jesus.", "Oh, pensai! Oh, pensai!\nOh, pensai nesse lar l\u00e1 do c\u00e9u!\nL\u00e1 do c\u00e9u, l\u00e1 do c\u00e9u, l\u00e1 do c\u00e9u!\nOh, pensai nesse lar l\u00e1 do c\u00e9u!", "Oh, pensai nos amigos no c\u00e9u,\nQue venceram a luta afinal,\nE nos cantos que sempre ressoam\nNa harmonia do lar eternal.", "Hei de ver, l\u00e1 no c\u00e9u, meu Jesus,\nFace a face seu rosto mirar!\nE bem longe cuidados, tristezas,\nPara sempre com ele habitar.", "Sem demora no c\u00e9u estarei;\nVejo o fim da jornada chegar.\nMeu bondoso Jesus l\u00e1 me espera\nPara as b\u00ean\u00e7\u00e3os eternas me dar."]'::json, NULL)
        ,
        ('187'::text, ' Cidade celestial'::text, '["Tenho lido da bela cidade,\nSituada no reino de Deus,\nCom seus muros de jaspe luzente,\nJuncada com \u00e1ureos trof\u00e9us!\nL\u00e1 no meio da pra\u00e7a est\u00e1 o rio\nDa vida e vigor eternal,\nMas metade da gl\u00f3ria celeste\nJamais se contou ao mortal!", "Jamais se contou ao mortal,\nJamais se contou ao mortal!\nMetade da gl\u00f3ria celeste\nJamais se contou ao mortal!", "Tenho lido dos belos pal\u00e1cios\nQue Jesus foi no c\u00e9u preparar,\nOnde os crentes, fi\u00e9is, mui felizes,\nIr\u00e3o para sempre habitar;\nNem tristeza, nem dor, nem velhice\nAtingem a mans\u00e3o eternal;\nMas metade da gl\u00f3ria celeste\nJamais se contou ao mortal!", "Tenho lido das vestes brilhantes,\nDas coroas que os salvos ter\u00e3o,\nQuando o Pai os chamar, proclamando:\n\u201cTereis eternal galard\u00e3o.\u201d\nTenho lido que os santos na gl\u00f3ria\nPisam ruas de ouro e cristal;\nMas metade da gl\u00f3ria celeste\nJamais se contou ao mortal!", "Tenho lido da hist\u00f3ria bendita\nDe Jesus, o fiel Redentor,\nQue por n\u00f3s padeceu no Calv\u00e1rio\nE aceita o mais vil pecador.\nTenho lido do sangue vertido\nQue a todos redime do mal;\nMas metade da gl\u00f3ria celeste\nJamais se contou ao mortal!"]'::json, NULL)
        ,
        ('188'::text, ' Clara luz'::text, '["Quanta dor, quanta amargura\nVem meu peito retalhar!\nN\u00e3o importa,se diviso\nClara luz al\u00e9m brilhar!\nNela, cheio de esperan\u00e7a,\nCravo os olhos tristes meus:\nEla \u00e9 selo e garantia\nDo supremo amor de Deus.", "Deus predestinou-me e fala:\n\u201cTens em Cristo a reden\u00e7\u00e3o;\nSou a luz dos pecadores,\nDissipando a escurid\u00e3o.\n\u201cVamos, vamos companheiros,\nCaminhemos nessa luz,\nQue atrav\u00e9s da escura noite\nResplandece sobre a cruz", "Eia, avante, a passos largos,\nVamos, vamos sem parar!\nFicar\u00e1 em densas trevas\nQuem na luz n\u00e3o caminhar!\nPois nos mostra a bela terra\nDonde mana leite e mel;\nEssa luz jamais se apaga,\nPois prov\u00e9m do Deus fiel."]'::json, NULL)
        ,
        ('189'::text, ' Jerusal�m celeste'::text, '["Jerusal\u00e9m celeste, de f\u00falgido esplendor.\n\u00c9s v\u00edvida esperan\u00e7a da Igreja do Senhor!\nTeus muros fulgurantes avisto pela f\u00e9,\nE ansioso em alcan\u00e7ar-te mantenho-me de p\u00e9!", "\u00c9 Deus quem te ilumina, perene \u00e9 tua luz;\nRefletes, incessante, a gl\u00f3ria de Jesus.\nQue alegre canto inspiras, oh divinal mans\u00e3o,\nSaber que \u00e9s alvo eterno da peregrina\u00e7\u00e3o.", "Cidade radiosa, sem noite, morte ou mal,\nQue neste pobre mundo atinge o que \u00e9 mortal.\nExulta, \u00f3 alma crente, eis teu glorioso lar,\nQue Cristo, redivivo, no c\u00e9u foi preparar."]'::json, NULL)
        ,
        ('19'::text, ' Rei sublime'::text, '["\u00d3 Rei sublime em majestade e gl\u00f3ria,\nSobre as mil\u00edcias do celeste al\u00e9m,\nOuve o louvor, os hinos de vit\u00f3ria,\nDos que de ti recebem todo bem!", "Vinde, \u00f3 remidos, filhos de Deus!\nCantai louvores que alcancem os c\u00e9us!", "Nos altos c\u00e9us louvor a Deus ressoam,\nDe gratid\u00e3o ao soberano amor!\nOs redimidos com fervor entoam\nO nome excelso de seu Benfeitor.", "Eterno Deus, teus filhos v\u00eas prostrados\nPerante o brilho da superna luz,\nPois do pecado foram resgatados,\nE agora rendem gl\u00f3rias a Jesus!"]'::json, NULL)
        ,
        ('190'::text, ' Maior que a dor'::text, '["Se amarga for aqui a vida,\nIrm\u00e3os, volvei o vosso olhar\nA quem na cruz infame, erguida,\nPor v\u00f3s sofreu sem murmurar.", "A dor assola o mundo inteiro,\nQuem pode alegre sempre andar?\nNa vida tudo \u00e9 passageiro:\nO sofrimento, o bem-estar.", "Por\u00e9m, o verdadeiro gozo,\nNo c\u00e9u, Jesus ir\u00e1 vos dar,\nPois ele diz: \u201cNo lar formoso\nVou preparar-vos um lugar.\u201d"]'::json, NULL)
        ,
        ('191'::text, ' Rio da vida'::text, '["Fonte de amor perene,\nManancial de luz!\n\u00c1gua da vida corre\nDo trono de Jesus!\nCalmo rio! Belo rio!\nQuero estar, tamb\u00e9m,\nOnde as \u00e1guas sempre correm\nDesse rio al\u00e9m.", "Muitos de n\u00f3s j\u00e1 foram\nCantar no c\u00e9u louvor\nCom os remidos cantam\nA Cristo, o Redentor.\nSanto rio! Junto ao rio\nVou cantar tamb\u00e9m,\nOnde as vozes nunca cessam,\nNa Jerusal\u00e9m,", "L\u00edmpida fonte corre,\nBrilhante corre a flux;\nQuem fez aquela alvura?\nO sangue de Jesus.\nCorre rio, calmo corre!\nComo assim a paz\nEm minha alma, para sempre,\nCorra mais e mais."]'::json, NULL)
        ,
        ('192'::text, ' No c�u com Jesus'::text, '["Junto ao trono de Deus, preparado,\nH\u00e1 crist\u00e3o um lugar para ti;\nH\u00e1 alegria perene ao seu lado,\nH\u00e1 profusas del\u00edcias ali;\nSim ali, sim, ali\nDe seus anjos fi\u00e9is rodeado\nNuma esfera de gl\u00f3ria e de luz,\nJunto ao Pai nos espera Jesus!", "Os encantos da terra n\u00e3o podem\nDar id\u00e9ia do gozo dali!\nSe na terra os prazeres acodem,\nTais prazeres se findam aqui.\nMas ali, mas,ali\nAs venturas eternas concorrem\nCom o brilho perp\u00e9tuo da luz,\nA tornar-te feliz com Jesus.", "Conservemos em nossa lembran\u00e7a\nAs riquezas do lindo pa\u00eds,\nE guardemos conosco a esperan\u00e7a\nDuma vida melhor, mais feliz!\nPois ali, pois ali\nO crist\u00e3o, pela f\u00e9, sempre alcan\u00e7a\nAs riquezas do Reino de luz,\nPrometidas por Cristo Jesus.", "Quem quiser desfrutar da ventura\nQue no belo pa\u00eds haver\u00e1,\n\u00c9 somente pedir de alma pura,\nQue de gra\u00e7a Jesus lhe dar\u00e1.\nPois dali, pois dali,\nTodo cheio de amor, de ternura,\nDesse amor que mostrou l\u00e1 na cruz,\nNos atende, nos ouve Jesus."]'::json, NULL)
        ,
        ('193'::text, ' Aspira��o do c�u'::text, '["Vou \u00e0 P\u00e1tria - eu, peregrino -,\nA viver eternamente com Jesus\nQue concedeu-me feliz destino\nQuando, ferido, por mim morreu na cruz.", "Vou \u00e0 P\u00e1tria - eu peregrino -\nA viver eternamente com Jesus!\nVou \u00e0 P\u00e1tria - eu, peregrino -,\nA viver eternamente com Jesus!", "Dor e pena, tristeza e morte\nL\u00e1 no c\u00e9u jamais o salvo encontrar\u00e1!\nE desfrutando de Cristo a sorte,\nEternamente a Deus exaltar\u00e1.", "Terra santa, formosa e pura,\nSalvo por Jesus eu entrarei em ti;\nFelicidade, paz e ventura,\nPerfeitamente desfrutarei ali."]'::json, NULL)
        ,
        ('194'::text, ' Morada feliz'::text, '["Com Jesus h\u00e1 morada feliz,\nPrometida e segura nos c\u00e9us.\nAvistamos o lindo Pa\u00eds,\nPela f\u00e9 na Palavra de Deus.", "Com Jesus, no porvir,\nViveremos no lindo Pa\u00eds!\nCom Jesus no porvir,\nViveremos,no lindo Pa\u00eds!", "Pacientes podemos penar,\nSe sofrermos por Cristo Jesus!\nPois sem culpa, sem falta ou pesar,\nViveremos no Reino de luz!", "No descanso perfeito, eternal,\nDesfrutando o labor que passou,\nCantaremos em tom triunfal,\nOs louvores de quem nos amou."]'::json, NULL)
        ,
        ('195'::text, ' Dormindo no Senhor'::text, '["Dormindo no Senhor,\nBendito \u00e9 nosso irm\u00e3o!\nPerante o trono, vencedor,\nDesfruta a salva\u00e7\u00e3o", "Dormindo no Senhor,\nLiberto j\u00e1 do mal,\nDeixando o mundo e seu labor,\nDescansa em paz real.", "Dormindo no Senhor\nNa gl\u00f3ria de Jesus,\nPerante o grande redentor,\nNos c\u00e9us, vivendo em luz.", "Dormindo no Senhor!\n\u00c9 doce assim morrer!\nDo crente a morte sem terror,\n\u00c9 ir com Deus viver.", "Dormindo no Senhor!\nAo p\u00f3 seu corpo ir\u00e1,\nMas Deus, um dia, com poder\nO ressuscitar\u00e1.", "Os mortos viver\u00e3o!\nE os vivos, com fulgor,\nAo teu encontro subir\u00e3o!\nOh, vem, Jesus Senhor!"]'::json, NULL)
        ,
        ('196'::text, ' Gozo, esplendores, venturas'::text, '["Oh, quanto gozo, esplendores, venturas,\nH\u00e3o de fruir as fi\u00e9is criaturas!\nPaz e descanso ter\u00e3o os cansados,\nPelo Senhor ser\u00e3o galardoados!", "Jerusal\u00e9m, oh, celeste cidade\nP\u00e1tria gloriosa de felicidade;\nLar ideal, sem pecado, sem ja\u00e7a,\nSob o imp\u00e9rio inef\u00e1vel da Gra\u00e7a!", "Na mais completa e feliz harmonia,\nRegozijando em perene alegria,\nN\u00f3s cantaremos eternos louvores,\nCom gratid\u00e3o ao Senhor dos senhores", "De gl\u00f3ria eterna, Senhor, te cobriste!\nEm ti, de ti e por ti tudo existe!\nN\u00f3s te adoramos na voz deste canto,\nDeus Pai, Deus Filho e Esp\u00edrito Santo. Am\u00e9m."]'::json, NULL)
        ,
        ('197'::text, ' O salvador espera'::text, '["Jesus quer entrar hoje em teu cora\u00e7\u00e3o,\nE para sempre ficar.\nE h\u00e1 de outorgar-te seu pleno perd\u00e3o,\nQuando, afinal, ele entrar.", "Oh! Quantas vezes Jesus te chamou,\nE agora ele volta a chamar.\nReal salva\u00e7\u00e3o com seu sangue comprou,\nPara, de gra\u00e7a, te dar.", "Se Cristo adentrar esse teu cora\u00e7\u00e3o,\nE em tua vida reinar,\nQue paz infinita, que satisfa\u00e7\u00e3o\nSua presen\u00e7a h\u00e1 de te dar."]'::json, NULL)
        ,
        ('198'::text, ' Salva��o graciosa'::text, '["Eis mensagem do Senhor, Aleluia!\nPalavras do bom Deus de amor!\nCristo salva o pecador, aleluia!\nSalva at\u00e9 por meio de um olhar!", "Oh, olhai, com f\u00e9, olhai!\nSim, olhai s\u00f3 a Jesus!\nEle salva o pecador, Aleluia!\nSim, salva quem confiante olhar!", "Vossa d\u00edvida pagou, Aleluia!\nJesus a satisfez na cruz.\nSua vida ele entregou, Aleluia!\nPara vos apresentar a Deus", "Esta oferta Cristo faz, Aleluia!\nEterna vida l\u00e1 nos c\u00e9us.\nReden\u00e7\u00e3o aqui vos traz, Aleluia\nConvertei-vos j\u00e1 ao Salvador!", "Aceitai a salva\u00e7\u00e3o, Aleluia\nSegui os passos do Senhor!\nAnunciai o seu perd\u00e3o, Aleluia!\nProclamai a grande Reden\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('199'::text, ' Cristo salva'::text, '["Cristo salva o pecador,\nD\u00e1-lhe um novo cora\u00e7\u00e3o;\nAo contrito, com amor,\nOferece salva\u00e7\u00e3o.", "Confiai em seu poder\nConfiai em seu amor!\nCrede j\u00e1 que Cristo quer\nLibertar o pecador", "Cristo salva o pecador\nE concede-lhe perd\u00e3o;\nAceitai o bom Senhor\nAceitai de cora\u00e7\u00e3o", "Vinde todos e achareis\nPlena paz no Redentor;\nVinde, e ent\u00e3o recebereis\nVida eterna no Senhor."]'::json, NULL)
        ,
        ('2'::text, ' Rever�ncia'::text, '["O Senhor est\u00e1 no seu santo templo,\nO Senhor est\u00e1 no seu santo templo!\nCale-se diante dele toda terra,\nCale-se diante dele toda terra!"]'::json, NULL)
        ,
        ('20'::text, ' Glorifica��o � Trindade'::text, '["Louvor", "A ti, \u00f3 Deus louvamos,\nE por nosso Senhor te confessamos!\nA ti, \u00f3 Pai da Eternidade, adora toda a terra;\nA ti, o coro ang\u00e9lico, A ti, todo o poder do c\u00e9u,\nA ti, querubins e serafins proclamam sem cessar;\n\u201cSanto! Santo! Santo! Senhor Deus dos Ex\u00e9rcitos!", "Os c\u00e9us e a terra est\u00e3o cheios\nDa majestade da sua gl\u00f3ria!\u201d\nA ti, o glorioso coro apost\u00f3lico louva;\nA ti, a congrega\u00e7\u00e3o dos profetas louva;\nA ti, o nobre ex\u00e9rcito dos m\u00e1rtires louva;\nA santa Igreja reconhece por toda a terra a ti:\nAo Deus Pai, infinito Dominador!\nE ao teu vener\u00e1vel, vero e \u00fanico Filho,\nE ao Santo Esp\u00edrito, Consolador.", "Declara\u00e7\u00e3o", "Tu \u00e9s Rei da gl\u00f3ria, \u00f3 Jesus!\nTu \u00e9s o sempiterno Filho de Deus!\nQuando vieste resgatar os homens,\nN\u00e3o te escusaste a nascer do ventre da virgem!\nQuando venceste a morte e seu aguilh\u00e3o,\nLogo abriste a teus servos", "As portas do reino dos c\u00e9us.\nEst\u00e1s sentado \u00e0 destra de Deus\nNo trono Onipotente.\nCremos que voltar\u00e1s e aqui ser\u00e1s Juiz.\nPortanto, n\u00f3s, remidos com teu sangue,\nSuplicamos teu aux\u00edlio!\nN\u00f3s, que j\u00e1 estamos alistados com teus santos\nNa gl\u00f3ria eterna.", "Ora\u00e7\u00e3o", "Senhor, guarda o povo e aben\u00e7oa-nos.\nReina e guarda-nos para sempre.\nNoite de dia te adoramos\nE glorificamos teu nome sem par\nSenhor, concede que hoje", "N\u00f3s ven\u00e7amos o pecado!\nEterno e bondoso Pai, compadece-te de n\u00f3s!\nConcede-nos a tua miseric\u00f3rdia,\nPois confiamos e esperamos em ti.\nSenhor! Em ti, em ti eu espero;\nNunca eu seja, nunca eu seja confundido. Am\u00e9m."]'::json, NULL)
        ,
        ('200'::text, ' Palavra aben�oada'::text, '["Palavra aben\u00e7oada,\nConvite que cont\u00e9m\nPromessa e cumprimento\nCom infinito bem.\n\u201cOh, vinde a mim!\u201d- conclama\nO Cristo o Salvador,\nSeus bra\u00e7os estendendo\nAo pobre pecador.", "Vinde, oh, vinde a mim! ( b i s )\nTristes alquebrados,\nVinde, oh, vinde a mim!\nVinde, oh vinde a mim! ( b i s )\nFracos e cansados,\nVinde, oh, vinde a mim!", "Por que viver t\u00e3o longe\nDa gra\u00e7a de Jesus?\nPor que vagar nas trevas,\nPodendo andar na luz?\nDa vida sem proveito,\nDa culpa e perdi\u00e7\u00e3o,\nCorramos para a senda\nDa eterna salva\u00e7\u00e3o.", "Em tudo agora e sempre,\nOu\u00e7amos o Senhor;\nPois h\u00e1 descanso e al\u00edvio\nNo seu profundo amor.\nAssim conheceremos\nO gozo que produz\nNo cora\u00e7\u00e3o submisso\nO \u201cvinde\u201d de Jesus."]'::json, NULL)
        ,
        ('201'::text, ' Manso e suave'::text, '["Manso e suave! Eis Jesus nos chamando:\nChama por ti e por mim.\nEis que ele \u00e0s portas espera velando:\nVela por ti e por mim!", "Vem j\u00e1! Vem j\u00e1! Alma cansada, vem j\u00e1!\nManso e suave, Jesus convidando,\nChama: \u201c\u00d3 pecador, vem!\u201d", "Com paci\u00eancia, ele est\u00e1 esperando\nHoje por ti e por mim.\nOh, n\u00e3o desprezes a quem, convidando,\nConvida a ti e a mim.!", "O tempo corre! As horas, passando,\nPassam por ti e por mim!\nTranses de morte, por fim, v\u00e3o chegando,\nChegam a ti e a mim.", "Oh, quanto amor que Jesus nos em dado:\nDado a ti e a mim!\nEis que foi morte, foi crucificado\nMorto por ti e por mim!"]'::json, NULL)
        ,
        ('202'::text, ' Palavras preciosas'::text, '["Preciosas as palavras\nDe Jesus, eterno Rei:\n\u201cAquele que vier a mim,\nEu n\u00e3o desprezarei.\u201d", "Preciosas as palavras\nDe Jesus, eterno Rei:\n\u201c\u00d3 v\u00f3s, cansados, vinde j\u00e1!\n\u00c9 suave a minha lei.\u201d", "Preciosas as palavras\nDe Jesus, eterno Rei:\n\u201cA porta sou, por mim entrai,\nDescanso vos darei.\u201d", "Preciosas as palavras\nDe Jesus, eterno Rei:\n\u201cPor v\u00f3s o mundo j\u00e1 venci,\nPor v\u00f3s a vida dei."]'::json, NULL)
        ,
        ('203'::text, ' Convite para a salva��o'::text, '["Eis, Cristo te convida; vem pecador!\nConcede eterna vida, vem, pecador!\nHoje \u00e9 t\u00e3o favor\u00e1vel, vem, pecador!\n\u00c9 tempo aceit\u00e1vel, vem, pecador!", "Eis o ensejo agora, vem pecador!\nAtende sem demora, vem, pecador!\nCereces desta Gra\u00e7a? Vem, pecador!\nDecide! A vida passa! Vem, pecador!"]'::json, NULL)
        ,
        ('204'::text, ' Cristo � porta'::text, '["\u00c0 porta estou, \u00f3 alma triste,\nAnsioso por e consolar;\nA minha voz, enfim, ouviste,\nEu vou entrar, eu vou entrar!", "\u00c0 porta, por amor levado,\nProcuro j\u00e1 teu mal sanar;\n\u00d3 pecador desalentado,\nEu vou entrar, eu vou entrar!", "Por ti foi grande o meu castigo,\nSofri na cruz sem murmurar!\nS\u00f3 para teres paz comigo,\nEu vou entrar, eu vou entrar!", "A minha gra\u00e7a poderosa\nO teu pecado vem lavar!\n\u00d3 alma impura, pesarosa,\nEu vou entrar, eu vou entrar!"]'::json, NULL)
        ,
        ('205'::text, ' O amor que chama'::text, '["N\u00e3o vos demoreis, Jesus vos chama,\nEle chama com amor.\nN\u00e3o vos demoreis, Jesus vos ama,\nEle acalma a vossa dor.", "N\u00e3o vos demoreis!\nN\u00e3o vos demoreis!\nVinde sem temor!\nQuem vos chama \u00e9 Jesus,\nQuem morreu por n\u00f3s na cruz.", "N\u00e3o vos demoreis, perd\u00e3o alcan\u00e7a\nQuem confia no Senhor.\nN\u00e3o vos demoreis, e, sem tardan\u00e7a,\nRecebei o Redentor.", "N\u00e3o vos demoreis, Jesus foi morto\nEm lugar do pecador.\nN\u00e3o vos demoreis, real conforto\nQuer vos dar o Salvador."]'::json, NULL)
        ,
        ('206'::text, ' Convite de jesus'::text, '["Triste est\u00e1s, cansado, aflito,\nSem vigor, sem luz?\n\u201cVem a mim!\u201d Eis o convite de Jesus!", "\u201cQuais as marcas que me apontam\nSeu real perd\u00e3o?\u201d\nNos seus p\u00e9s, nas m\u00e3os, no lado se achar\u00e3o!", "\u201cTraz coroa de monarca?\nVeste de esplendor\u201d\nTem coroa, sim, de espinhos, sangue e dor.", "\u201cQuando o vir e ent\u00e3o segui-lo,\nRecompensar\u00e1?\u201d\nN\u00e3o! Jamais a infinda gra\u00e7a vai falhar!", "\u201cSe constante o obede\u00e7o,\nGalard\u00e3o terei?\u201d\nSim, ter\u00e1s, e boas-vindas do teu Rei!", "\u201cSe confio na Palavra,\nSalvar\u00e1, por fim?\u201d\nAnjos, santos e o universo bradam: \u201cSim.\u201d"]'::json, NULL)
        ,
        ('207'::text, ' Convite ao pecador'::text, '["Vem a Cristo, mesmo agora,\nVem assim como est\u00e1s!\nQue dele, sem demora,\nO perd\u00e3o obter\u00e1s.", "Cr\u00ea em Cristo, sem deten\u00e7a,\nFoi por n\u00f3s que morreu!\nE s\u00f3 por esta cren\u00e7a\nTens entrada no c\u00e9u.", "Ele almeja receber-te,\nTua vida salvar,\nPois quer consigo ver-te\nE contigo habitar."]'::json, NULL)
        ,
        ('208'::text, ' Aproxima��o'::text, '["Chegai-vos ao Senhor com puro cora\u00e7\u00e3o.\nOuvi palavras de favor, a voz da salva\u00e7\u00e3o!\n\u00c9 Deus quem fala aqui, na sua santa lei;\nHumildes, pois, a ele ouvi e sempre obedecei.", "Entendimento d\u00e1 a quem com f\u00e9 pedir.\nCom paci\u00eancia guiar\u00e1 a todo que o seguir.\nAs trevas dissipou, rasgou o escuro v\u00e9u,\nA luz divina derramou, por ela vinde ao c\u00e9u."]'::json, NULL)
        ,
        ('209'::text, ' Encorajamento'::text, '["Medo tens que o advers\u00e1rio v\u00e1 vencer?\nLuz te falta e onde est\u00e1s n\u00e3o podes ver?\nAbre o cora\u00e7\u00e3o, que Cristo vai entrar,\nE o sol em ti raiar.", "Deixa a luz do c\u00e9u entrar,\nDeixa o sol em ti nascer;\nAbre o cora\u00e7\u00e3o que, Cristo vai entrar,\nE o sol em ti nascer.", "Tu n\u00e3o tens ainda f\u00e9 no Salvador?\nDeus n\u00e3o ouve as tuas preces com favor?\nAbre o cora\u00e7\u00e3o, que Cristo vai entrar,\nE o sol em ti raiar.", "Queres ir andando alegre para o c\u00e9u\nIgnorando todo o negro e denso v\u00e9u?\nAbre o cora\u00e7\u00e3o, que Cristo vai entrar,\nE o sol em ti raiar."]'::json, NULL)
        ,
        ('21'::text, ' Deus de Abra�o'::text, '["Ao Deus de Abra\u00e3o louvai, do vasto c\u00e9u Senhor,\nEterno e poderoso Pai e Deus de amor!\nAugusto Jeov\u00e1 que terra e c\u00e9u criou,\nMinha alma o Nome exaltar\u00e1 do grande EU-SOU.", "Ao Deus de Abra\u00e3o louvai! Eis por mandado seu,\nMinha alma deixa a terra e vai gozar no c\u00e9u.\nO mundo desprezei, seu lucro e seu louvor,\nE Deus por meu quinh\u00e3o tomei e protetor.", "Meu Guia Deus ser\u00e1! Seu infinito amor\nFeliz em tudo me far\u00e1 por onde eu for.\nTomou-me pela m\u00e3o, na trevas deu-me luz,\nE d\u00e1-me eterna salva\u00e7\u00e3o por meu Jesus.", "Meu Deus por si jurou, eu nele confiei!\nE para o c\u00e9u que preparou eu subirei.\nSua face eu hei de ver, confiado em seu amor,\nE para sempre engrandecer meu Redentor."]'::json, NULL)
        ,
        ('210'::text, ' O filho perdido'::text, '["Vem, filho perdido, \u00f3 pr\u00f3digo, vem!\nRu\u00edna te espera nas trevas al\u00e9m.\nTu de medo, tremendo, e faminto, gemendo,\n\u00d3 filho perdido, vem, pr\u00f3digo, vem!", "Vem, vem! \u00d3 pr\u00f3digo, vem!", "Vem, filho perdido! \u00d3 pr\u00f3digo, vem!\nTeu Pai te convida, querendo-te bem!\nVestes h\u00e1 para ornar-te, ricos dons: vem fartar-te!\n\u00d3 filho perdido, vem, pr\u00f3digo, vem!", "Vem, filho perdido! Oh, volta a Jesus\nBondade infinita se avista na cruz.\nEm mis\u00e9ria vagando, tuas culpas chorando,\n\u00d3 filho perdido, vem, pr\u00f3digo, vem!", "\u00d3 pr\u00f3digo, escuta a voz do Senhor!\nOh, rompe as ciladas do vil tentador!\nEm teu lar h\u00e1 bastante, e tu vagas errante!\n\u00d3 filho perdido, vem, pr\u00f3digo,vem!"]'::json, NULL)
        ,
        ('211'::text, ' Quase induzido'::text, '["Quase induzido a crer em Jesus;\nQuase induzi-do a andar na luz!\nPensas em replicar:\n\u201cQuando tiver vagar,\nIrei, ent\u00e3o, buscar o Redentor.\u201d", "Quase induzido, oh, cora\u00e7\u00e3o!\nQuase induzi-do, oh, ilus\u00e3o!\nHoje o bom Salvador,\nCom voz de terno amor,\nQuer dar ao pecador a sua paz.", "Quase induzido, n\u00e3o queres crer?\nQuase induzi-do, vem sem temer!\n\u201cQuase\u201d n\u00e3o servir\u00e1;\n\u201cQuase\u201d te afastar\u00e1!\n\u201cQuase\u201d te lan\u00e7ar\u00e1 na perdi\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('212'::text, ' Apelo'::text, '["Alma, escuta o bom Senhor\nA Jesus, o Salvador.\nEle diz com terno amor:\n\u201cTu me amas, pecador?\u201d\nDas pris\u00f5es te soltarei,\nAs feridas curarei!\nVim do c\u00e9u por teu amor;\n\u201cTu me amas, pecador?", "\u201cMinha gl\u00f3ria tu ver\u00e1s,\nMinha gra\u00e7a gozar\u00e1s!\nVida eterna te darei,\nN\u00e3o te desampararei!\u201d\nAjudar-me vem, Senhor!\nA entregar-te o meu amor!\nFaze, \u00f3 bom Jesus, que assim\nSejas Tu que viva em mim. Am\u00e9m"]'::json, NULL)
        ,
        ('213'::text, ' A �ltima hora'::text, '["Ao findar o labor desta vida,\nQuando a morte ao teu lado chegar,\nQue destino h\u00e1 de ter a tua alma?\nQual ser\u00e1, no futuro, teu lar?", "Meu amigo, hoje tens a escolha:\nVida ou morte, qual vais aceitar?\nAmanh\u00e3 pode ser muito tarde,\nHoje Cristo te quer libertar.", "Tu procuras a paz neste mundo\nEm prazeres que passam em v\u00e3o,\nMas na \u00faltima hora da vida,\nTais prazeres valor n\u00e3o ter\u00e3o.", "Muitas vezes tu riste, \u00f3 amigo,\nAo ouvires falar de Jesus.\nMas somente o Senhor pode dar-te\nSalva\u00e7\u00e3o, pela morte na cruz.", "Com tua alma em pecado n\u00e3o podes\nAdentrar as moradas de Deus.\nMas aquele quem Deus tornou limpo,\nGozar\u00e1 das venturas dos c\u00e9us!", "Se quiseres deixar teus pecados\nE entregar-te hoje mesmo a Jesus,\nH\u00e1s de ter, nesta vida e na morte,\nUm brilhante caminho de luz."]'::json, NULL)
        ,
        ('214'::text, ' Convite e aceita��o'::text, '["Ou\u00e7o meu Senhor dizer:\n\u201cTuas for\u00e7as d\u00e9beis s\u00e3o,\nNada podes merecer;\nEu te dou a salva\u00e7\u00e3o.", "A ti, Senhor Jesus,\nVenho como estou;\nPois sem nada merecer,\nTeu sangue me salvou.", "Sim, eu venho a ti, Senhor,\nTua gra\u00e7a receber;\nInfinito \u00e9 o teu amor,\nSem limite o teu poder.", "Quando estou em afli\u00e7\u00f5es,\nTu \u00e9s meu Consolador;\nQuando exposto \u00e0s tenta\u00e7\u00f5es,\nMeu aux\u00edlio e protetor.", "L\u00e1 na gl\u00f3ria cantarei\nTua eterna salva\u00e7\u00e3o;\nJunto ao trono renderei\nMeu louvor e gratid\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('215'::text, ' Apelo'::text, '["Jesus, est\u00e1s \u00e0 porta\nDo aflito cora\u00e7\u00e3o!\nE queres fazer nele\nPerene habita\u00e7\u00e3o.\nCrist\u00e3os jamais seremos,\nNem filhos do Senhor,\nSe n\u00e3o fizer morada\nEm n\u00f3s o Salvador.", "Jesus, est\u00e1s batendo\nCom traspassada m\u00e3o!\nEspinhos tens na fronte,\nNos olhos compaix\u00e3o!\nCom amor incompreens\u00edvel\nEsperas sem cansar!\nPor causa do pecado\nN\u00e3o podes Tu entrar!", "Jesus, nos amas tanto,\nBondoso \u00e9 teu olhar!\nSofreste no Calv\u00e1rio\nA fim de nos salvar!\nSenhor, com f\u00e9 abrimos\nO nosso cora\u00e7\u00e3o!\nOh, entra e faze nele\nEterna habita\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('216'::text, ' Perd�o'::text, '["Acharei merc\u00ea em Deus?\nPode ainda perdoar?\nEsquecer pecados meus?\nMinha vida transformar?", "Eu que sempre resisti,\nSua ira suscitei,\nSeus apelos n\u00e3o ouvi,\nTransgredi a sua Lei!", "Arrependo-me, Senhor,\nTenho ao mal grande avers\u00e3o!\nSinto nalma intenso horror\nPosso ter o teu perd\u00e3o?", "Mansamente diz Jesus:\n\u201cQuero a paz te conceder!\nA salvar-te fui \u00e0 cruz,\nSalvo est\u00e1s, se podes crer.\u201d", "Sim, eu creio e salvo estou!\nReina gozo l\u00e1 no c\u00e9u,\nPois a Nova j\u00e1 chegou\nDe mais um que nele creu."]'::json, NULL)
        ,
        ('217'::text, ' Desprendimento'::text, '["Tal como estou, sem me esquivar,\nMe entrego a quem me quis salvar.\nPois padeceste em meu lugar,\n\u00d3 Salvador, eu venho a ti.", "Tal como estou, sem esperar\nDas manchas todas livre estar,\nPureza venho em ti buscar,\n\u00d3 Salvador, eu venho a ti.", "Tal como estou, sem dire\u00e7\u00e3o,\nConfuso, em grande como\u00e7\u00e3o,\nTemendo, em luta a tenta\u00e7\u00e3o;\n\u00d3 Salvador, eu venho a ti.", "Tal como estou, na escurid\u00e3o\nMe estendes tua santa m\u00e3o,\nE em ti encontro a salva\u00e7\u00e3o;\n\u00d3 Salvador, eu venho a ti.", "Tal como estou me acolher\u00e1s,\nPureza e al\u00edvio me dar\u00e1s!\nTu prometeste e cumprir\u00e1s;\n\u00d3 Salvador, eu venho a ti.", "Tal como estou, por teu amor\nSa\u00ed das garras do opressor!\nE teu somente sou, Senhor;\n\u00d3 Salvador, eu venho a ti. Am\u00e9m."]'::json, NULL)
        ,
        ('218'::text, ' Vontade soberana'::text, '["Tua vontade faze, \u00f3 Senhor!\nEu sou feitura, tu \u00e9s o Autor.\nMolda e refaze todo meu ser,\nSegundo as normas do teu querer", "Tua vontade faze, \u00f3 meu Deus!\nSonda e corrige os passos meus!\nTorna-me santo porque Tu \u00e9s!\nOuve os meus rogos, eis-me aos teus p\u00e9s.", "Tua vontade faze, \u00f3 meu Pai!\nPor ela o crente vive e n\u00e3o cai.\nGuia-me a vida com tua luz!\nPoder e gra\u00e7a d\u00e1-me em Jesus.", "Tua vontade, boa e sem par\nQuero na vida realizar.\nVive, triunfa, domina, enfim,\nReina, supremo, meu Deus, em mim! Am\u00e9m."]'::json, NULL)
        ,
        ('219'::text, ' Dedica��o'::text, '["Sofreste, \u00f3 meu Senhor, na amarga cruz,\nE derramaste ali teu sangue a flux;\nQue resgatou minha alma dos pecados meus\nE deu-me o c\u00e9u de luz!", "Agora, \u00f3 Salvador, quero te honrar,\nE, enquanto aqui viver, sempre te amar!\nE dando provas desse amor, meu cora\u00e7\u00e3o\nDesejo consagrar.", "Aceita, \u00f3 terno Deus, a adora\u00e7\u00e3o\nQue oferto a ti, Senhor, com devo\u00e7\u00e3o.\nDeponho, ent\u00e3o, a teus p\u00e9s, para sempre, \u00f3 Pai,\nA eterna gra-tid\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('22'::text, ' Os c�us proclamam'::text, '["Altamente os c\u00e9us proclamam\nSeu divino Criador!\nAnunciam o firmamento\nTua obras \u00f3 Senhor!\nIncessantes, noite e dia,\nD\u00e3o sinais do teu poder,\nSem palavras, proclamando\nDeus excelso no saber!", "Majestoso o sol caminha\nPelos c\u00e9us, com resplendor!\nExultando, em seu percurso,\nEnche o mundo de calor.\nTodo o vasto universo\nCanta, em coro, teu louvor!\nE a n\u00f3s vem doce ensino\nDa Palavra do Senhor.", "Tua Lei - qu\u00e3o preciosa!-\nBrilha mais que a clara luz;\nEsclarece as nossas mentes,\nGuia as almas a Jesus,\nEm tua gra\u00e7a meditando,\nCantarei, \u00f3 bom Senhor,\nE ser\u00e1 na minha boca\nAgrad\u00e1vel o teu louvor. Am\u00e9m."]'::json, NULL)
        ,
        ('220'::text, ' Plena dedica��o'::text, '["Sobre a cruz Jesus comprou-me,\nCorpo e alma, todo o ser.\nHoje e sempre, inteiramente,\nQuero e ele pertencer.", "Meu Senhor! Meu Senhor!\nSempre a ti pertencerei.", "Os meus olhos guia sempre,\nConsagrando-os a Jesus!\nDa vaidade bem distantes,\nFaze-os ver celeste luz", "Oh! Disp\u00f5e o meu ouvido\nA fechar-se para o mal,\nAtendendo ao teu ensino\nCom respeito filial.", "Torna, \u00f3 Deus, a l\u00edngua serva\nDe Jesus, meu grande Rei!\nP\u00f5e palavras nos meus l\u00e1bios,\nE teu nome exaltarei.", "Minhas m\u00e3os, Senhor, emprega\nNo servi\u00e7o que conv\u00e9m\nDiligentes para o Mestre,\nTrabalhando em todo o bem!", "Os meus p\u00e9s sustenta sempre,\nFirmes, nos caminhos teus.\nNos sagrados mandamentos\nConsolida os passos meus.", "Que ventura pertencer-te!\nOuve a minha peti\u00e7\u00e3o:\nVem, Senhor, supremo Amigo,\nDominar meu cora\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('221'::text, ' Um vaso de ben��o'::text, '["Quero ser um vaso de b\u00ean\u00e7\u00e3o,\nSim, um vaso escolhido de Deus,\nPara as novas levar aos perdidos,\nBoas-novas que v\u00eam l\u00e1 dos c\u00e9us.", "Faze-me vaso de b\u00ean\u00e7\u00e3o, Senhor\nVaso que leve a mensagem de amor!\nEis-me submisso, e ao teu servi\u00e7o\nEu me consagro, bendito Senhor!", "Quero ser um vaso de b\u00ean\u00e7\u00e3o,\nPara todos os dias fazer\nOs culpados que vivem nas trevas\nO perd\u00e3o de Jesus conhecer.", "Quero ser um vaso de b\u00ean\u00e7\u00e3o,\nSim, um vaso de b\u00ean\u00e7\u00e3o sem par,\nAnunciando que os crentes em Cristo\nJubilosos nos c\u00e9us h\u00e3o de entrar.", "Para ser um vaso de ben\u00e7\u00e3o\n\u00c9 mister uma vida real,\nUma vida de f\u00e9 e pureza,\nRevestida de amor divinal. Am\u00e9m."]'::json, NULL)
        ,
        ('222'::text, ' Mais perto da cruz'::text, '["Meu Senhor, sou teu, tua voz ouvi\nA chamar-me com amor!\nE contigo sempre desejo estar,\n\u00d3 bendito Salvador!", "Mais perto da tua cruz\nQuero estar, \u00f3 Salvador!\nMais perto da tua cruz\nEu desejo estar, Senhor.", "A seguir-te sempre, eu me decidi,\nConstrangido pelo amor!\nJubiloso, pois me declaro teu,\nSem reservas, meu Senhor.", "Que prazer sem par, que del\u00edcia \u00e9\nAos teus santos p\u00e9s me achar!\nE com viva f\u00e9 e profundo amor,\nCom meu Salvador falar. Am\u00e9m."]'::json, NULL)
        ,
        ('223'::text, ' Ora��o do arrependido'::text, '["Convencido estou, \u00f3 Cristo\nDe que o mundo \u00e9 sempre v\u00e3o.\nQuero, pois, viver contigo,\nSempre em doce comunh\u00e3o", "Toma, \u00f3 Salvador,\nMinha d\u00e9bil m\u00e3o!\nPois viver contigo eu quero\nSempre em comunh\u00e3o!", "Quanta dor, tristeza imensa,\nSinto, \u00f3 Deus, ao me lembrar\nQue vivi por tanto tempo,\nTua causa a desprezar!", "N\u00e3o mais quero a vida ingl\u00f3ria,\nOnde, incauto, me abismei.\nNa doutrina do evangelho,\nDe hoje em diante eu viverei!", "Quero os dons que me outorgaste\nEmpregar no teu labor,\nTrabalhando em tua Igreja,\nBem firmado em teu amor. Am\u00e9m."]'::json, NULL)
        ,
        ('224'::text, ' Consagra��o'::text, '["A ti seja consagrada\nMinha vida, \u00f3 meu Senhor!\nMeus momentos e meus dias\nSejam s\u00f3 em teu louvor.", "Sempre minhas m\u00e3os se movam\nCom presteza e com amor!\nE meus p\u00e9s velozes corram\nAo servi\u00e7o do Senhor.", "Minha voz, Jesus aceita,\nPara o teu louvor cantar!\nFaze que meus l\u00e1bios possam\nA mensagem proclamar.", "Os meus bens quero ofertar-te,\nNada quero te esconder;\nMinha intelig\u00eancia guia\nSob a luz do teu saber.", "A vontade minha toma\nSujeitando-a a ti, Senhor\nDo meu cora\u00e7\u00e3o fazendo\nO teu trono, \u00f3 Salvador!", "Meu anelo, meu desejo,\nSejam s\u00f3 teu nome honrar!\nQue meu ser completo eu possa,\n\u00d3 Jesus, te consagrar! Am\u00e9m."]'::json, NULL)
        ,
        ('225'::text, ' Dedica��o pessoal'::text, '["Eis-me, \u00f3 Salvador, aqui!\nCorpo e alma oferto a ti!\nServo in\u00fatil, sem valor,\nMas perten\u00e7o a meu Senhor!", "Fraco em obra e no pensar;\nMui propenso a trope\u00e7ar;\nSalvo estou por teu amor\nE me voto a ti Senhor!", "Subjugado em todo ser,\nMe submeto ao teu poder!\nGrande \u00e9 o pre\u00e7o do perd\u00e3o,\nD\u00e1-me igual consagra\u00e7\u00e3o!", "Eu, remido pecador,\nMe dedico ao Redentor.\nTeu \u00e9 este cora\u00e7\u00e3o,\nTeu em plena sujei\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('226'::text, ' A hist�ria de Cristo'::text, '["Conta-me a hist\u00f3ria de Cristo,\nGrava-a no meu cora\u00e7\u00e3o!\nConta-me a hist\u00f3ria inaudita\nDe gra\u00e7a, paz e perd\u00e3o.\nConta como ele, encarnado,\nVeio no mundo morar;\nAos pecadores indignos,\nDe Deus o amor revelar.", "Conta como ele, bondoso,\nNunca a ningu\u00e9m rejeitou,!\nComo, de m\u00e3os estendidas,\nTodos a si convidou!\nComo n\u00e3o pode o bom Mestre\nSeja a quem for rejeitar,\nSe, convencido e contrito,\nO seu convite aceitar!", "Conta-me quando, no monte,\nSobre a cidade chorou,\nPois, orgulhosa e rebelde,\nO seu amor rejeitou.\nConta como ele ainda chora\nSobre os que seguem o mal,\nE, endurecidos, resistem\nAo seu amor divinal.", "Conta-me as duras afrontas\nQue mansamente sofreu!\nComo na cruz, levantado,\nEle por \u00edmpios morreu!\nD\u00e1-me o viver na certeza\nDe que foi mesmo por mim,\nQue seu amor inef\u00e1vel\nN\u00e3o tem mudan\u00e7a nem fim."]'::json, NULL)
        ,
        ('227'::text, ' A velha hist�ria'::text, '["Conta-me a velha hist\u00f3ria\nDo grande Salvador,\nDe Cristo e sua vida,\nDe Cristo e seu amor.\nCom pausa e paci\u00eancia,\nPois quero penetrar\nA altura do mist\u00e9rio,\nQue Deus me pode amar.", "Conta-me a velha hist\u00f3ria,\nQue fala ao cora\u00e7\u00e3o,\nDe Cristo e sua gl\u00f3ria,\nDe Cristo e seu perd\u00e3o!", "Fala-me com ternura\nDo amado Redentor,\nA mim que tanto sofro\nPor ser um pecador.\nOh, que consolo traz-me\nEm tempos de afli\u00e7\u00e3o,\nOuvir a velha hist\u00f3ria\nQue alegra o cora\u00e7\u00e3o!", "Se o brilho deste mundo\nToldar dos c\u00e9us a luz\nOh, narra com ternura\nA hist\u00f3ria de Jesus!\nE quando, enfim, a aurora\nDo mundo al\u00e9m raiar,\nRecorda a velha hist\u00f3ria,\nQue Deus me quis salvar!"]'::json, NULL)
        ,
        ('228'::text, ' De deus, o ungido'::text, '["Saudai de Deus, o Ungido,\nO Filho de Davi;\nSaudai o Prometi-do\nQue vem reinar aqui!\nAs garras da maldade\nPartiu e nos livrou!\nE um reino de equidade\nNo mundo, ent\u00e3o, criou!", "O Justo, em sofrimento,\nSocorre com amor;\nAos pobres d\u00e1 sustento\nE aos fracos d\u00e1 vigor.\nAos tristes d\u00e1 alegria,\nDesfaz a escurid\u00e3o,\nE ao que pereceria\nOutorga a salva\u00e7\u00e3o.", "No trono glorioso\nEnt\u00e3o se assentar\u00e1!\nDas eras vitorioso,\nBendito reinar\u00e1\nConcerto permanente\nConosco ir\u00e1 guardar\nSeu nome, eternamente,\nTraduz amor sem par."]'::json, NULL)
        ,
        ('229'::text, ' Boas-novas'::text, '["Eu venho a v\u00f3s, dos altos c\u00e9us,\nCom boas-novas do meu Rei\nMaravilhoso e grande Deus!\nOuvi-me: A hist\u00f3ria contarei.", "Um pequenino vos nasceu\nDa virgem m\u00e3e, h\u00e1 tempo atr\u00e1s.\nCom ele Deus ao mundo deu\nAmor e vida, gozo e paz.", "Comigo vem, meu bom Jesus,\nEscolhe, como ber\u00e7o teu,\nMeu cora\u00e7\u00e3o, e em tua luz\nPor certo alcan\u00e7arei o c\u00e9u.", "Eterna gl\u00f3ria ao nosso Deus,\nQue veio em Cristo nos salvar.\nAlegres anjos v\u00eam dos c\u00e9us\nUm novo dia anunciar!"]'::json, NULL)
        ,
        ('23'::text, ' Adora��o ao Criador'::text, '["Oh! Vinde adorar ao Deus criador,\nDa terra e dos c\u00e9us eterno Senhor,\nAo Deus que, habitando no meio da luz,\nA n\u00f3s se revela por Cristo Jesus.", "Seu grande poder deveis contemplar\nNo c\u00e9u estrelado, no amplo mar.\nA gota de orvalho, a m\u00ednima flor,\nProclamam, constantes, ser Deus seu Autor.", "Oh! Vinde adorar ao Deus Salvador,\nQue em Cristo Jesus nos mostra favor.\nAo Deus que, benigno, com gra\u00e7a e perd\u00e3o,\nPecados cobrindo nos d\u00e1 reden\u00e7\u00e3o."]'::json, NULL)
        ,
        ('230'::text, ' Adora��o'::text, '["Oh, vinde fi\u00e9is, triunfantes e alegres,\nSim, vinde a Bel\u00e9m, j\u00e1 movidos de amor!\nNasceu vosso Rei, o Messias prometido,", "Oh, vinde adoremos! Oh vinde adoremos!\nOh, vinde adoremos ao nosso Senhor!", "Olhai, admirados, a sua humildade,\nOs anjos o louvam com grande fervor!\nPois veio conosco habitar, encarnado,", "Por n\u00f3s, das alturas celestes baixando,\nEm forma de servo se fez,por amor!\nE em gl\u00f3rias a vida nos d\u00e1, sempiterna,", "Nos c\u00e9us adorai-o, v\u00f3s anjos, em coro,\nE todos na terra lhe rendam louvor!\nA Deus honra e gl\u00f3ria contentes rendamos,"]'::json, NULL)
        ,
        ('231'::text, ' O Primeiro natal'::text, '["Eis que um anjo proclamou o primeiro Natal,\nA uns pobres pastores ao p\u00e9 de Bel\u00e9m\nQue, nos campos, a guardar seu rebanho, afinal,\nSuportavam, da noite, o frio tamb\u00e9m.", "Natal! Natal! Natal! Natal!\n\u00c9 vindo ao mundo o Rei divinal!", "De repente, l\u00e1 no c\u00e9u, linda estrela surgiu,\nE no oriente brilhou com estranho fulgor.\nVeio \u00e0 terra forte luz, que do c\u00e9u lhe caiu,\nMuitas noites, ainda, em fulgente esplendor.", "Tal estrela apareceu e os magos guiou\nPela estrada a Bel\u00e9m, rumo certo os conduz.\nE chegando ali, por fim, a estrela parou,\nMesmo acima da casa em que estava Jesus.", "E os magos, com af\u00e3 e sublime temor,\nOs joelhos dobraram naquele lugar,\nPara ofertas liberais, e de raro valor,\nQual incenso, ouro e mirra, ao Menino entregar.", "E como eles vimos n\u00f3s, com intenso fervor,\nDar louvores sinceros a quem nos amou;\nAdorar de cora\u00e7\u00e3o o Supremo Senhor\nQue, morrendo na cruz, nossas almas salvou!"]'::json, NULL)
        ,
        ('232'::text, ' Pequena vila de Bel�m'::text, '["Pequena vila de Bel\u00e9m,\nRepousa em teu dormir,\nEnquanto os astros l\u00e1 no c\u00e9u\nEst\u00e3o a refulgir.\nPor\u00e9m, nas tuas trevas\nResplende eterna luz,\nIncompar\u00e1vel, divinal:\nNasceu o bom Jesus!", "Da virgem m\u00e3e nasceu Jesus!\nV\u00f3s anjos proclamai\nAs boas-novas l\u00e1 dos c\u00e9us,\nE a Deus louvores dai!\nEstrelas matutinas,\nEm hinos de louvor,\nAos anjos e homens proclamai\nDe Deus o eterno amor.", "O dom glorioso, divinal\nNenhum estrondo faz,\nAssim, aos homens, o Senhor\nConcede gra\u00e7a e paz.\nSereno e sem alarde\nVem ele ao mundo assim,\nTrazendo aos homens Reden\u00e7\u00e3o,\nAmor e paz sem fim.", "Oh! Vem morar nos cora\u00e7\u00f5es,\nDivino Rei, Jesus,\nE faze-os entrever no c\u00e9u\nVis\u00f5es da tua luz.\nAli proclamam anjos\nDe Deus o amor fiel!\nOh! Vem, Senhor, aqui reinar,\nEterno Emanuel. Am\u00e9m."]'::json, NULL)
        ,
        ('233'::text, ' Os pastores no campo'::text, '["No campo, o rebanho guardando,\nOs pastores, deitados no ch\u00e3o,\nMal se v\u00ea pela luz das estrelas,\nQue ao redor as ovelhas est\u00e3o.\nDe repente, um clar\u00e3o tudo envolve,\nQuando l\u00e1 das alturas dos c\u00e9us\nDesce um anjo, fulgente de gl\u00f3ria,\nCantando em louvor a Deus!\nNo primeiro Natal, eis que um anjo\nGloriosa mensagem nos traz:", "\u201cGl\u00f3ria a Deus nas altu-ras,\nNa terra acordo e paz!\u201d", "\u201cBoas-novas alegres vos trago;\nEm Bel\u00e9m Jesus Cristo nasceu!\u201d\nLogo ap\u00f3s, lindo coro de anjos\nAparece cantando no c\u00e9u.\nOh, jamais t\u00e3o bendita mensagem\nAos ouvidos de todos soou,\nNem t\u00e3o doce e divina harmonia\nO pr\u00f3prio c\u00e9u enlevou.\nOh, bem-vindo o Evangelho a este mundo\nQue em pecado e mis\u00e9ria ainda jaz.", "\u00c0 cidade v\u00e3o logo os pastores,\nPara verem de perto Jesus!\nNo pres\u00e9pio, o Menino contemplam,\nQue, do mundo, \u00e9 a l\u00eddima Luz.\nAs estrelas parecem unir-se\nAo celeste conjunto a cantar:\n\u201cEm Bel\u00e9m, de Davi a cidade,\nNasceu quem vos vem salvar!\u201d\nComo cantam! E cremos que nunca\nDesse canto ouviremos assaz!"]'::json, NULL)
        ,
        ('234'::text, ' Um pequeno a repousar'::text, '["Quem \u00e9 o pequeno a repousar\nNos bra\u00e7os de Maria,\nA quem os anjos v\u00eam cantar\nOs hinos de alegria?", "\u00c9 este Jesus o Rei que anuncia a paz\nA quem Deus quer bem.\nDa virgem eleita \u00e9 o filho, Jesus,\nQue nasceu em Bel\u00e9m!", "Por que t\u00e3o pobre fora, ent\u00e3o\nA sua estrebaria?\n\u00c9 que ao pobre pecador\nO verbo a paz traria!", "Trazei a ele o cora\u00e7\u00e3o,\nProstrai-vos reverentes,\nAos p\u00e9s do Rei, que a salva\u00e7\u00e3o\nNos trouxe eternamente!"]'::json, NULL)
        ,
        ('235'::text, ' O anjo da paz'::text, '["Um anjo formoso desceu das alturas,\nNa fresca do\u00e7ura da noite, em Bel\u00e9m.\nE a luz fulgurante brilhou nas colinas,\nEncheu as campinas e vales tamb\u00e9m.", "A doce mensagem que o anjo trazia,\nDe grande alegria, de amor divinal,\nEncheu de esperan\u00e7as a vida do crente,\nFeliz toda gente naquele Natal!", "Se h\u00e1 sombras de medo e o mundo se empenha\nEm luta ferrenha de ao bem se opor,\nMal surge dezembro, e eis outro clima,\nPois vem l\u00e1 de cima bafejos de amor.", "\u00c9 o anjo formoso que vem, novamente,\nLan\u00e7ar a semente do amor divinal,\nTrazendo a este mundo can\u00e7\u00f5es de alegria,\nNa santa harmonia que inspira o Natal."]'::json, NULL)
        ,
        ('236'::text, ' Em linda noite'::text, '["Em linda noite o mundo, a n\u00f3s\nEm coro angelical,\nCom harpas de ouro, o c\u00e9u quis dar\nUm canto divinal.\nE a toda a terra, ent\u00e3o, se fez\nPromessas imortais\nDe gl\u00f3ria a Deus, aos homens paz,\nCom b\u00ean\u00e7\u00e3os eternais.", "Pairando sobre a terra est\u00e3o\nOs anjos a cantar,\nE sobre o mundo pecador\nDerramam luz sem par.\nAcima dos tormentos mil,\nAng\u00fastias do viver,\nProclamam vida perenal,\nPois b\u00ean\u00e7\u00e3os v\u00eam trazer.", "E v\u00e3o-se os dias sem cessar,\nSegundo a voz de Deus,\nMas sobre a terra as gera\u00e7\u00f5es\nCaminham rumos seus!\nE quando, enfim, reinar a paz\nDe Cristo, o Salvador,\nOs salvos todos cantar\u00e3o\nEm coro o seu louvor!"]'::json, NULL)
        ,
        ('237-A'::text, ' Linda estrela'::text, '["Oh, linda estrela! Fulgor cintilante!\nRumo nas trevas teu brilho nos d\u00e1;\nVem conduzir-nos, \u00f3 lume divino,\nPara onde o Infante nascido j\u00e1 est\u00e1.", "Gotas de orvalho no ber\u00e7o rebrilham;\nNa manjedoura, sozinho, ele jaz.\nAnjos o adoram, real maravilha!\nDeus sempiterno do reino de paz.", "Que lhe traremos, que pias ofertas?\nRaros odores? Presentes de luz?\nP\u00e9rolas puras de mares long\u00ednquos ?\nMirra da Ar\u00e1bia? Tesouros de Ormuz?", "S\u00e3o todas v\u00e3s as ofertas mundanas,\nOuro n\u00e3o pode comprar seu favor!\nCristo, por\u00e9m, de n\u00f3s todos aceita\nAs ofertas sinceras de amor!"]'::json, NULL)
        ,
        ('237'::text, ' Jesus nasceu'::text, '["Vinde cantai Jesus nasceu!\n\u00c0 terra a luz desceu!\nA gra\u00e7a infinda ao mundo vem\nNa gruta de Bel\u00e9m, na gruta de Bel\u00e9m,\nJesus, o Amado, o Sumo Bem!", "Sim proclamai em derredor,\nQue foi por grande amor\nQue \u00e0 terra veio o Sumo Bem,\nNa gruta de Bel\u00e9m, na gruta de Bel\u00e9m,\nJesus, humilde, ao mundo vem."]'::json, NULL)
        ,
        ('238'::text, ' Novas de amor'::text, '["Harmonias pelo ar,\nUma estrela no c\u00e9u,\nUma prece de m\u00e3e,\nUm infante a chorar,\nS\u00e3o come\u00e7os da hist\u00f3ria\nQue muito nos diz:\nRude ber\u00e7o em Bel\u00e9m,\nMas de gl\u00f3ria sem par.", "Toda gente j\u00e1 ouviu\nDessa nova de amor\nPois o brilho da estrela\n\u00c9 de luz sem igual...\nH\u00e1 louvores de gozo,\nH\u00e1 promessas de paz.\nPois no ber\u00e7o, em Bel\u00e9m,\nDorme um Rei divinal.", "Nos fulgores da estrela\nH\u00e1 mensagens dos c\u00e9us\nQue nos homens despertam\nImenso prazer\nCada lar se bendiz\nE proclama esse bem,\nAnunciando \u00e0s na\u00e7\u00f5es\nO divino querer!", "Hoje o mundo j\u00e1 pode\nEsse dom repetir,\nCom mil gra\u00e7as a Deus\nPelo santo Natal.\nPois no ber\u00e7o, em Bel\u00e9m,\nManjedoura de luz,\nEis o Deus Salvador,\nEsse humano imortal."]'::json, NULL)
        ,
        ('239'::text, ' Nasce Jesus'::text, '["Nasce Jesus! Fonte de luz\nDescem os anjos cantando.\nNasce Jesus! \u00c9 nossa luz\nQue as trevas vem dissipando!\nNasce Jesus! Nasce Jesus!\nEis a mensagem celeste!\nRaia a luz da Salva\u00e7\u00e3o, triunfante vem!\nSalve, \u00f3 Cristo! Firma teu justo imp\u00e9rio!\nGratos louvores homens e anjos d\u00eaem!", "Nasce Jesus! Nasce Jesus!\nGl\u00f3ria a Deus nas alturas!\nPaz na terra, gra\u00e7a e amor\nA quantos Deus quer bem!", "Deus nos amou! Deus nos mandou\nCristo, seu Filho amado!\nDeus nos amou! Deus se encarnou!\nVede o menino deitado!\nDeus nos amou! Deus nos amou!\nDigam-no todos os povos!\nGozam paz e salva\u00e7\u00e3o todos os que cr\u00eaem.\nReino bendito! Reino de amor divino!\nGratos louvores homens e anjos d\u00eaem!"]'::json, NULL)
        ,
        ('24'::text, ' Convite ao louvor'::text, '["Vinde irm\u00e3os louvar a Deus,\nCriador da terra e c\u00e9us", "Exaltemos o Senhor!\nInfinito \u00e9 seu amor!", "Gl\u00f3ria em honra ao grande Rei;\nAlta santa \u00e9 sua lei.", "Age com poder real,\nCom grandeza divinal.", "Dia e noite a sua m\u00e3o\nDesenvolve o tenro gr\u00e3o.", "Com os dons do seu poder\nNossas vidas faz crescer."]'::json, NULL)
        ,
        ('240'::text, ' Louvor angelical'::text, '["Eis dos anjos a harmonia!\nCantam gl\u00f3ria ao Rei Jesus.\nPaz aos homens que alegria!\nPaz com Deus em plena luz.\nOu\u00e7am povos, exultantes,\nErgam Salmos triunfantes,\nAclamando seu Senhor.\nNasce Cristo, o Redentor!", "Exultai, \u00f3 terra e c\u00e9us,\nDando gl\u00f3ria ao Homem-Deus!", "Cristo eternamente honrado,\nDo seu trono se ausentou!\nE entre os homens, encarnado,\nDeus conosco se mostrou.\nQu\u00e3o bondosa Divindade!\nQu\u00e3o gloriosa Humanidade!\nSalve Cristo, Emanuel!\nLuz do mundo, Deus fiel!", "Cante o povo resgatado:\nGl\u00f3ria ao Pr\u00edncipe da Paz!\nDeus em Cristo, revelado,\nVida e luz ao homem traz!\nNasce a fim de renascermos!\nVive para revivermos!\nRei, profeta, intercessor,\nLouvem todos ao Senhor!"]'::json, NULL)
        ,
        ('241'::text, ' O nascimento de Jesus'::text, '["Mal sup\u00f5e aquela gente\nQue em Bel\u00e9m quer ir parar,\nQue uma luz t\u00e3o refulgente\nVai ali brilhar.\n\u00c9 por anjos anunciado,\nE os pastores logo v\u00eaem\nQue o Senhor, por Deus mandado,\nNasce em Bel\u00e9m.", "Vinde ouvir a doce hist\u00f3ria,\nQue dos altos c\u00e9us nos vem!\nO Messias, Rei da gl\u00f3ria,\nNasce em Bel\u00e9m.", "Mundo triste, vem! Desperta!\nTeus grilh\u00f5es defeitos s\u00e3o!\nTens a porta franca aberta;\nSai da vil pris\u00e3o!\nN\u00e3o hesites, duvidoso,\nEsse dom do c\u00e9u prov\u00e9m;\nCristo, o Todo-Poderoso,\nNasce em Bel\u00e9m.", "Proclamai a todo mundo,\nToda ra\u00e7a, toda cor:\nCristo, em seu amor profundo,\nSalva o pecador!\nConfian\u00e7a nele tende,\nN\u00e3o desprezar\u00e1 ningu\u00e9m!\nVinde, os bra\u00e7os vos estende!\nNasce em Bel\u00e9m."]'::json, NULL)
        ,
        ('242'::text, ' Os anjos e o natal'::text, '["Anjos das mans\u00f5es da gl\u00f3ria,\nSobre a terra j\u00e1 voai.\nConhecei do mundo a hist\u00f3ria,\nO Natal hoje anunciai:\nAdoremos, adoremos,\nCristo \u00e9 o nosso grande Rei!", "O rebanho aqui dormita,\nV\u00f3s, pastores, acordai.\nEntre o homens Deus habita,\nSua estrela contemplai!\nAdoremos, adoremos,\nCristo \u00e9 o nosso grande Rei!", "As vis\u00f5es deixai de lado,\nVinde, \u00f3 magos, e achareis\nDas na\u00e7\u00f5es o desejado,\nSua estrela conheceis!\nAdoremos, adoremos,\nCristo \u00e9 o nosso grande Rei!", "V\u00f3s, seus santos que, prostrados,\nEsper\u00e1veis sem cansar,\nRecebei o Cristo Amado,\nQue conosco vem morar.\nAdoremos, adoremos,\nCristo \u00e9 o nosso grande Rei."]'::json, NULL)
        ,
        ('243'::text, ' Noite de paz'::text, '["Noite de paz! Noite de amor!\nTudo dorme em derredor.\nEntre os astros que espargem a luz,\nProclamando o menino Jesus,\nBrilha a estrela da paz!", "Noite de paz! Noite de amor!\nNas campinas, ao pastor,\nLindos anjos mandados por Deus,\nAnunciam as novas dos c\u00e9us:\nNasce o bom Salvador!", "Noite de paz! Noite de amor!\nOh, que belo resplendor\nIlumina o menino Jesus!\nNo pres\u00e9pio do mundo, eis a luz,\nSol de eterno fulgor!"]'::json, NULL)
        ,
        ('244'::text, ' Mensagem aos pastores'::text, '["Em noite linda e silente,\nNos verdes prados do Oriente,\nNos verdes prados do Oriente,\nRebanhos a descansar!\nEis que ouviram pastores\nUm canto de mil louvores, (bis)\nA Deus os anjos entoar.", "Que canto ang\u00e9lico \u00e9 este?\nQue mensagem de ti vem?\n\u00d3 Pai, que nova nos deste\nPara teu povo em Bel\u00e9m?", "Ent\u00e3o \u00e0queles pastores,\nDa grande estrela aos fulgores,\nDa grande estrela aos fulgores\nRessoa a nova do c\u00e9u!\nJesus, de celeste origem,\nDa mais crente e humilde virgem, (bis)\nA vos salvar, j\u00e1 nasceu."]'::json, NULL)
        ,
        ('245'::text, ' Homens s�bios e de bem'::text, '["V\u00f3s homens s\u00e1bios e de bem,\nE v\u00f3s que estais em dor,\nLembrai-vos todos, afinal:\nNasceu o Salvador,\nTrazendo a todos salva\u00e7\u00e3o, a todos paz e amor!", "Oh, louvai o bom Deus com fervor,\nCom fervor!\nOh, louvai o bom Deus com fervor!", "De Deus o nosso Pai,\nDo c\u00e9u um anjo apareceu,\nE boas novas de louvor\nAos bons pastores deu:\nQue l\u00e1 na vila de Bel\u00e9m o Cristo lhes nasceu.", "Oh, n\u00e3o temais,\nPois vim trazer mensagem singular:\nNasceu o vosso Redentor,\nQue aos homens quer salvar!\nLibertar\u00e1 de todo o mal a quem n\u00e3o duvidar.", "Ao nosso Deus e Salvador\nLouvores entoai,\nCom verdadeira gratid\u00e3o\nAs novas proclamai,\nE neste dia de Natal ao Salvador louvai"]'::json, NULL)
        ,
        ('246'::text, ' Jesus, o Messias'::text, '["Jesus, o Messias, desceu l\u00e1 do c\u00e9u,\nE foi no Natal quando ao mundo nasceu.\nAlegre e feliz eu me devo sentir,\nPois ele \u00e9 o bem Rei que nos veio remir.", "Jesus, o messias, n\u00e3o quis aqui ter\nUm ber\u00e7o macio e na vida prazer!\nMas na manjedoura nasceu meu Jesus\nQuando ele nos veio trazer vida e luz", "Jesus, o Messias, que veio salvar,\nA paz j\u00e1 nos deu e com Deu foi estar.\nE neste Natal, aben\u00e7oa, Senhor,\nAs vidas que agora te rendem louvor. Am\u00e9m."]'::json, NULL)
        ,
        ('247'::text, ' Estrela cintilante'::text, '["Uma estrela cintilante,\nDe ofuscante e rara luz,\nConduziu os s\u00e1bios magos\nJunto ao ber\u00e7o de Jesus", "Ofertaram-lhe presentes\nPreciosos, sem igual:\nOuro puro, incenso e mirra,\nDons de gl\u00f3ria terreal!", "N\u00f3s, tamb\u00e9m, agradecemos\nAo Senhor o seu amor.\nPor nos dar seu Filho amado,\nCristo o nosso Redentor"]'::json, NULL)
        ,
        ('248'::text, ' No humilde pres�pio'::text, '["No humilde pres\u00e9pio, sem ter nada seu,\nJesus, pobrezinho, sem teto nasceu!\nOs c\u00e9us estrelados, fulgentes de luz,\nAbrigam o meigo e divino Jesus.", "Desejo a teu lado viver, meu Senhor;\nAmar-te e servir-te, Jesus Salvador.\nAos teus pequeninos pedimos guardar\nAt\u00e9 que possamos contigo morar. Am\u00e9m."]'::json, NULL)
        ,
        ('249'::text, ' Na manjedoura'::text, '["Num ber\u00e7o de palha dormia Jesus,\nO meigo menino que ali veio \u00e0 luz.\nNum rude pres\u00e9pio, de noite, em Bel\u00e9m,\nEnquanto as estrelas brilhavam al\u00e9m.", "O gado inquieto, se p\u00f5e a mugir,\nMas ele n\u00e3o teme e desperta a sorrir!\nEu te amo, Jesus! Vem meu sono velar,\nAt\u00e9 quando o sol no horizonte raiar.", "Tu foste crian\u00e7a, nasceste em Bel\u00e9m!\nPor isso \u00e0s crian\u00e7as atendes tamb\u00e9m.\nDesejo ter sempre ao meu lado, Senhor,\nA tua presen\u00e7a t\u00e3o cheia de amor. Am\u00e9m."]'::json, NULL)
        ,
        ('25'::text, ' O Criador de tudo'::text, '["Deus, do infinito o Criador,\nQue deste aos astros seu fulgor,\nTuas obras te proclamam!\nAo explorarmos a amplid\u00e3o,\nObra sem par da tua m\u00e3o,\nExtasiados te louvamos!\nAleluia! Aleluia! Aleluia!", "Ergue-se o homem para o c\u00e9u,\nDeixa o planeta em que nasceu;\nOh, conquista sem limites!\nComo havemos de entender\nQue Deus quisesse aqui viver?\nDeus eterno, entre os homens!\nAleluia! Aleluia! Aleluia!", "Hoje h\u00e1 problemas entre n\u00f3s\nPor n\u00e3o ouvirmos tua voz.\nPai, perdoa as nossas faltas!\nQue nossas vidas em Jesus\nBusquem guarida, paz e luz,\nE teu nome celebremos;\nAleluia! Aleluia1 Aleluia!", "No grande plano divinal,\nDessa harmonia universal\nDeus com grande amor salvou-nos!\nEle nos deixa conhecer\nToda extens\u00e3o de seu poder\nRevelado no universo!\nAleluia! Aleluia! Aleluia!"]'::json, NULL)
        ,
        ('250'::text, ' A voz de Jesus'::text, '["Ouvi o Salvador dizer:\n\u201cVem descansar em mim\nE, confiante, receber\nConforto e paz sem fim.\u201d\n\u201cFui a Jesus e lhe entreguei\nMeu triste cora\u00e7\u00e3o!", "Abrigo e paz eu nele achei,\nAchei consola\u00e7\u00e3o!", "Ouvi o Salvador dizer:\n\u201cDa vida a Fonte eu sou;\n\u00c0s \u00e1guas vivas vem beber,\nDe gra\u00e7a eu sempre as dou.\u201d\nFui a Jesus e me prostrei\n\u00c0s \u00e1guas e bebi;\nJamais a sede sentirei,\nEstando sempre ali!", "Ouvi o Salvador dizer:\n\u201cDo mundo eu sou a luz!\nOh, vem a mim, pois qual farol\nTe guio desde a cruz.\u201d\nFui a Jesus e nele achei\nO sol que brilha em mim;\nE nessa luz eu andarei\nAt\u00e9 da vida ao fim!"]'::json, NULL)
        ,
        ('251'::text, ' O p�o da vida'::text, '["\u00d3 Cristo, P\u00e3o da vida,\nDescido l\u00e1 do c\u00e9u,\nO P\u00e3o de nossas almas\nQue o Pai de amor nos deu!\nEm ti nos alegramos,\nProvando, mesmo aqui,\nDo alento e da bondade\nQue achamos sempre em ti.", "Da eterna e santa vida,\nDa qual Tu \u00e9s o Autor,\nSustento e fortaleza\n\u00c9s Tu, tamb\u00e9m, Senhor!\nSem ti n\u00e3o nos assistem\nNem for\u00e7as nem poder;\nDe ti, nosso alimento,\nQueremos n\u00f3s viver.", "\u00d3 Cristo, P\u00e3o da vida,\nA ti louvamos n\u00f3s!\nE ao Pai tamb\u00e9m erguemos\nA nossa alegre voz.\nAgradecemos sempre\nO amor que aqui nos deu\nSustento verdadeiro,\nNo santo P\u00e3o do c\u00e9u."]'::json, NULL)
        ,
        ('252'::text, ' P�o celestial'::text, '["P\u00e3o da vida, p\u00e3o dos c\u00e9us,\nP\u00e3o celeste \u00e9 meu Jesus!\nP\u00e3o que d\u00e1 ao cora\u00e7\u00e3o\nAlegria, paz e luz.\nSangue que Jesus verteu\n\u00c9 divino e eficaz!\nEste sangue, ao cora\u00e7\u00e3o,\nFor\u00e7a, amor e vida traz.", "Vem, Jesus, me aben\u00e7oar!\nEnche-me de ti Senhor!\nPois almejo te servir\nE te honrar, meu Salvador.\nPois almejo te servir\nE te honrar, meu Salvador. Am\u00e9m."]'::json, NULL)
        ,
        ('253'::text, ' Cristo, a luz do mundo'::text, '["Luz do mundo, Jesus Cristo\nDissipaste as ilus\u00f5es!\nDesvendaste os nossos olhos,\nLibertando os cora\u00e7\u00f5es!\nPara ver-te, para ver-te,\nOuve nossas ora\u00e7\u00f5es!", "Neste mundo atribulado,\nOnde opera Satan\u00e1s,\nN\u00f3s pregamos o Evangelho,\nTua gra\u00e7a, amor e paz!\nLuz divina, luz divina,\nVence toda luz falaz.", "E onde as trevas do pecado\nObscurecem teu amor,\nFaze a luz do teu ensino\nDominar, \u00f3 Salvador!\nManifesta, manifesta,\nTua gl\u00f3ria, \u00f3 Redentor!", "Luz dos homens, Luz da vida,\nBrilha com poder nos teus!\nEsclarece os incautos,\nRevelando o grande Deus!\nLuz do mundo, Luz do mundo,\n\u00c9s o resplendor dos c\u00e9us. Am\u00e9m."]'::json, NULL)
        ,
        ('254'::text, ' Sossegai'::text, '["Mestre. o mar se revolta\nE as ondas nos d\u00e3o pavor!\nO c\u00e9u se reveste de trevas,\nN\u00e3o temos um Salvador!\nN\u00e3o se te d\u00e1 que morramos?\nPodes assim dormir?\nSe a cada momento nos vemos\nJ\u00e1 prestes a submergir?", "\u201cAs ondas atendem ao meu mandar, sossegai!\nSeja o encapelado mar,\nA ira dos,homens o g\u00eanio do mal;\nTais \u00e1guas n\u00e3o podem a nau tragar,\nQue leva o Senhor,Rei dos c\u00e9us e mar!\nPois todos ouvem o meu mandar:\nSossegai! Sossegai!\nConvosco estou para vos salvar; sossegai!\u201d", "Mestre,t\u00e3o grande tristeza\nMe quer hoje consumir!\nNa dor que perturba minha alma\nTe imploro: \u201cVem me acudir!\u201d\nDe ondas do mal que me encobrem,\nQuem me vir\u00e1 valer?\nN\u00e3o tardes, n\u00e3o tardes, bom Mestre,\nEstou quase a perecer!", "Mestre chegou a bonan\u00e7a;\nEm paz vejo o c\u00e9u e o mar!\nO meu cora\u00e7\u00e3o goza a calma\nQue n\u00e3o poder\u00e1 findar.\nFirme ao teu lado, \u00f3 Mestre,\nDono da terra e c\u00e9u,\nEu hei de chegar, bem seguro,\nAo porto, destino meu."]'::json, NULL)
        ,
        ('255'::text, ' A ovelha perdida'::text, '["Noventa e nove ovelhas v\u00e3o\nSeguras ao curral.\nMas uma se extraviou\nDo aprisco pastoral,\nVagando em montes de terror,\nDistante do fiel Pastor,\nDistante do fiel Pastor.", "\u201cNoventa e nove a ti, Pastor,\nN\u00e3o podem contentar?\u201d\n\u201cA errante \u00e9 minha\u201d, respondeu,\n\u201cZeloso a irei buscar!\nNos montes h\u00e1 perigos mil.\nDe novo a quero em meu redil,\nDe novo a quero em meu redil\u201d.", "Ningu\u00e9m jamais imaginou\nO quanto padeceu;\nBuscando a ovelha t\u00e3o rebel,\nTerr\u00edvel dor sofreu.\nFoi, pois, assim que o Bom Pastor\nSalvou a ovelha por amor,\nSalvou a ovelha por amor.", "Vem da montanha exulta\u00e7\u00e3o:\n\u00c9 a voz do Bom Pastor!\nRessoa em notas triunfais\nO Salmo Vencedor.\nE os anjos cantam l\u00e1 nos c\u00e9us:\n\u201cVoltou a ovelha para Deus,\nVoltou a ovelha para Deus.\u201d"]'::json, NULL)
        ,
        ('256'::text, ' Gl�ria singular'::text, '["Vis\u00e3o de gl\u00f3ria singular\nA Igreja p\u00f4de contemplar\nEm Cristo, quando ascendeu\nAo monte e ali resplandeceu.", "Com sua face a reluzir,\nQuis ele a gl\u00f3ria do porvir\nAos escolhidos demonstrar,\nQue em luz e gozo h\u00e3o de habitar.", "As almas crentes subir\u00e3o!\nMist\u00e9rio dado na vis\u00e3o!\nPor isso, alegres, com louvor,\nCantamos hinos ao Senhor.", "A Deus o Filho, a Deus o Pai\nE a Deus o Esp\u00edrito entoai!\nO Deus que a gra\u00e7a nos quis dar\nDe sua gl\u00f3ria contemplar. Am\u00e9m."]'::json, NULL)
        ,
        ('257'::text, ' Hosana ao grande Rei'::text, '["Hosana! Hosana! Hosana!\nHosana ao Filho de Davi!\nHosana ao grande Rei,\nAo Salvador, o Bom Pastor\nQue resgatou sua grei!\nDos altos c\u00e9us, de Deus o Pai,\nDo trono celestial\nDesceu Jesus, trazendo luz\nE vida eternal.\nHosana ao Filho de Davi!\nHosana nas alturas!", "Hosana! Hosana! Hosana!\nHosana ao Filho de Davi!\nHosana ao Redentor!\nCom gratid\u00e3o, meu cora\u00e7\u00e3o\nEntoa o seu louvor.\nMessias, Pr\u00edncipe da paz,\nInvicto General,\nMeu Deus, meu Rei, serei aqui\nUm servo teu, leal..\nHosana ao Filho de Davi,\nHosana nas alturas!"]'::json, NULL)
        ,
        ('258'::text, ' Majestade'::text, '["Caminha em majestade e vai!\nAo som de hosana e exulta\u00e7\u00e3o\nPor entre palmas, para a cruz,\nNa estrada segue o bom Jesus.", "Caminha em majestade e vai!\nEm gl\u00f3ria segue para a cruz,\nEle o pecado vencer\u00e1\nE a morte atroz abater\u00e1.", "Caminha em majestade e vai!\n\u00c9 vinda a luta pertinaz.\nNo c\u00e9u o Pai o acolher\u00e1!\nSeu Filho, Ungido, subir\u00e1!", "Caminha em majestade e vai!\nEm gl\u00f3ria segue para a cruz.\nSe abate a fronte, em face \u00e0 dor,\nMas vai triunfante o Vencedor"]'::json, NULL)
        ,
        ('259'::text, ' Hosana e gl�ria'::text, '["A ti, hosana e gl\u00f3ria, \u00f3 Cristo Salvador,\nCantavam as crian\u00e7as com vozes de louvor.\nO povo hebreu saudou-te com palmas e com flor,\nE n\u00f3s cantamos hinos a ti, Rei Vencedor.", "Tu \u00e9s o Rei eterno, Tu \u00e9s o Emanuel,\nQue vens no santo Nome de nosso Deus fiel.\nCom \u201cvivas\u201d te acolheram, \u00e0s portas da paix\u00e3o,\nE n\u00f3s cantamos hoje louvor e gratid\u00e3o.", "Assim como aceitaste do povo a sauda\u00e7\u00e3o,\nAceita o nosso canto de grata exalta\u00e7\u00e3o,\nA ti, hosana e gl\u00f3ria, \u00f3 Cristo Salvador,\nCantavam as crian\u00e7as com vozes de louvor."]'::json, NULL)
        ,
        ('26'::text, ' Ao Deus grandioso'::text, '["Senhor meu Deus, quando eu maravilhado\nOs grandes feitos vejo de tua m\u00e3o,\nEstrelas, mundos e trov\u00f5es rolando,\nA proclamar teu nome na amplid\u00e3o.", "Canta minh\u2019alma, ent\u00e3o, a ti Senhor:\nGrandioso \u00e9s Tu, grandioso \u00e9s Tu!\nCanta minh\u2019alma, ent\u00e3o, a ti Senhor:\nGrandioso \u00e9s Tu, grandioso \u00e9s Tu!", "Quando atravesso bosques e florestas,\nOuvindo, \u00e0 brisa, p\u00e1ssaros cantar,\nOu vejo, al\u00e9m, montanhas altaneiras\nO teu poder em, gl\u00f3ria proclamar,", "Quando percebo que na cruz maldita,\nPor teu amor Jesus morreu por mim,\nE me livrou do jugo do pecado,\nAli vertendo sangue carmesim,", "Quando afinal em resplendor e gl\u00f3ria,\nJesus abrir as portas da mans\u00e3o,\nEu hei de estar de joelhos entre os santos,\nNa mais humilde e vera adora\u00e7\u00e3o,", "E ent\u00e3o cantar eternamente ali:\nGrandioso \u00e9s Tu, grandioso \u00e9s Tu!\nE ent\u00e3o cantar eternamente ali:\nGrandioso \u00e9s Tu, grandioso \u00e9s Tu!"]'::json, NULL)
        ,
        ('260'::text, ' Amor que vence'::text, '["Amor, que por amor desceste!\nAmor, que por amor morreste!\nAh! Quanta dor n\u00e3o padeceste!\nMinha alma vieste resgatar\nE meu amor ganhar!", "Amor, que por amor seguias\nA mim, que sem amor Tu vias!\nOh! Quanto amor por mim sentias,\nEterno Deus, Senhor Jesus,\nSofrendo sobre a cruz!", "Amor, que tudo me perdoas,\nAmor, que at\u00e9 mesmo aben\u00e7oas\nUm r\u00e9u de quem Tu te afei\u00e7oas!\nVencido, \u00f3 Salvador, por ti,\nTeu grande amor senti!", "Amor sublime, que perduras;\nQue em tua gra\u00e7a me seguras,\nCercando-me de mil venturas!\nAceita agora, \u00f3 Salvador,\nO meu humilde amor. Am\u00e9m."]'::json, NULL)
        ,
        ('261'::text, ' O g�lgota'::text, '["Mui longe o monte verde est\u00e1,\nAo norte de Si\u00e3o.\nAli na cruz, o bom Jesus,\nNos deu a salva\u00e7\u00e3o.", "Oh! Quanto, quanto nos amou!\nAmemo-lo tamb\u00e9m!\nE confiando em seu amor,\nFa\u00e7amos todo bem.", "Quem neste mundo sondar\u00e1\nA dor que padeceu?\nMas crer podemos: \u201cFoi por n\u00f3s\nQue ali na cruz sofreu.\u201d", "Morreu por n\u00f3s na infame cruz,\nPerd\u00e3o nos concedeu.\nLavou os nossos cora\u00e7\u00f5es\nNo sangue que verteu.", "Ningu\u00e9m podia nos livrar\nDa pena eternal.\nS\u00f3 Cristo pode nos remir\nA pre\u00e7o divinal."]'::json, NULL)
        ,
        ('262'::text, ' Contempla��o da cruz'::text, '["Ao contemplar a tua cruz\nE o que sofreste ali, Senhor,\nSei que n\u00e3o h\u00e1, \u00f3 meu Jesus,\nUm bem maior que o teu amor.", "Quero somente me gloriar\nNa tua cruz, meu Salvador,\nPois que morreste em meu lugar!\nTeu, sempre teu serei, Senhor.", "De tua fronte, m\u00e3os e p\u00e9s,\nDe teu ferido cora\u00e7\u00e3o,\nTeu sangue em dores t\u00e3o cru\u00e9is\nDeste por minha reden\u00e7\u00e3o.", "Ao contemplar a tua cruz,\nO teu sofrer, o teu penar,\nQu\u00e3o leve sinto, \u00f3 meu Jesus,\nA que me deste a carregar.", "Tudo o que eu possa consagrar\nAo teu servi\u00e7o, ao teu louvor,\nEm nada poderei pagar\nAo que me d\u00e1s em teu amor. Am\u00e9m."]'::json, NULL)
        ,
        ('263'::text, ' Junto � cruz de Cristo'::text, '["Na cruz de Jesus Cristo,\nMeus olhos podem ver\nUm vulto agonizante,\nPor mim, ali morrer!\nEnt\u00e3o, estremecido,\nContemplo o grande amor!\nAmor incompar\u00e1vel,\nPor mim, vil pecador!", "Bem junto \u00e0 cruz de Cristo\nEu quero me abrigar;\n\u00c0 sombra do madeiro\nAlento possa achar.\nAragem no deserto,\nNa estrada um doce lar,\nJesus sempre ameniza\nMeu \u00e1rduo caminhar.", "Viver eu quero sempre\n\u00c0 sombra dessa cruz!\nNenhum fulgor anseio\nSen\u00e3o o de Jesus!\nO mundo abandonando,\nEu tudo quis perder,\nPorquanto achei a Cristo\nQue transformou meu ser.!"]'::json, NULL)
        ,
        ('264'::text, ' Fronte ensang�entada'::text, '["Oh, fronte ensang\u00fcentada\nEm tanto opr\u00f3brio e dor,\nDe espinhos coroada,\nCom \u00f3dio e com furor!\nT\u00e3o gloriosa outrora,\nT\u00e3o bela e t\u00e3o viril,\nT\u00e3o abatida, agora,\nDe afronta e esc\u00e1rnio vil.", "Qu\u00e3o humilhada pende\nA face do Senhor!\nN\u00e3o vive, n\u00e3o resplende,\nJ\u00e1 n\u00e3o tem luz nem cor!\nOh, crime inomin\u00e1vel,\nFazer anuviar\nO brilho inigual\u00e1vel\nDe um t\u00e3o piedoso olhar!", "Est\u00e1s t\u00e3o carregado,\nMas todo fardo \u00e9 meu!\nEu s\u00f3 me fiz culpado,\nE o sofrimento \u00e9 teu.\nVenho aos teus p\u00e9s, tremente,\nMere\u00e7o a puni\u00e7\u00e3o,\nMas olhas-me clemente\nCom santa compaix\u00e3o!", "S\u00ea meu ref\u00fagio forte,\nMeu guia e vida e luz!\nQue eu sinta, vendo a morte,\nConforto em tua cruz!\nNa cruz com f\u00e9 me abrigo\nE amparo Tu me d\u00e1s!\nE unido assim contigo,\nHei de dormir em paz!"]'::json, NULL)
        ,
        ('265'::text, ' Cristo redentor'::text, '["Que agonia dolorosa\nTu sofreste, \u00f3 meu Jesus!\nFoi por mim tanta amargura\nQue penaste a-li na cruz.\nQuanto amor! Oh, quanto amor\nNo teu sangue, \u00f3 meu Senhor!", "Despreza-do at\u00e9 por Deus\nPelos crimes que eram meus,\nFoi por mim, \u00f3 meu Jesus,\nTodo horror da infame cruz.\nQuanto amor! Oh, quanto amor\nNa tua morte, \u00f3 meu Senhor!", "\u00d3 Deus meu, Justi\u00e7a e amor\nS\u00e3o a ess\u00eancia do teu Ser.\nFoi por is-so que teu Filho\nVeio \u00e0 cruz por mim morrer!\nQue justi\u00e7a e quanto amor\nNo teu ser, \u00f3 Criador!", "Pecadores, todos v\u00f3s,\nQue buscais a salva\u00e7\u00e3o,\nVislumbrai no sacrif\u00edcio\nQue nos trouxe a reden\u00e7\u00e3o!\nCom amor, sim, muito amor,\nCrede em Cristo, o Redentor."]'::json, NULL)
        ,
        ('266'::text, ' Rude cruz'::text, '["Rude cruz se erigiu,\nDela o dia fugiu\nComo emblema de vergonha e dor.\nMas eu sei que na cruz,\nNesse dia Jesus\nDeu a vida por mim, pecador.", "Sim, eu amo a mensagem da cruz;\nSeu triunfo meu gozo ser\u00e1!\nPois um dia em lugar de uma cruz,\nA coroa Jesus me dar\u00e1!", "Desde a gl\u00f3ria dos c\u00e9us,\nO Cordeiro de Deus\nAo Calv\u00e1rio humilhante baixou.\nNessa cruz, para mim\nH\u00e1 mist\u00e9rio sem fim,\nPorque nela Jesus me salvou.", "Nessa cruz padeceu,\nDesprezado morreu\nMeu Jesus, para dar-me perd\u00e3o.\nEu me alegro na cruz\nDela vem gra\u00e7a e luz,\nPara minha santifica\u00e7\u00e3o."]'::json, NULL)
        ,
        ('267'::text, ' Precioso sangue'::text, '["Oh, que precioso sangue\nO Senhor verteu,\nQuando, para resgatar-nos,\nPadeceu!", "Oh, que precioso sangue,\nSangue de Jesus,\nQue por n\u00f3s foi derramado\nSobre a cruz!", "Oh, que precioso sangue!\nNele temos paz.\nTudo quanto a lei exige,\nSatisfaz!", "Oh, que precioso sangue,\nSangue remidor!\nNele Cristo manifesta\nSeu amor!"]'::json, NULL)
        ,
        ('268'::text, ' Reden��o'::text, '["Pendurado no madeiro,\n\u00d3 Jesus, pudeste, assim,\nDestruir meu cativeiro\nE provar-me amor sem fim.", "O teu sangue foi vertido,\nExpiraste, \u00f3 meu Jesus!\nE ficou por ti cumprido\nMeu resgate sobre a cruz.", "Neste sangue que verteste,\nPurifica-me Senhor!\nFoi por mim que Tu morreste:\nS\u00ea prop\u00edcio ao pecador!", "S\u00ea prop\u00edcio aos condenados\nA lutar na escurid\u00e3o\nDeste abismo de pecados,\nSob a dor da maldi\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('269'::text, ' Pureza no sangue de Cristo'::text, '["Seja bendito o Cordeiro\nQue na cruz por n\u00f3s padeceu;\nSeja bendito o seu sangue\nQue por n\u00f3s, pecadores, verteu.\nEis que no sangue lavados,\nE tendo puro o cora\u00e7\u00e3o,\nOs pecadores remidos\nPor Jesus t\u00eam com Deus comunh\u00e3o.", "Alvo mais que a neve,\nAlvo mais que a neve!\nSim, nesse sangue lavado,\nMais alvo que a neve eu estou", "Qu\u00e3o espinhosa a coroa\nQue Jesus por n\u00f3s suportou!\nOh! Qu\u00e3o profundas as chagas\nQue nos provam o quanto ele amou!\nEis nessas chagas pureza\nPara o maior pecador,\nA quem mais alvo que a neve\nO teu sangue transforma, Senhor.", "Se as faltas n\u00f3s confessarmos\nE seguirmos na tua luz,\nTu n\u00e3o somente perdoas;\nPurificas tamb\u00e9m, \u00f3 Jesus!\nLavas de todo pecado,\nQue maravilha de amor!\nPois que mais alvo que a neve\nO teu sangue nos torna, Senhor."]'::json, NULL)
        ,
        ('27'::text, ' Um hino ao Senhor'::text, '["As grutas, as rochas imensas,\nDos mundos o grande esplendor,\nProclamam bem alto, constantes\nUm hino ao teu nome, Senhor!", "Nos c\u00e9us e no mar e na terra,\nNos bosques, nos prados em flor,\nNo fragoso alcantil, na amplitude celeste,\nUm hino ressoa ao Senhor!", "Nos c\u00e9us, as estrelas brilhantes,\nDos mares o grande fragor,\nE as brisas entoam, ridentes,\nUm hino ao teu nome, Senhor!", "As aves alegres, na mata,\nPor entre as ramagens em flor,\nExultam em coro, cantando\nUm hino ao teu nome, Senhor!", "E tu, pecador que vagueias,\nQue fazes ao teu Criador?\nN\u00e3o achas momento em que cantes\nUm hino de gl\u00f3ria ao Senhor!"]'::json, NULL)
        ,
        ('270'::text, ' Desafio'::text, '["Morri na cruz por ti,\nFoi para te livrar.\nMeu sangue ali verti\nE posso te salvar.", "Morri, morri na cruz por ti,\nQue fazes tu por mim?", "Vivi assim por ti,\nSofrendo a intensa dor!\nE tudo fiz aqui\nPor ser teu Salvador.", "Sofri na cruz por ti\nA fim de te salvar.\nA vida consegui\nE agora ta vou dar.", "Eu trouxe a salva\u00e7\u00e3o,\nDos altos c\u00e9us favor.\n\u00c9 livre o meu perd\u00e3o,\n\u00c9 grande o meu amor."]'::json, NULL)
        ,
        ('271'::text, ' Ressurrei��o'::text, '["Aleluia! Aleluia! Aleluia! Aleluia!", "\u00d3 filhos, vinde aqui cantar\nAo Rei do c\u00e9u, da terra e mar\nQue, ressurreto, vem reinar.\nAleluia! Aleluia!", "\u00c0 tumba foram de manh\u00e3\nMulheres com piedoso af\u00e3!\nMas Cristo vive - a busca \u00e9 v\u00e3!\nAleluia! Aleluia!", "Um anjo, ent\u00e3o, lhes proclamou\nQue Cristo j\u00e1 ressuscitou!\nAs suas m\u00e1goas confortou.\nAleluia! Aleluia!", "Aceita agora, \u00f3 Salvador,\nO nosso canto de louvor,\nQue a ti erguemos com amor!\nAleluia! Aleluia! Am\u00e9m."]'::json, NULL)
        ,
        ('272'::text, ' Aleluia ao Cristo redivivo'::text, '["Cristo j\u00e1 ressuscitou! Aleluia!\nSobre a morte triunfou! Aleluia!\nTudo consumado est\u00e1! Aleluia!\nSalva\u00e7\u00e3o de gra\u00e7a d\u00e1! Aleluia!", "Cristo sobre a cruz sofreu! Aleluia!\nE por n\u00f3s ali morreu! Aleluia!\nMas agora vivo est\u00e1! Aleluia!\nPara sempre reinar\u00e1! Aleluia!", "Gratos hinos hoje erguei! Aleluia!\nA Jesus, o grande Rei! Aleluia!\nRessurgiu, \u00e9 vencedor! Aleluia!\nToda gl\u00f3ria ao Redentor! Aleluia!"]'::json, NULL)
        ,
        ('273'::text, ' Mem�rias da ressurrei��o'::text, '["Hoje nos lembramos da Ressurrei\u00e7\u00e3o\nQue assegura ao crente plena reden\u00e7\u00e3o.\nAo terceiro dia, qual nos prometeu,\nOs grilh\u00f5es da morte o Salvador rompeu.\nSejas Tu louvado, \u00f3 Redentor Jesus!", "Vencedor da morte, o Salvador Jesus\nTransformou as trevas em gloriosa luz.\nE as prim\u00edcias fez-se, na Ressurrei\u00e7\u00e3o,\nDos fi\u00e9is que um dia ressuscitar\u00e3o.\nSejas Tu louvado, \u00f3 grande Vencedor!", "Teu amor foi tanto, pelo pecador,\nQue por n\u00f3s sofreste a puni\u00e7\u00e3o e a dor.\nMas, ressuscitado, junto ao Pai est\u00e1s,\nGarantia eterna de perd\u00e3o e paz.\nSejas Tu louvado, \u00f3 forte Mediador.", "Sumo Sacerdote! Santo Intercessor!\nHoje te aclamamos Rei e Salvador!\nPara sempre vives, na celeste luz,\nHomem-Deus ilustre, \u00f3 Redentor Jesus!\nSejas Tu louvado eternamente, am\u00e9m.!"]'::json, NULL)
        ,
        ('274'::text, ' Morto e ressurreto'::text, '["Eis morto o Salvador,\nNa sepultura\nMas com poder real\nRessuscitou.", "Da sepultura saiu!\nCom triunfo e gl\u00f3ria ressurgiu!\nRessurgiu, vencendo a morte\nE seu poder!\nPode agora a todos vida conceder!\nRessurgiu! Ressurgiu!\nAleluia! Ressurgiu!", "Tomaram precau\u00e7\u00e3o\nCom seu sepulcro.\nFoi tudo em v\u00e3o, por\u00e9m,\nPara o reter.", "No grande Vencedor\nEis as prim\u00edcias\nDos santos que tamb\u00e9m\nRessurgir\u00e3o."]'::json, NULL)
        ,
        ('275'::text, ' Cristo venceu'::text, '["Cristo venceu!\nLiberta est\u00e1 minha alma!\nGozo a divina calma de sua paz,\nBem longe dos grilh\u00f5es da eterna morte,\nBem junto \u00e0 fonte que me satisfaz.", "Cristo venceu!\nMeu cora\u00e7\u00e3o se abre\nAo resplendor da soberana luz.\nDe clarida-de inunda minha vida,\nDe formosura veste minha cruz.", "Cristo venceu!\nA noite se fez dia:\nNova esperan\u00e7a nele eu encontrei!\nDas armas da justi\u00e7a revestido,\nPelejo agora na divina grei.", "Cristo venceu!\nTemores n\u00e3o mais sinto.\nVencida a morte, abriu-se o denso v\u00e9u!\nCristo venceu! O Filho poderoso\nReina! E com ele viverei no c\u00e9u!"]'::json, NULL)
        ,
        ('276'::text, ' Amor�vel convite'::text, '["Vinde v\u00f3s, fi\u00e9is, cantai\nAmoravelmente:\nDeus seu povo libertou\nTriunfantemente!\nExaltai Jerusal\u00e9m\nCom amor profundo,\nProclamai que ressurgiu\nO Senhor do mundo.", "T\u00eam as almas em Jesus\nReden\u00e7\u00e3o, guarida.\nRessurgiu o Redentor,\nEis a luz e a vida\nFoge o inverno, a dor cruel,\nDo pecado a treva;\nRedimidos por Jesus,\nEle ao c\u00e9u nos leva!", "Aleluia cantareis\nAo Senhor amado,\nSalvador, Deus imortal,\nJ\u00e1 ressuscitado!\nAleluia! Gl\u00f3ria dai,\nCom fervor supino!\nAleluia sem cessar,\nAo Deus Uno e trino."]'::json, NULL)
        ,
        ('277'::text, ' Cristo vive! Ressurgiu!'::text, '["Cristo vive! Ressuscitou de entre os mortos,\nCristo vive! Ressuscitou de entre os mortos,\nE foi feito, e foi feito as prim\u00edcias dos que dormem.\nGl\u00f3ria e louvor sejam dados com poder\nAo Pai eterno, que no trono assentado est\u00e1,\nE ao Cordeiro de Deus! Cristo vive! Ressurgiu!\nE foi feito as prim\u00edcias dos que dormem!\nGl\u00f3ria e louvor sejam dados com poder\nAo Pai eterno, que no trono assentado est\u00e1,\nE ao Cordeiro de Deus, por s\u00e9culos e s\u00e9culos,,\nAm\u00e9m."]'::json, NULL)
        ,
        ('278'::text, ' A vit�ria de Jesus'::text, '["Aleluia! Aleluia! Aleluia!", "Findou-se a luta de Jesus!\nO Salvador venceu na cruz!\nJ\u00e1 neste mundo raia a luz!\nAleluia!", "Das m\u00e3os do grande usurpador\nLivrou-nos com profundo amor.\nCantai ao forte Salvador\nAleluia!", "Vencida a morte e seu horror\nSubiu \u00e0 gl\u00f3ria o Redentor\nRompei em cantos de louvor!\nAleluia!", "Com majestade sem igual\nFirmou o imp\u00e9rio divinal\nErguei o salmo triunfal\nAleluia!"]'::json, NULL)
        ,
        ('279'::text, ' O dia triunfal raiou'::text, '["O dia triunfal raiou,\nTrazendo o sol de alegre luz;\nPois ressurgiu e nos livrou\nDa morte o nosso Rei Jesus. Aleluia!", "Rejubilando sem cessar,\nCom aleluias de louvor\nEm coro vamos proclamar:\n\u201cRessuscitou o Salvador!\u201d Aleluia!", "Com alegria e com louvor\nA P\u00e1scoa vamos celebrar,\nPois Cristo, o grande Vencedor,\nDe Deus o amor nos vem mostrar! Aleluia!"]'::json, NULL)
        ,
        ('28'::text, ' Coroa��o'::text, '["Tuas obras te coroam\nComo um halo de esplendor!\nAstros, anjos, c\u00e9us entoam\nHino eterno a ti Senhor!\nCampos, matas, vales, montes,\nVerde outeiro e verde mar,\nAves e sonoras fontes\nFormam coro singular!", "N\u00f3s, mortais, por ti remidos,\nDeus de gl\u00f3ria, Deus de amor,\nCora\u00e7\u00f5es aos c\u00e9us erguidos,\nCelebramos teu louvor!\nRevelaste amor profundo,\nInsond\u00e1vel, sem igual,\nEnviando Cristo ao mundo\nA vencer por n\u00f3s o mal!", "Fonte \u00e9s de alegria e vida,\n\u00c9s do bem o Inspirador;\nTua gra\u00e7a nos convida\nA viver em m\u00fatuo amor\nQuais alegres peregrinos,\nSempre em marcha triunfal,\nCantaremos gratos hinos\nNa jornada at\u00e9 o final!"]'::json, NULL)
        ,
        ('280'::text, ' Ascens�o'::text, '["Subiste ao c\u00e9u, Triunfador!\nDe tudo agora \u00e9s Tu Senhor;\nVenceste, Rei da gl\u00f3ria!\nEis que te sentas junto ao Pai.\nColher, em breve, a Igreja vai\nAs b\u00ean\u00e7\u00e3os da vit\u00f3ria!\nA cruz \u00e9 luz.\nO pecado? Expiado!\nVida e morte,\nTudo est\u00e1 em m\u00e3o que \u00e9 forte!", "Atrai-nos bem a ti, Jesus,\nQue andemos sempre em tua luz\nE o Reino teu busquemos.\nD\u00e1 que vivamos com temor\nE humildes pelo teu amor\nO mal abandonemos!\nD\u00e1, Rei, \u00e0 grei\nQue, lutando sob teu mando,\nSeja firme;\nE sua f\u00e9 ao mundo afirme.", "Clamamos, \u00f3 Jesus, por ti.\nVem ser com o teu povo aqui.\nFaze que viva unido\nE seja grato e liberal,\nBuscando o bem celestial,\nO bem imerecido.\nOh, sim! E assim,\nN\u00f3s, constantes e exultantes,\nJuntaremos\nNossas m\u00e3os: louvar-te-emos! Am\u00e9m."]'::json, NULL)
        ,
        ('281-A'::text, ' Fronte ensang�entada'::text, '["A fronte que uma vez sangrou\nAgora brilha em luz!\nReal \u00e9 o halo que adornou\nO vencedor Jesus!", "Ele \u00e0s alturas ascendeu,\n\u00c0 gl\u00f3ria se elevou!\nE \u00e9 a eterna luz do c\u00e9u,\nO Rei que se humilhou!", "Ele \u00e9 dos anjos o prazer,\nDos salvos, Redentor.\nA estes faz-se conhecer\nNos bens do seu amor.", "A n\u00f3s o esc\u00e1rnio dessa cruz\nCompleta gra\u00e7a traz!\nUm nome eterno d\u00e1 Jesus\nDe gl\u00f3ria, gozo e paz!", "Os que carregam sua cruz,\nCom Cristo h\u00e3o de reinar.\nVer\u00e3o de perto a sua luz\nNo sempiterno lar."]'::json, NULL)
        ,
        ('281'::text, ' Jesus triunfante'::text, '["Aleluia! Triunfante,\nPara os c\u00e9us Jesus subiu.\nAs pris\u00f5es quebrou da morte,\nPecadores redimiu.\nCom poder em majestade,\nVive e reina l\u00e1 no c\u00e9u;\nMas um dia triunfante,\nVoltar\u00e1 ao povo seu!", "Aleluia! O Cristo amado\nSobre a morte triunfou!\nE por n\u00f3s, quebrando os la\u00e7os,\nA vit\u00f3ria conquistou!\nSeu triunfo \u00e9 nossa gl\u00f3ria,\nSeu sofrer \u00e9 nossa paz.\nSalva\u00e7\u00e3o o Cristo amado\nCom poder e gra\u00e7a traz.", "Aleluia! Ressurreto,\nFiador por n\u00f3s se fez!\nConseguiu por sua morte,\nRedimir-nos de uma vez.\nEi-lo agora, junto ao trono,\nPelos seus a interceder.\nAleluia! Ressurreto,\nCristo reina com poder!"]'::json, NULL)
        ,
        ('282'::text, ' A grande comiss�o'::text, '["Disse Jesus: \u201cIde por todo o mundo,\nE pregai o eterno dom\nDa salva\u00e7\u00e3o que, com amor profundo,\nD\u00e1 o Deus gracioso e bom!\u201d\nTendo na cruz a afirma\u00e7\u00e3o do amor,\nProclamai o dom do Redentor!\nOh! Conquistai\nAlmas perdidas, buscai\nO pecador enfermo, quase moribundo!", "Vamos, irm\u00e3os, levar\nEssa luz ao mundo inteiro!\nVamos, irm\u00e3os, contar\nQue esse dom \u00e9 verdadeiro!\nVamos, irm\u00e3os, pregar\nMui confiados no Cordeiro\nQue na cruz, j\u00e1 fez\nA nossa Reden\u00e7\u00e3o!", "Todos marchemos nesse grande intento\nDe pregar a salva\u00e7\u00e3o!\nSem recuar, sempre mostrando alento.\nSim, cumpramos a miss\u00e3o\nQue o Salvador, Cristo Jesus, nos deu!\nEle est\u00e1 tamb\u00e9m no posto seu.\nDiz-nos o Rei:\n\u201cSempre convosco estarei!\u201d\nVamos, irm\u00e3os, bem firmes neste pensamento!", "Firmes, levemos a mensagem santa\nDo Evangelho de Jesus!\nEsta mensagem divinal, que encanta,\nEspargindo gra\u00e7a e luz!\nCheia de b\u00ean\u00e7\u00e3os do glorioso Deus,\nQue conhece os escolhidos seus,\nCheia de amor,\nVem revelar o favor\nDa compaix\u00e3o de Deus e d\u00e1-nos gra\u00e7a tanta."]'::json, NULL)
        ,
        ('283'::text, ' Desafio'::text, '["Avan\u00e7ai ! Avan\u00e7ai! Derramai essa luz\nSobre os povos da terra que n\u00e3o t\u00eam Jesus!\nIde j\u00e1, diz o Mestre. Quem \u00e9 que ir\u00e1,\nObediente ao preceito que a todos nos d\u00e1?\nConfiai no Senhor! N\u00e3o tenhais mais temor!\nAvan\u00e7ai, com Jesus, avan\u00e7ai!", "Avan\u00e7ai! Avan\u00e7ai com a B\u00edblia na m\u00e3o!\nProclamai \u00e0s na\u00e7\u00f5es pela cruz Reden\u00e7\u00e3o!\nEnfrentai os perigos com f\u00e9 sempre em Deus,\nMesmo havendo pesares, rumamos aos c\u00e9us.\nDuras lutas tereis, com Jesus vencereis!\nAvan\u00e7ai, sem temor, avan\u00e7ai!", "Avan\u00e7ai! Avan\u00e7ai e pregai aos milh\u00f5es\nQue perecem nas trevas das desilus\u00f5es!\nFoi por eles tamb\u00e9m que o Senhor padeceu,\nE na terra, entre os homens, insultos sofreu.\nProclamai reden\u00e7\u00e3o! Em Jesus h\u00e1 perd\u00e3o!\nAvan\u00e7ai, com amor, avan\u00e7ai!"]'::json, NULL)
        ,
        ('284'::text, ' Obedi�ncia'::text, '["Nem sempre ser\u00e1 para onde eu quiser\nQue o Mestre me quer enviar!\n\u00c9 grande a Seara a embranquecer,\nEm que eu deverei trabalhar.\nSe, pois, a caminhos que eu nunca segui,\nUma voz a chamar-me eu ouvir,\nDirei: Meu Senhor, confiado em ti,\nEstou pronto ao teu mando seguir.", "Estou pronto a fazer o que queres, Senhor,\nConfiado no teu poder!\nEstou pronto a dizer o que queres, Senhor,\nSempre a ti pronto a obedecer!", "Palavras ter\u00e1s de amor e perd\u00e3o\nQue aos outros eu deva levar.\nNo triste caminho do v\u00edcio, est\u00e3o,\nPerdidos que eu deva ir buscar.\nSenhor, se a tua presen\u00e7a real\nMeu trabalho h\u00e1 de fortalecer,\nDarei a mensagem, bem firme e leal!\nEstou pronto a cumprir teu querer.", "Um canto modesto eu quero encontrar\nNa Seara do meu bom Senhor.\nEnquanto for vivo eu vou trabalhar\nEm prova do meu grande amor.\nDe ti meu sustento s\u00f3 depender\u00e1,\nE de tudo me h\u00e1s de prover!\nA tua vontade a minha ser\u00e1,\nEstou pronto a votar-te meu ser. Am\u00e9m."]'::json, NULL)
        ,
        ('285'::text, ' A salva��o do Brasil'::text, '["Do vasto Mato Grosso\nAt\u00e9 ao Cear\u00e1,\nPor vilas e cidades\nDo Sul ao Gr\u00e3o Par\u00e1,\nDeste Evangelho santo\nQue nos legou Jesus,\nAo povo brasileiro\nLevemos n\u00f3s a luz!", "Do Sul ao Amazonas,\nDo Oeste at\u00e9 ao mar,\nJ\u00e1 corre a doce nova\nDo amor que n\u00e3o tem par.\nJ\u00e1 muitos foram salvos\nDa morte e perdi\u00e7\u00e3o;\nNo sangue do Cordeiro\nAcharam salva\u00e7\u00e3o!", "Contudo, ainda muitos\nBem longe de crist\u00e3os,\nAdoram deuses feitos\nPor suas pr\u00f3prias m\u00e3os.\nDe t\u00e3o fatal pecado,\nDa idolatria vil,\nUnidos no Evangelho,\nSalvemos o Brasil."]'::json, NULL)
        ,
        ('286'::text, ' Colheita bendita'::text, '["Ceifeiros somos n\u00f3s, fi\u00e9is,\nCeifando para o Rei dos reis\nOs frutos prontos a colher\nQue em derredor se est\u00e3o a ver.\nAssim, a Cristo, o Salvador,\nRendemos preito de louvor,\nAo nosso Mestre, l\u00e1 no C\u00e9u,\nQue sobre a cruz por n\u00f3s morreu.", "Vamos n\u00f3s obedecer! Vamos \u00e0 colheita!\nPara quando anoitecer ver a obra feita\nPouco tempo restar\u00e1;\nBreve o prazo acabar\u00e1;\nBreve, breve, breve acabar\u00e1!", "N\u00f3s trabalhamos por Jesus\nQue para os campos nos conduz,\nE na Seara imensa quer\nObreiros novos receber.\nAinda h\u00e1 muito o que fazer,\nE tanto fruto que colher!\nN\u00e3o ouves Cristo perguntar:\n\u201cQuem quer por mim ir trabalhar?\u201d", "Est\u00e3o as horas a fugir;\nO teu Senhor n\u00e3o tarda em vir.\nTu queres fruto ao c\u00e9u levar,\nOu folhas s\u00f3 apresentar?\nOh, n\u00e3o demores a atender!\nA noite em breve vai descer.\nConosco toma o teu lugar\nE por Jesus vem trabalhar!"]'::json, NULL)
        ,
        ('287'::text, ' Igreja, alerta!'::text, '["\u00c9 tempo! \u00c9 Tempo, o Mestre est\u00e1 chamando j\u00e1!\nMarchar, marchar, confiando em seu amor!\nPartir, partir, a salva\u00e7\u00e3o a proclamar,\nCumprindo a ordem santa do bom Salvador!", "Marchar, sim avante!\nMarchar, sim, erguendo o seu pend\u00e3o real, avante!\nSim, avante! Unidos sempre, firmes avan\u00e7ai!\n\u201cGl\u00f3ria! Gl\u00f3ria!\u201d Eis que canta a multid\u00e3o!\nConsagrando todo o vosso cora\u00e7\u00e3o,\nA Jesus obedecei, seu querer executai,\nEntoai louvores altos! Avan\u00e7ai!", "\u201cQueremos luz\u201d - \u00c9 o grito das na\u00e7\u00f5es pag\u00e3s\nQue vem atravessando o imenso mar.\nOh, vamos j\u00e1 levar-lhes novas de amor,\nSem esquecer tamb\u00e9m aqui de semear.", "Desperta, Igreja! E vem o teu dever cumprir.\nA todos faze a Cristo conhecer!\nA tua m\u00e3o estende, com paciente amor,\nAjuda-os, em Jesus, a vida receber.", "Igreja, alerta! O dia prometido vem,\nNo qual Jesus, o Salvador, vir\u00e1!\nPor toda parte, o vitorioso Redentor\nEterna gl\u00f3ria e honra e louvor ter\u00e1."]'::json, NULL)
        ,
        ('288'::text, ' A mensagem real'::text, '["Sou forasteiro aqui, em terra estranha estou,\nCeleste P\u00e1tria, sim, \u00e9 para onde vou.\nEmbaixador por Deus, do Reino l\u00e1 dos c\u00e9us,\nVenho em servi\u00e7o do meu Rei!", "\u00c9 a mensagem que me deu,\nProvinda l\u00e1 dos altos c\u00e9us:\nQue vos reconcilieis\nCom o Senhor, Rei meu!\nReconciliai-vos j\u00e1 com Deus!", "Por Deus mandado est\u00e1, que o homem pecador!\nArrependido j\u00e1 se chegue ao Salvador!\nPois quem o receber, no Reino vai viver.\nVenho em servi\u00e7o do meu Rei!", "Mais belo que um rosal, o lar celeste tem\nA b\u00ean\u00e7\u00e3o imortal, o gozo eterno, al\u00e9m.\nNo c\u00e9u tem galard\u00e3o quem frui a reden\u00e7\u00e3o.\nVenho em servi\u00e7o do meu Rei!"]'::json, NULL)
        ,
        ('289'::text, ' Quem ir�?'::text, '["Eis os milh\u00f5es que, em trevas t\u00e3o medonhas,\nJazem perdidos, sem o Salvador!\nOh! Quem ir\u00e1 as novas proclamando\nQue Deus, em Cristo, salva o pecador?", "\u201cTodo poder o Pai me deu,\nNa terra como l\u00e1 no c\u00e9u!\nIde ao mundo anunciar o Evangelho,\nE eis-me convosco sempre!\u201d", "Portas abertas, eis por todo o mundo\nServos de Deus, avante sempre andai!\nCrentes em Cristo, uni as vossas for\u00e7as:\nDa escravid\u00e3o os povos libertai.", "\u00d3 Deus! Apressa o glorioso dia\nEm que os remidos todos se unir\u00e3o,\nE em coro santo, excelso, jubiloso,\nEternamente gl\u00f3ria a ti dar\u00e3o!"]'::json, NULL)
        ,
        ('29'::text, ' Louvor'::text, '["\u00d3 minha alma, a Deus bendize\nEm humilde adora\u00e7\u00e3o,\nNada h\u00e1 mais que te escravize\nNo pecado e escurid\u00e3o!\nOh, louvai-o! Oh, louvai-o!\nEle \u00e9 o Rei da cria\u00e7\u00e3o!", "Pelo seu favor e gra\u00e7a\nQue outorgou a nossos pais,\nE por tudo que ele fa\u00e7a,\nBendizei-o mais e mais!\nOh, louvai-o! Oh, louvai-o!\nComo nossos ancestrais.", "Se estivermos em perigo,\nNosso Deus nos guardar\u00e1;\nEle as trevas e o inimigo\nPara sempre abater\u00e1!\nOh, louvai-o! Oh, louvai-o!\nPelas b\u00ean\u00e7\u00e3os que nos d\u00e1!", "Anjos e homens, reverentes,\nAdorai-o com fervor;\nLua, estrela, sol luzente,\nQue ao espa\u00e7o dais fulgor,\nOh, louvai-o! Oh louvai-o!\nPois \u00e9 Deus de excelso amor!"]'::json, NULL)
        ,
        ('290'::text, ' Jesus j� vem'::text, '["Jesus j\u00e1 vem, o grande Salvador,\nO Bem-amado destes cora\u00e7\u00f5es!\nE os crentes mortos, vastas multid\u00f5es,\nRessurgir\u00e3o!", "Jesus j\u00e1 vem! E ao mesmo tempo n\u00f3s,\nOs que no mundo vivos ele achar,\nJ\u00e1 transformados vamos a cantar\nO seu louvor!", "Jesus j\u00e1 vem! Oh, perfei\u00e7\u00e3o de amor!\nPois para sempre, a andar com ele em luz,\nQuer ter a Igreja que, na sua cruz,\nJ\u00e1 redimiu.", "Jesus j\u00e1 vem! Que b\u00ean\u00e7\u00e3os em irmos ver\nA quem, por n\u00f3s, a morte aqui sofreu!\nE a interceder por n\u00f3s, compareceu\nPerante o Pai.", "Oh, vem, Senhor! N\u00e3o tardes, \u00f3 Jesus!\nPois Tu somente ir\u00e1s satisfazer\nAos nossos cora\u00e7\u00f5es, que anelam ver\nSeu Salvador! Am\u00e9m."]'::json, NULL)
        ,
        ('291'::text, ' Triunfante'::text, '["Entre nuvens multicores,\nCom os santos vem do c\u00e9u\nQuem, amando os pecadores,\nMorte atroz na cruz sofreu.\nAleluia! Cristo volta!\nEle reina, pois venceu!", "Todo olho h\u00e1 de v\u00ea-lo\nTriunfante aqui descer.\nMesmo quem num falso zelo\nCruelmente o fez sofrer.\nSeus algozes, consternados,\nSua gl\u00f3ria h\u00e3o de ver.", "Vem gloriosa, num momento,\nA esperada Reden\u00e7\u00e3o!\nCessa todo sofrimento,\nQueixas m\u00e1goas longe v\u00e3o.\nAleluia! Principia\nA total renova\u00e7\u00e3o.\nTodo o orbe, humildemente,\nHonre e louve a ti, Senhor!\nAnte o trono aurifulgente\nSe prosterne com amor!\nInaugura o Rei Eterno!\nAleluia! Vem, Senhor! Am\u00e9m."]'::json, NULL)
        ,
        ('292'::text, ' A vinda do Senhor'::text, '["Como foi para o c\u00e9u\nJesus Cristo h\u00e1 de vir,\nQuando o som da trombeta ecoar!\nQuando a voz do arcanjo\nCeleste se ouvir,\nEu irei com Jesus me encontrar.", "Oh! Que dia glorioso esse dia h\u00e1 de ser,\nQuando o som da trombeta ecoar!\nQuando Cristo nas nuvens tiver de descer\nE, ent\u00e3o, triunfante reinar!", "Nesse dia de gl\u00f3ria\nO meu corpo mortal\nSemelhante ao de Cristo h\u00e1 de ser!\nE j\u00e1 livre da morte\nE j\u00e1 livre do mal,\nA vit\u00f3ria de Cristo hei de ver.", "Eu aqui, pela cruz,\nPara o mundo morri\nMuita dor inda aqui sofrerei;\nMinha vida com Cristo\nEm meu Deus escondi,\nE com Cristo eu, enfim, reinarei.", "Vem Jesus, \u00f3 Senhor,\nVem depressa reinar!\nVem a paz e a justi\u00e7a trazer!\nCria\u00e7\u00e3o povo teu,\nTudo almeja o raiar\nDesse dia de gl\u00f3ria e poder.", "Este imp\u00e9rio do mal\nVem, Senhor destruir!\nRei celeste, vem presto reinar.\nVem, \u00f3 sol da justi\u00e7a,\nNo mundo luzir.\n\u00d3 meu Rei, vem meu pranto estancar."]'::json, NULL)
        ,
        ('293'::text, ' O dia glorioso'::text, '["Vinde, \u00f3 crist\u00e3os, regozijai-vos,\nO Senhor n\u00e3o tardar\u00e1!\nJ\u00e1 vem o glorioso dia\nEm que Cristo voltar\u00e1!", "Oh, dia triunfal de Cristo\nQuando l\u00e1 do c\u00e9u descer!\nTodos juntos, jubilosos, vamos\nO Senhor a receber!\nVinde, \u00f3 crist\u00e3os, regozijai-vos!\nO Senhor n\u00e3o tardar\u00e1!\nJ\u00e1 vem o glorioso dia\nEm que Cristo voltar\u00e1!", "Com multid\u00e3o inumer\u00e1vel\nSobre as nuvens ele vem!\nE todos juntos entraremos\nCom Jesus na gl\u00f3ria al\u00e9m!", "Ent\u00e3o ser\u00e1 glorificado\nNos remidos, o Senhor.\nE o mundo inteiro admirar\u00e1\nO seu poder e seu amor."]'::json, NULL)
        ,
        ('294'::text, ' O senhor voltar�'::text, '["Breve o Senhor, em esplendor,\nAqui h\u00e1 de descer.\nO mundo inteiro, com temor,\nJusti\u00e7a, ent\u00e3o, vai ter.", "Eis a verdade a despontar,\nQual planta a reflorir;\nEis a justi\u00e7a a iluminar\nO mundo que h\u00e1 de vir!", "Os povos todos, \u00f3 Senhor,\nSubmissos estar\u00e3o.\nDo r\u00e9gio s\u00f3lio em derredor\nTeu nome bendir\u00e3o.", "Faze o milagre, \u00f3 grande Deus:\nQue ven\u00e7a, enfim, o Bem,\nCom o poder dos altos c\u00e9us,\nAqui no mundo. Am\u00e9m."]'::json, NULL)
        ,
        ('295'::text, ' A volta de Jesus'::text, '["Sobre nuvem fulgurante\nVem do c\u00e9u o Salvador.\nCom poder e majestade\nAnjos traz ao seu redor.\nVem glorioso, vem glorioso,\nCristo, o eterno Vencedor.", "Quem atrozes inimigos\nUma vez na cruz venceu,\nRessurgiu da sepultura\nE subiu al\u00e9m do v\u00e9u.\nAleluia! Aleluia!\nOutra vez vem l\u00e1 do c\u00e9u.", "Para um dia t\u00e3o solene\nPreparaste-nos, Senhor,\nPara que, vencida a morte,\nNos vejamos sem temor,\nContemplando, contemplando\nTua face em resplendor. Am\u00e9m."]'::json, NULL)
        ,
        ('296'::text, ' Cristo n�o tarda'::text, '["Cristo em breve do c\u00e9u vir\u00e1,\nPois prometeu e n\u00e3o tardar\u00e1.\nOh, que alegria, que gl\u00f3ria ser\u00e1,\nQuando Jesus regressar!", "Cristo n\u00e3o tarda, n\u00e3o tarda em vir,\nCristo n\u00e3o tarda em vir.\nOh, que alegria e gl\u00f3ria ser\u00e1,\nQuando Jesus regressar!", "Em breve os mortos ressurgir\u00e3o,\nTodos os crentes se encontrar\u00e3o.\nJuntos, alegres, ao c\u00e9u subir\u00e3o,\nQuando Jesus regressar!", "Na terra, em breve teremos paz,\nQuando for preso o maligno mordaz!\nToda afli\u00e7\u00e3o ficar\u00e1 para tr\u00e1s,\nQuando Jesus regressar!", "Cristo n\u00e3o tarda, n\u00e3o tarda em vir.\nQuem pronto est\u00e1 para aquele porvir?\nOh, que alegria teremos em ir,\nQuando Jesus regressar."]'::json, NULL)
        ,
        ('297'::text, ' A chamada final'::text, '["Quando l\u00e1 do c\u00e9u descendo\nPara os seus Jesus voltar,\nE o clarim de Deus a todos proclamar\nQue chegou o grande dia\nDa vit\u00f3ria do meu Rei,\nEu, por sua imensa gra\u00e7a, l\u00e1 estarei!", "Quando enfim chegar o dia\nDa vit\u00f3ria do meu Rei,\nQuando enfim chegar o dia\nPela gra\u00e7a de Jesus eu l\u00e1 estarei!", "Nesse dia, quando os mortos\nH\u00e3o de a voz de Cristo ouvir,\nE dos seus sepulcros h\u00e3o de ressurgir,\nOs remidos, transformados,\nLogo aclamar\u00e3o seu Rei,\nE por sua imensa gra\u00e7a l\u00e1 estarei!", "Pelo mundo rejeitado\nFoi Jesus, meu Salvador!\nDesprezaram, insultaram meu Senhor!\nMas glorioso vem o dia\nDa vit\u00f3ria do meu Rei,\nE por sua imensa gra\u00e7a l\u00e1 estarei!"]'::json, NULL)
        ,
        ('298'::text, ' A pedra fundamental'::text, '["Da Igreja o fundamento\n\u00c9 Cristo, o Salvador!\nEm seu poder descansa\nE \u00e9 forte em seu amor.\nPois nele, alicer\u00e7ada,\nSegura e firme est\u00e1,\nE sobre a Rocha Eterna\nJamais se abalar\u00e1.", "A pedra preciosa\nQue Deus predestinou\nSustenta pedras vivas\nQue a gra\u00e7a trabalhou.\nE quando o monumento\nSurgir em plena luz,\nA gl\u00f3ria do edif\u00edcio\nSer\u00e1 do Rei Jesus!", "Neste edif\u00edcio santo\nQue visa o teu louvor,\nEsteja a tua b\u00ean\u00e7\u00e3o,\nRogamos-te, Senhor!\nQue muitos pecadores\nAqui, em contri\u00e7\u00e3o,\nSe tornem templos santos\nDe tua habita\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('299'::text, ' Renova��o'::text, '["Fortalece a tua Igreja,\n\u00d3 bendito Salvador!\nD\u00e1-lhe tua plena gra\u00e7a,\nOh, renova seu vigor.\nVivifica, vivifica\nNossas almas, \u00f3 Senhor! Am\u00e9m.\nVivifica, vivifica\nNossas almas, \u00f3 Senhor! Am\u00e9m."]'::json, NULL)
        ,
        ('3'::text, ' A Igreja em adora��o'::text, '["Eterno Pai teu povo congregado,\nHumilde entoa o teu louvor aqui!\nNo dia para o culto reservado,\nCom esperan\u00e7a olhamos para ti.\nTeu santo livro \u00f3 grande Deus,tomamos\nCom f\u00e9 singela e reverente amor,\nE, como atentos filhos procuramos\nCi\u00eancia na palavra do Senhor.", "Jesus! Aos teus benditos p\u00e9s sentados\nQueremos teu conselho receber,\nE sendo por ti mesmo doutrinados,\nDe mais em mais na santa f\u00e9 crescer.\nDo mundo e seus encargos retirados\nQueremos descansar em ti, Senhor,\nMirando os ricos bens entesourados\nNa plenitude do teu vasto amor.", "Ensina aos teus, Esp\u00edrito divino,\nDissipa as trevas desses cora\u00e7\u00f5es\nE com a luz do teu celeste ensino,\nVem aclarar as santas instru\u00e7\u00f5es.\nAviva em n\u00f3s as for\u00e7as da mem\u00f3ria,\nPois sempre mais queremos conhecer\nO Rei dos c\u00e9us, o Cristo cuja a gl\u00f3ria\nEnleva os santos anjos de prazer. Am\u00e9m."]'::json, NULL)
        ,
        ('30'::text, ' Provid�ncia de Deus (na cria��o)'::text, '["\u00d3 Deus, \u00f3 Provid\u00eancia! Com teu real poder\nTrouxeste \u00e0 exist\u00eancia o mundo, todo ser!\nE a tudo que criaste, com benfazeja m\u00e3o,\nAs b\u00ean\u00e7\u00e3os n\u00e3o negaste da tua prote\u00e7\u00e3o.", "Eterno Deus, \u00f3 Deus sem par,\nA cria\u00e7\u00e3o, com gratid\u00e3o\nTe vem louvar!\nNos c\u00e9us teu nome aclamam\nOs astros, que s\u00e3o teus;\nA terra e o mar proclamam:\n\u201c\u00c9s o nosso Deus!\u201d", "Ao sol resplandecente e \u00e0 vastid\u00e3o do mar\nTra\u00e7aste, \u00f3 Deus potente, as leis que ir\u00e3o guardar.\nE a tenra e pequenina florzinha em bot\u00e3o,\nA tua m\u00e3o divina conserva em prote\u00e7\u00e3o.", "Dos campos, a verdura, dos frutos, o sabor,\nCelebram a ternura do teu constante amor!\nE a natureza inteira, por toda a Cria\u00e7\u00e3o,\nProclama, alvissareira, a tua prote\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('300'::text, ' Igreja militante'::text, '["Para resgatar a Igreja\nGrande pre\u00e7o Cristo deu.\nN\u00e3o foi ouro, nem foi prata:\nFoi seu sangue que verteu.", "Grande foi o teu amor\nQue na cruz assim mostraste!\nPara meus grilh\u00f5es partires,\nTua vida n\u00e3o poupaste!", "Pois agora que sou tua,\nN\u00e3o te quero mais perder.\nE com gratid\u00e3o servindo\nQuero s\u00f3 por ti viver.", "Quero receber teu jugo\nE em teus passos caminhar!\nSe por ti eu sofro tudo,\nVou contigo em paz reinar.", "Eis que estou aqui na terra\nEsperando o teu voltar.\nVem buscar a tua Igreja,\nVem, Senhor, e sem tardar. Am\u00e9m."]'::json, NULL)
        ,
        ('301'::text, ' O �nico salvador'::text, '["Igreja do Senhor,\nProclama com fervor:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nA todos faze ouvir,\nInsiste em repetir:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d", "Em n\u00f3s n\u00e3o h\u00e1 poder\nQue venha o mal vencer.\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u00c9 v\u00e3o tentar viver\nCom Deus, sem renascer:\n\u201cQuem salva \u00e9 s\u00f3 Jesus", "A lei n\u00e3o d\u00e1 perd\u00e3o:\nTraz morte e maldi\u00e7\u00e3o.\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nEm Cristo os bens est\u00e3o\nDa plena reden\u00e7\u00e3o!\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d", "O Bem dos altos c\u00e9us\n\u00c9 Cristo, o Dom de Deus.\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nEle \u00e9 quem livra os r\u00e9us\nTornando-os filhos seus.\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d", "Igreja do Senhor\nProclama com fervor:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nPor esse extremo amor\nQue tem ao pecador\nLouvemos a Jesus."]'::json, NULL)
        ,
        ('302'::text, ' Povoam as cidades'::text, '["Povoam as cidades inquietas multid\u00f5es\nQue vivem na gan\u00e2ncia e em vis degrada\u00e7\u00f5es.\nBem pouco ali ressoam as preces e o louvor\nQue as mal nutridas almas levantam ao Senhor.", "H\u00e1 muitas cenas tristes, flagrantes erros h\u00e1!\nH\u00e1 lares sem carinho e o medo em tudo est\u00e1.\nNos centros e nos bairros, nas ruas, nas pris\u00f5es,\nAs almas esquecidas perecem aos milh\u00f5es.", "Contempla, \u00f3 Deus, teu povo nas lutas do viver,\nE aos crentes das cidades concede teu poder!\nDo sonho \u00e0 realidade permite-nos sair,\nOh, faze a Igreja de hoje melhor a ti servir. Am\u00e9m."]'::json, NULL)
        ,
        ('303'::text, ' Pend�o Real'::text, '["Um pend\u00e3o real vos entregou o Rei,\nA v\u00f3s, soldados seus!\nCorajosos, pois, em tudo o defendei,\nMarchando para os c\u00e9us.", "Com valor, sem temor!\nPor Cristo prontos a sofrer!\nBem alto erguei o seu pend\u00e3o,\nFirmes, sempre, at\u00e9 morrer!", "Eis formados j\u00e1 terr\u00edveis batalh\u00f5es\nDo grande usurpador!\nRevelai-vos hoje bravos campe\u00f5es!\nAvante, sem temor!", "Quem receio sente no seu cora\u00e7\u00e3o\nE fraco se mostrar,\nN\u00e3o receber\u00e1 o eterno galard\u00e3o\nQue Cristo tem pra dar!", "Pois sejamos todos a Jesus fi\u00e9is,\nE a seu real pend\u00e3o!\nOs que da vit\u00f3ria colhem os laur\u00e9is\nCom ele reinar\u00e3o."]'::json, NULL)
        ,
        ('304'::text, ' A voz do evangelho'::text, '["A voz do Evangelho\nJ\u00e1 se fez ouvir aqui,\nPublicando em som alegre,\nO que Deus j\u00e1 fez por ti.\nPois tanto ao mundo amou,\nE ao perdido pecador,\nQue do c\u00e9u mandou seu Filho\nPara ser seu Redentor.", "Santa paz e perd\u00e3o,\n\u00c9 a nova l\u00e1 dos c\u00e9us!\nSanta paz e perd\u00e3o,\nBendito o nosso Deus!", "A voz do Evangelho\nSeguran\u00e7a, vida e paz,\n\u00c9 o amor de Jesus Cristo\nQue o perd\u00e3o de Deus nos traz.\nAs novas se nos d\u00e3o\nDe haver um Salvador,\nPoderoso e mui bondoso\nQue perdoa o pecador.", "A voz do Evangelho\nVem a todos avisar\nDo perigo, grande e grave,\nPara quem se descuidar.\nSalvai-vos desde j\u00e1,\nN\u00e3o vos detenhais no mal,\nCobi\u00e7ando os seus prazeres,\nPois vos pode ser fatal."]'::json, NULL)
        ,
        ('305'::text, ' Quem quiser'::text, '["Quem ouvir as novas, deve proclamar.\nSalva\u00e7\u00e3o de gra\u00e7a, vinde desfrutar!\nOh! Que o mundo inteiro ou\u00e7a anunciar:\nTodo o que quiser \u00e9 vir!", "Todo o que quiser, todo o que quiser\nPode a boa-nova hoje receber!\nQue o Senhor da gl\u00f3ria a todos quer remir!\nTodo o que quiser \u00e9 vir!", "Quem quiser, agora, venha aceitar;\nEis a porta aberta, entre sem tardar!\n\u00c9 Jesus caminho para o c\u00e9u chegar!\nTodo o que quiser \u00e9 vir!", "Que fiel promessa tens, \u00f3 pecador,\nDe uma vida eterna! Vem ao Salvador!\nEle a todos fala com mui terno amor.\nTodo o que quiser \u00e9 vir!"]'::json, NULL)
        ,
        ('306'::text, ' Fidelidade na luta'::text, '["Erguei-vos, crist\u00e3os! Cristo quer-vos de p\u00e9!\nNa luta do bem, revelai vossa f\u00e9!\nCingindo a armadura, hoje a Cristo exaltai,\n\u00c0 sombra da cruz, corajosos lutai!", "Sede fi\u00e9is, sede fi\u00e9is\nSede fi\u00e9is e por Cristo lutai!", "Jesus vos remiu, afastai o temor.\nConfiantes, segui vosso bom Salvador!\nNa causa de Cristo fervor demonstrai!\n\u00c0 sombra da cruz, corajosos lutai!", "As hostes do mal deveis hoje enfrentar,\nAs suas pris\u00f5es pecadores livrar.\nDe Cristo bem alto o pend\u00e3o levantai!\n\u00c0 sombra da cruz, corajosos lutai!"]'::json, NULL)
        ,
        ('307'::text, ' A santa peleja'::text, '["Erga-se o estandarte,\nTremulando \u00e0 luz!\nSeu bras\u00e3o: coroa\nCircundando a cruz!\nNa cruzada invicta,\nQuem quer hoje entrar,\nE o evangelho santo\nIr anunciar?", "Erga-se o estandarte,\nTremulando \u00e0 luz!\nSeu bras\u00e3o coroa\nCircundando a cruz.", "Luta contra as trevas,\nLuta contra o mal!\nEis-nos \u00e0 peleja\nSanta, divinal!\nDar combate ao erro,\n\u00c0 supersti\u00e7\u00e3o,\nE salvar os homens\nDa degrada\u00e7\u00e3o.", "Vinde ao bom combate\nSem esmorecer!\nDe valor eterno\nGl\u00f3ria haveis de ter.\nA quem quer segui-lo,\nEis que diz Jesus:\n\u201cNegue-se a si mesmo,\nTome a sua cruz.\u201d", "Salvador confio\nEm teu grande amor!\nEntro na batalha\nCom vibrante ardor.\nD\u00e1 que em teu servi\u00e7o\nSaiba a cruz tomar\nE teu santo nome\nHoje e sempre honrar"]'::json, NULL)
        ,
        ('308'::text, ' Escurid�o e luz'::text, '["Nas tormentas dessa vida,\nPerto est\u00e1 a perdi\u00e7\u00e3o.\nAos incautos navegantes,\nQuem trar\u00e1 a salva\u00e7\u00e3o?", "Resplande\u00e7am nossas luzes\nAtrav\u00e9s do escuro mar,\nPois nas trevas do pecado\nAlmas podem naufragar!", "Brilha sempre, em gra\u00e7a imensa,\nRico amor do eterno Deus.\nCumpre a n\u00f3s mostrar o rumo\nDo caminho para os c\u00e9us.", "Nuvens de paix\u00e3o mundana\nN\u00e3o nos deixam ver o sol.\nOh, mostremos o perigo\nCom as luzes do farol.", "Aos errantes, insensatos,\nGuia ao porto divinal!\nEm Jesus h\u00e1 vero abrigo\nDo furor do temporal.", "Noite eterna se aproxima,\nTenebrosa em seu horror!\nClama, avisa aos infelizes;\nInsta-os para o Salvador!"]'::json, NULL)
        ,
        ('309'::text, ' Proclama��o'::text, '["Proclamai que Jesus Cristo \u00e9 Senhor e Rei,\nProclamai! Proclamai!\nQue nos livra da cruel condena\u00e7\u00e3o da Lei,\nProclamai! Proclamai!\nContai que l\u00e1 dos c\u00e9us desceu Jesus,\nPor n\u00f3s sofrendo a morte da nefanda cruz!\nAgora, ressurreto, sobre o trono est\u00e1,\nE \u00e9 Deus de toda gra\u00e7a, que de gra\u00e7a tudo d\u00e1!", "Proclamai que Jesus Cristo \u00e9 Senhor e Rei,\nProclamai! Proclamai!\nQue nos livra da cruel condena\u00e7\u00e3o da Lei,\nProclamai! Proclamai!", "Proclamai que reina em gra\u00e7a Cristo Salvador,\nProclamai! Proclamai!\nQue por cetro de seu reino ele tem o amor,\nProclamai! Proclamai!1\nContai aos tristes que a alegria ele tem.\nE \u00e9 dele que aos cansados o descanso vem!\nDizei aos pecadores que ele os quer salvar,\nE a todos os cativos que ele os pode libertar!", "Proclamai que em grande gl\u00f3ria Cristo voltar\u00e1,\nProclamai! Proclamai!\nE com todos os remidos ele reinar\u00e1,\nProclamai! Proclamai!\nDizei que, sem demora, todos devem crer\nE a Cristo agora mesmo o cora\u00e7\u00e3o render!\nQue estejam preparados quando aqui voltar,\nA fim de, redimidos, com louvores o aclamar."]'::json, NULL)
        ,
        ('31'::text, ' Provid�ncia de Deus (na reden��o)'::text, '["\u00d3 Deus, \u00f3 Provid\u00eancia! Sem ti n\u00e3o h\u00e1 viver!\nVem dar-nos a assist\u00eancia do teu real poder!\nT\u00e3o s\u00f3 em ti confiamos e em tua prote\u00e7\u00e3o,\nPois s\u00f3 em ti achamos conforto e reden\u00e7\u00e3o.", "\u00c9s nosso Pai, \u00f3 Deus de amor!\nTeus filhos v\u00eam aqui, tamb\u00e9m,\nCantar louvor!\nAo mundo renunciamos,\nAgora somos teus;\nE alegres proclamamos:\n\u201c\u00c9s o nosso Deus!\u201d", "Ao homem Tu criaste, de ti vivo exemplar;\nNa terra o colocaste a fim de te louvar.\nCa\u00eddo embora esteja, estendes tua m\u00e3o,\nNa gra\u00e7a benfazeja de tua Reden\u00e7\u00e3o.", "Em Cristo, o Filho amado, a tua gra\u00e7a vem\nCobrir-nos o pecado e dar-nos todo o bem,\nReais munific\u00eancias gozamos n\u00f3s, ent\u00e3o,\nNas terna provid\u00eancias de tua Reden\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('310'::text, ' Quem salva � s� Jesus'::text, '["De Deus, \u00f3 eterna Igreja\nQue espalha santa luz,\nProclama aos pecadores:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nProclama aos pecadores:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d", "Aos presos, algemados,\nNo mundo que seduz,\nRevela a esperan\u00e7a:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nRevela a esperan\u00e7a:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d", "Atrai os que, perdidos,\nMui longe est\u00e3o da cruz.\nVai, dize aos desgarrados:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d\nVai, dize aos desgarrados:\n\u201cQuem salva \u00e9 s\u00f3 Jesus!\u201d"]'::json, NULL)
        ,
        ('311'::text, ' Avanta, � crentes'::text, '["Avante, avante, \u00f3 crentes,\nSoldados de Jesus!\nErguei seu estandarte,\nLutai por sua cruz!\nE contra os inimigos\nEm grandes multid\u00f5es,\nO excelso Comandante\nDirige os batalh\u00f5es.", "Avante, avante \u00f3 crentes!\nPor Cristo pelejai!\nVesti sua armadura,\nEm seu poder marchai!\nNo posto sempre achados,\nVelando em ora\u00e7\u00e3o,\nEm meio dos perigos\nSeguindo o Capit\u00e3o!", "Avante, avante, \u00f3 crentes,\nA passo triunfal!\nAp\u00f3s combate horrendo,\nVir\u00e1 a paz final!\nEnt\u00e3o, eternamente,\nSer\u00e1 o vencedor\nNo c\u00e9u glorificado\nCom Cristo, o Salvador!"]'::json, NULL)
        ,
        ('312'::text, ' H� trabalho certo'::text, '["H\u00e1 trabalho certo para ti crist\u00e3o\nQue demanda toda tua devo\u00e7\u00e3o.\nVem, alegremente, a Cristo obedecer,\nPois s\u00f3 tu, \u00f3 crente, o poder\u00e1 fazer!", "Por Jesus \u00e9 Trabalhar!\nProntamente, fielmente, trabalhar!\nEm servi-lo, que prazer!\nE s\u00f3 tu, \u00f3 crente, o poder\u00e1 fazer!", "Para cada crente o mestre preparou\nUm trabalho certo, quando o resgatou.\nO trabalho a que Jesus te chama aqui,\nComo ser\u00e1 feito, se n\u00e3o for por ti?", "Mesmo que humilde, sendo para Deus,\nEle \u00e9 contemplado l\u00e1 dos altos c\u00e9us!\nE o esfor\u00e7o feito n\u00e3o ser\u00e1 em v\u00e3o,\nSe tiver, de Cristo, plena aprova\u00e7\u00e3o!", "Quantos h\u00e1 perdidos, sem a salva\u00e7\u00e3o,\nQuantos que precisam de consola\u00e7\u00e3o!\nComo Cristo os ama, faze-os entender,\nPois s\u00f3 tu, \u00f3 crente, o poder\u00e1s fazer."]'::json, NULL)
        ,
        ('313'::text, ' Prontid�o'::text, '["Quem de Cristo ao lado agora quer andar?\nQuem a sua vida quer lhe dedicar?\nTudo abandonando, a Jesus seguir,\nEncarando as lutas que lhe possam vir?", "Quem de Cristo ao lado sempre quer andar?\nQuem quer ajud\u00e1-lo outros a chamar?\nPela tua gra\u00e7a, pelo teu amor,\nEis-nos ao teu lado, somos teu, Senhor!", "N\u00e3o ambicionado honras ou poder,\nEis-nos todos firmes para combater.\nQuem o amor de Cristo vem a contemplar,\nH\u00e1 de, resoluto, ao seu lado estar.", "N\u00e3o com ouro ou prata foi, Senhor Jesus,\nQue nos redimiste, mas por tua cruz.\nSim, com o teu sangue, sangue remidor,\nTu nos resgataste de uma vez, Senhor!", "A peleja sempre dura nos ser\u00e1;\nInimigo forte nos combater\u00e1;\nMas Onipotente \u00e9 o Rei dos reis!\nA vit\u00f3ria \u00e9 certa para os seus fi\u00e9is!"]'::json, NULL)
        ,
        ('314'::text, ' Dilig�ncia'::text, '["Ouve! A voz divina clama:\n\u201cQuem deseja trabalhar?\u201d\nVastos campos nos convidam,\nHoje entremos a ceifar!\nIncessante o Mestre apela,\nChama obreiros para si\nQuem responder\u00e1 dizendo:\n\u201cManda-me, estou pronto aqui?\u201d", "Corre! Aponta aos pecadores\nO benigno Salvador!\nVai! Conduze os cordeirinhos\nAo rega\u00e7o do Pastor!\nLeva \u00e0s almas doloridas\nNovas de consola\u00e7\u00e3o!\nVai! Proclama a todo mundo:\n\u201cEm Jesus h\u00e1 salva\u00e7\u00e3o.\u201d", "Oh! N\u00e3o digas, ocioso:\nEu n\u00e3o tenho o que fazer!\u201d\nEis os povos que padecem,\nMultid\u00f5es a perecer!\nOlha o Mestre a convocar-te,\nOuve a voz chamando a ti!\nOh! Responde sem demora:\n\u201cManda-me! Estou pronto aqui!\u201d"]'::json, NULL)
        ,
        ('315'::text, ' Servi�o do crente'::text, '["Vamos n\u00f3s trabalhar, somos servos de Deus,\nE o mestre seguir no caminho dos c\u00e9us!\nCom o seu bom conselho o vigor renovar,\nDiligentes fazendo o que ele ordenar.", "No labor, sem cessar,\nA servir a Jesus.\nCom amor e f\u00e9 e com ora\u00e7\u00e3o,\nAt\u00e9 que volte o bom Senhor!", "Vamos n\u00f3s trabalhar e os famintos fartar,\nPara a fonte os sedentos depressa levar!\nS\u00f3 na cruz do Senhor nossa gl\u00f3ria ser\u00e1,\nPois Jesus salva\u00e7\u00e3o por seu sangue nos d\u00e1!", "Vamos n\u00f3s trabalhar, ajudados por Deus,\nQue a coroa de gl\u00f3ria nos d\u00e1 l\u00e1 nos c\u00e9us.\nA mans\u00e3o dos fi\u00e9is sempiterna ser\u00e1,\nPois Jesus salva\u00e7\u00e3o inef\u00e1vel nos d\u00e1!"]'::json, NULL)
        ,
        ('316'::text, ' Os intentos de Deus'::text, '["Os seus intentos cumpre Deus\nNo decorrer dos anos.\nEle executa o seu querer\nDe acordo com seus planos.\nEia! Aproxima-se o final!\nBem perto o dia vem,\nQuando a gl\u00f3ria de Deus\nH\u00e1 de o mundo inundar,\nComo as \u00e1guas cobrem o mar.", "Desde o long\u00ednquo norte ao sul,\nEm todos os recantos,\nSai a mensagem do Senhor\nDa boca dos seus santos.\nPovos, na\u00e7\u00f5es, vinde! Atendei!\nO seu apelo ouvi,\nPara a gl\u00f3ria de Deus\nVir o mundo inundar,\nComo as \u00e1guas cobrem o mar.", "Com a bandeira de Jesus\nAvante, caminhemos.\nSeu Evangelho, a salva\u00e7\u00e3o,\nAo mundo anunciemos.\nContra o pecado e todo o mal\nLutemos com vigor,\nPara a gl\u00f3ria de Deus\nVir o mundo inundar,\nComo as \u00e1guas cobrem o mar.", "Nosso trabalho v\u00e3o ser\u00e1\nSe Deus n\u00e3o for presente.\nS\u00f3 ele o esfor\u00e7o aqui bendiz\nE \u00e9 quem nutre a semente.\nEia! Aproxima-se o final!\nBem perto o dia vem,\nQuando a gl\u00f3ria de Deus\nH\u00e1 de o mundo inundar,\nComo as \u00e1guas cobrem o mar."]'::json, NULL)
        ,
        ('317'::text, ' Chamada'::text, '["Eia, crentes destemidos,\nDa verdade convencidos,\nPara a luta apercebidos,\nNo combate entrai!\nEis que surgem, aleivosos,\nErros grandes, perniciosos,\nNestes tempos perigosos,\nVossa f\u00e9 mostrai!\nO dever vos chama.\nVosso Deus proclama\nA santa lei do eterno Rei,\nQue vosso ardor reclama.\nConfessai, pois, resolutos,\nFervorosos, incorruptos,\nE com l\u00e1bios impoluto:\nDeus, Verdade e F\u00e9!", "V\u00f3s por Cristo libertados,\nN\u00e3o sejais escravizados!\nOs direitos alcan\u00e7ados\nFirmes sustentai!\nSalva\u00e7\u00e3o por homens dada,\nPaz fingida, paz comprada,\nLei de Deus falsificada,\nTudo rejeitai!\nVosso Deus n\u00e3o muda.\nO Senhor ajuda\nA quem cumprir, sem desistir,\nE seus fi\u00e9is escuda.\nAvan\u00e7ai, pois, exultando,\nSempre em Cristo confiando,\nVosso testemunho dando:\nDeus Verdade e F\u00e9!"]'::json, NULL)
        ,
        ('318'::text, ' Ceifeiros do Senhor'::text, '["Ceifeiros da Seara santa,\nQu\u00e3o poucos, fracos sois!\nMas forte \u00e9 Cristo, vosso Mestre,\nAvante, avante pois!", "Os que esperam no Senhor renovar-se-\u00e3o!\nCrescer\u00e3o em vigor; subir\u00e3o at\u00e9 \u00e0s alturas!\nCorrer\u00e3o e sem fadiga andar\u00e3o sem se cansar!\nCorrer\u00e3o e sem fadiga andar\u00e3o sem se cansar!\nVoar\u00e3o e nas alturas, como \u00e1guias ser\u00e3o!", "Cansados, tristes, sem alento,\nDeixai-vos de chorar!\nSe Onipotente \u00e9 vosso Mestre,\nPor que desanimar?", "Jesus est\u00e1 convosco sempre,\nAssim nos prometeu.\nCoragem, pois, irm\u00e3os avante,\nEis que ele j\u00e1 venceu!"]'::json, NULL)
        ,
        ('319'::text, ' Obreiros em marcha'::text, '["Eis marchamos para aquele bom pa\u00eds,\nOnde o crente, sim, \u00e9 Cristo que o diz,\nCom o Salvador, vivendo ali feliz,\nVai com ele descansar.\nTrabalhemos, pois, com zelo e com vigor,\nConstrangidos pelo seu imenso amor;\nBem servindo em tudo ao grande Salvador,\nEis que a vida vai findar!", "Acordai! Acordai! Despertai! Despertai!\nE cantai! Sim,cantai: O Senhor n\u00e3o tardar\u00e1!\nEis marchamos para aquele bom pa\u00eds,\nOnde o crente, sim, \u00e9 Cristo que o diz,\nCom o Salvador, vivendo ali feliz,\nVai com ele descansar.", "Eis conosco est\u00e1 o insigne Capit\u00e3o,\nQue nos assegura eterna salva\u00e7\u00e3o!\nEis da santa f\u00e9 o invicto pavilh\u00e3o!\nVamos, vamos trabalhar!\nEia, avante! Nada temos que temer!\nPor Jesus havemos sempre de vencer.\nTrabalhemos, pois at\u00e9 ao anoitecer\nE o trabalho aqui findar.", "Revestidos da paci\u00eancia de Jesus,\nRedimidos para andar aqui na luz,\nExaltemos juntos hoje a sua cruz,\nVamos, vamos trabalhar.\nOs perdidos vamos com amor buscar!\nAos descrentes vamos logo declarar\nQue Jesus quer, hoje, a todos resgatar!\nOh, sim, vamos trabalhar!"]'::json, NULL)
        ,
        ('32'::text, ' O Deus fiel'::text, '["Tu \u00e9s fiel, Senhor, meu Pai celeste,\nTeus filhos sabem que n\u00e3o falhar\u00e1s!\nNunca mudaste, Tu nunca faltaste,\nTal como eras Tu sempre ser\u00e1s.", "Tu \u00e9s fiel Senhor! Tu \u00e9s fiel Senhor!\nDia ap\u00f3s dia, com b\u00ean\u00e7\u00e3os sem fim,\nTua merc\u00ea nos sustenta e nos guarda;\nTu \u00e9s fiel, Senhor, fiel assim.", "Flores e frutos, montanhas e mares,\nSol, luz, estrelas brilhando no c\u00e9u,\nTudo criaste na terra e nos ares,\nPara louvar-te, Senhor, que \u00e9s fiel.", "Pleno perd\u00e3o tu d\u00e1s! Que seguran\u00e7a!\nCada momento me guias, Senhor,\nE no porvir, \u00f3! Que doce esperan\u00e7a\nDesfrutarei do teu rico favor."]'::json, NULL)
        ,
        ('320'::text, ' Brilha no viver'::text, '["N\u00e3o somente ao se fazer trabalho singular\n\u00c9 mister agir com muito ardor;\nMas as coisas mais humildes a executar\nDeves faz\u00ea-las com fervor.", "Brilha no meio do teu viver,\nBrilha no meio do teu viver!\nPois talvez algum aflito possas socorrer;\nBrilha no meio do teu viver!", "Oh! Talvez alguma vida possas confortar\nCom palavras brandas de amor.\nOu, talvez, algumas almas tristes alcan\u00e7ar\nCom a mensagem do Senhor.", "Por maior que venha ser o teu esfor\u00e7o aqui,\nPor mais firme a tua devo\u00e7\u00e3o,\nQuantas almas inda jazem ao redor de ti,\nNa mais total escurid\u00e3o."]'::json, NULL)
        ,
        ('321'::text, ' M�os ao trabalho'::text, '["M\u00e3os ao trabalho, jovens!\nVai j\u00e1 passando o alvor.\nEia, enquanto tendes\nVossa vida em flor!\nVamos enquanto \u00e9 dia,\nCom for\u00e7a trabalhar!\nEia, que em vindo a noite\nN\u00e3o h\u00e1 mais lidar", "M\u00e3os ao trabalho, homens!\nFirmes, enquanto h\u00e1 luz\nEia, que \u00e9 tempo agora\nDe servir Jesus!\nIde o vigor da vida\nTodos ao bem votar!\nEia, que em vindo a noite\nN\u00e3o h\u00e1 mais lidar.", "M\u00e3os ao trabalho todos!\nBreve nos chega o fim\nQuando soar um dia\nDa morte o clarim!\nVamos, irm\u00e3os, \u00e0 obra!\nPor Cristo trabalhar!\nEia, que em vindo a noite\nVamos descansar."]'::json, NULL)
        ,
        ('322'::text, ' Hero�nas da f�'::text, '["Avante, companheiras,\nNa causa de Jesus!\nAtivas no trabalho,\nServi enquanto h\u00e1 luz!", "Mostrai, na atividade,\nA vossa gratid\u00e3o\nA Cristo, o grande Amigo\nO Autor da Reden\u00e7\u00e3o.", "O exemplo de Maria\nQue, aos p\u00e9s do Salvador,\nTomou a melhor parte,\nSegui com f\u00e9 e amor.", "Os dons do vosso afeto\nA Cristo consagrai!\n\u00c0 Igreja, que \u00e9 seu corpo,\nDedica\u00e7\u00e3o mostrai!", "Erguei-vos hero\u00ednas,\nE vinde trabalhar!\nMostrai vossas virtudes\nNa P\u00e1tria, Igreja e Lar!"]'::json, NULL)
        ,
        ('323'::text, ' Santa peleja'::text, '["Nesta arena da santa peleja,\nNo combate devemos entrar,\nBatalhando com f\u00e9 e coragem,\nPois a noite n\u00e3o tarda em chegar.", "Vamos, vamos, leais companheiras,\nCaminhando na luz do Senhor!\nA divisa do nosso estandarte\nSeja, esperan\u00e7a e amor. (bis)", "Mas se o mundo, coberto de trevas,\nNos olhar com rigor ou desd\u00e9m,\nProssigamos, ousadas, avante,\nEspalhando as id\u00e9ias do bem.", "Pelejemos! A causa \u00e9 sagrada!\nVamos todas com f\u00e9 e ora\u00e7\u00e3o;\nE guiados por Deus, Pai celeste,\nCumpriremos a nossa miss\u00e3o!"]'::json, NULL)
        ,
        ('324'::text, ' Unidas e firmes'::text, '["Sempre unidas companheiras,\nDeclaremos por Jesus,\nGuerra santa contra as trevas,\nPelejando junto \u00e0 cruz!", "Vamos todas, vamos todas,\nSempre unidas no Senhor!\nComo esposas, m\u00e3es ou filhas,\nTrabalhemos com fervor!", "Somos fracas, bem sabemos,\nMas havemos de vencer;\nSe tivermos confian\u00e7a\nSe cumprirmos o dever.", "Sempre firmes na esperan\u00e7a,\nConfiando no Senhor,\nImploremos sua gra\u00e7a\nE busquemos seu amor!"]'::json, NULL)
        ,
        ('325-A'::text, ' Jesus Cristo � o Senhor'::text, '["Todas unidas com santo ardor,\nAlmas remidas cheias de amor.\nNossa mensagem \u00e9 de valor,\nPois Jesus Cristo \u00e9 o Senhor!", "Sim, Jesus Cristo \u00e9 o Senhor!\nDas nossas vidas ele \u00e9 o Senhor!\nDos nossas lares ele \u00e9 o Senhor!\nE o nosso tempo \u00e9 do Senhor!", "Vamos bem alto o seu Nome erguer,\nNossos recursos oferecer,\nPara que o mundo venha saber\nQue ele \u00e9 o Senhor de todo ser!", "Nosso trabalho \u00e9 para o Senhor,\nNossos talentos s\u00e3o do Senhor.\nVamos, unidas, pois, proclamar\nQue ele \u00e9 o Senhor do c\u00e9u e mar!"]'::json, NULL)
        ,
        ('325'::text, ' Aspira��o feminina'::text, '["A n\u00f3s aqui reunidas,\nSenhor envia luz.\nS\u00e3o tuas nossas vidas,\nGanhaste-as sobre a cruz.\n\u00c9 v\u00e3o qualquer trabalho\nSem tua aprova\u00e7\u00e3o!\nO nosso esfor\u00e7o \u00e9 falho\nSe n\u00e3o nos d\u00e1s a m\u00e3o", "N\u00f3s crentes redimidas,\nDepomos nosso lar\nE as nossas pr\u00f3prias vidas\nPerante o teu altar.", "Se a nossa f\u00e9 se abala\nEm face \u00e0s tenta\u00e7\u00f5es,\nLevanta a voz e fala\nAos nossos cora\u00e7\u00f5es.\nA experi\u00eancia viva\nDo teu fiel amor\nO nosso ardor ativa\nE inspira em n\u00f3s fervor.", "Esposas, m\u00e3es piedosas,\nQueremos ser, Senhor\nFi\u00e9is e carinhosas,\nEnchendo o lar de amor.\nQue paz e harmonia\nDominem nosso lar,\nE em tua companhia\nPossamos sempre andar.", "Escola de piedade\nQue eleva a Igreja a ti,\nSeja esta sociedade\nAo teu servi\u00e7o aqui.\nE o mundo despertado,\nContemplar\u00e1 em n\u00f3s\nRebanho santo e amado,\nAtento \u00e0 tua voz. Am\u00e9m."]'::json, NULL)
        ,
        ('326'::text, ' Homens presbiterianos'::text, '["F\u00e9\nHomens presbiterianos do Brasil\nTestemunhas de Jesus, o Salvador!\nLevantemo-nos, com f\u00e9 e mui vigor,\nCom mensagem varonil!\nO Senhor nos manda a todos proclamar\nO valor do sangue que por n\u00f3s verteu\nL\u00e1 na cruz onde, bondoso, padeceu\nPara a gl\u00f3ria nos levar!", "Sim, lutemos por Cristo Jesus,\nApontando aos descrentes ateus\nO caminho repleto de luz,\n\u00d3 var\u00f5es santos, filhos de Deus.", "Ora\u00e7\u00e3o\nHomens presbiterianos do Brasil\nNa ora\u00e7\u00e3o teremos perenal poder!\nE faremos sempre a B\u00edblia resplender\nNesta P\u00e1tria senhoril!\nCom Jesus, o nosso grande e bom pa\u00eds\nViver\u00e1 em paz, e, olhando o seu porvir,\nTer\u00e1 b\u00ean\u00e7\u00e3o copiosa a espargir,\nE o Brasil ser\u00e1 feliz!", "Trabalho\nHomens presbiterianos do Brasil\nQue integramos vasta Confedera\u00e7\u00e3o,\nCom valor e piedosa voca\u00e7\u00e3o\nTrabalhemos, um por mil,\nNa grandiosa obra de Jesus, Senhor,\nPoderosa, forte, bela e triunfal,\nArvorando, com amor, pend\u00e3o real,\nExaltando seu labor!"]'::json, NULL)
        ,
        ('327'::text, ' Obreiros crist�os'::text, '["Aqui reunidos, queremos, \u00f3 Deus,\n\u00c0 causa da Igreja servir.\nNa santa seara, a n\u00f3s, servos teus,\nOh, mostra, Senhor, como agir.\nAos teus mensageiros, oh, vem, Salvador,\nTornar cada vez mais fi\u00e9is!\nE d\u00e1-lhes amparo e sustento, Senhor,\nEm tempos t\u00e3o maus e cru\u00e9is.", "Nas lutas, liberta-os de toda paix\u00e3o\nE d\u00e1-lhes o amor eficaz,\nO amor que congra\u00e7a, que faz a uni\u00e3o,\nO amor do \u201cEvangelho da Paz.\u201d\nDe vis preconceitos e impulsos carnais\nVem sempre os teus servos guardar,\nUnindo-os no afeto de amigos leais,", "Fazendo-os em paz trabalhar.\nOh, d\u00e1 \u00e0s mensagens mais alto poder;\nA todos inspira na cruz!\nSenhor, aos teus servos concede o saber,\nMais f\u00e9, mais lampejos de luz!\nDos falsos obreiros a ast\u00facia do mal\nAfasta, Senhor, com poder.\nE \u00e0 boa semente do santo trigal\nPermite nascer e crescer! Am\u00e9m."]'::json, NULL)
        ,
        ('328'::text, ' Deus do universo'::text, '["Deus do universo, de leis imut\u00e1veis,\nQue nos sust\u00e9m na luta terreal,\nD\u00e1-nos marchar em meio \u00e0s tuas hostes,\nPara servir na luta divinal.", "Filho de Deus, por ele aqui mandado\n\u00c9s com o Pai o mesmo Deus de amor!\nUne os teus servos para o teu servi\u00e7o\nSim, vem unir-nos, pois, em ti, Senhor.", "\u00d3 Santo Esp\u00edrito, divino Mestre,\nA s\u00e3 doutrina vem nos ensinar!\nTraze ao aflito e triste o teu consolo,\nE em meio \u00e0s trevas faze a luz brilhar.", "\u00d3 Deus Tri\u00fano, Deus de toda a gra\u00e7a,\nTeus pensamentos qu\u00e3o grandiosos s\u00e3o!\nTeu povo almeja estar contigo sempre;\n\u201cVenha o teu reino\u201d - \u00e9 a nossa peti\u00e7\u00e3o."]'::json, NULL)
        ,
        ('329'::text, ' Instala��o de pastor'::text, '["Senhor da Igreja, atende\nA nossa peti\u00e7\u00e3o!\nQue o teu trabalho siga\nCom grande anima\u00e7\u00e3o.\nOs campos j\u00e1 branquejam,\nConvidam a ceifar\nE os preciosos frutos\nNa Igreja a arrecadar.", "A ti, Senhor, compete\nCeifeiros escolher.\nQue tudo realizem\nConforme o teu querer.\nOs \u00e2nimos prepara,\nInflama os cora\u00e7\u00f5es\nE manda os bons obreiros\nEm grandes multid\u00f5es.", "Se aquele que escolhemos\nMandado foi por ti,\nSeu santo minist\u00e9rio\nConduze sempre aqui.\nConfirma o pastorado\nCom b\u00ean\u00e7\u00e3os especiais\nE d\u00e1-lhe, em ricos frutos,\nDivinas credenciais.", "Alenta-lhe a esperan\u00e7a,\nAumenta nele a f\u00e9.\nNa lida, n\u00e3o permitas\nQue lhe vacile o p\u00e9.\nE cada vez mais forte,\nMais cheio de fervor,\nA todos manifeste\nA gra\u00e7a do Senhor. Am\u00e9m."]'::json, NULL)
        ,
        ('33'::text, ' Maravilhas Divinas'::text, '["Ao Deus de amor e de imensa bondade,\nCom voz de j\u00fabilo, vinde aclamai!\nCom cora\u00e7\u00e3o transbordante de gra\u00e7as,\nSeu grande amor, todos, vinde louvai.", "No c\u00e9u, na terra, que maravilhas\nVai operando o poder do Senhor!\nMas seu amor, aos homens perdidos,\nDas maravilhas \u00e9 sempre a maior.", "J\u00e1 nossos pais nos contaram a hist\u00f3ria\nDe Deus, falando com muito prazer,\nQue nas tristezas, nos grandes perigos,\nEle os salvou por seu grande poder.", "Hoje tamb\u00e9m, n\u00f3s bem alto, cantamos\nQue as ora\u00e7\u00f5es ele nos atendeu:\nSeu forte bra\u00e7o, que \u00e9 t\u00e3o compassivo\nEm nosso aux\u00edlio ele sempre estendeu.", "Como at\u00e9 hoje, e daqui para frente,\nEle ser\u00e1 nosso eterno poder,\nNosso castelo bem forte e seguro,\nE nossa Fonte de excelso prazer."]'::json, NULL)
        ,
        ('330'::text, ' A B�n��o do batismo'::text, '["\u00d3 Jesus, eu te confesso,\nPara sempre \u00e9s meu Senhor\nObediente, aqui desejo\nPertencer - te, \u00f3 Salvador.", "Sendo agora batizado,\nTomo, enfim, o meu lugar\nEntre o povo que o teu sangue\nVeio l\u00e1 na cruz comprar.", "Para o mundo, agora., eu morto,\nTenho vida em ti, Jesus,\nJ\u00e1 nas trevas n\u00e3o vacilo,\nPois caminho em plena luz.", "Sendo nova criatura,\nSantidade buscarei,\nPela qual o mundo veja\nQue Tu \u00e9s Senhor e Rei.", "Fortalece ao meu anseio\nDe te ser fiel aqui,\nPois minha alma s\u00f3 aspira\nA viver de ti em ti. Am\u00e9m."]'::json, NULL)
        ,
        ('331'::text, ' Ora��o'::text, '["Confirma, \u00f3 Salvador,\nA decis\u00e3o feliz\nDe quem, por teu amor,\nDeixar o mundo quis,\nE agora vem se batizar,\nSubmisso e humilde ao teu mandar.", "Aperfei\u00e7oa em paz\nE em teu divino amor,\nO cora\u00e7\u00e3o que faz\nTal confiss\u00e3o, Senhor!\nE em tudo faze o meu viver\nConforme, sempre, o teu querer!", "\u00d3 protetor fiel,\nAmparador dos teus,\nDo mundo no tropel\nConduz os passos meus!\nNas tenta\u00e7\u00f5es s\u00ea Tu, Senhor,\nO meu constante Defensor.", "Nos sentimentos bons,\nNa comunh\u00e3o veraz,\nRevela os ricos dons\nQue o teu poder nos traz;\nMostrando ao mundo, assim, Jesus,\nQue \u00e9 bom andar na tua luz. Am\u00e9m."]'::json, NULL)
        ,
        ('332'::text, ' Batismo infantil'::text, '["Nossos filhos te pertencem,\n\u00d3 eterno Deus de amor!\nS\u00e3o herdeiros da alian\u00e7a\nQue proclama teu amor,\nE os trouxemos hoje aqui\nPara os dedicar a ti.", "Desde os dias mais remotos,\nDesde os tempos de Abra\u00e3o,\nDo antigo testamento,\nTempo da circuncis\u00e3o,\nO que nasce nesta f\u00e9\nDo Senhor ovelha \u00e9.", "Ao traz\u00ea-los ao batismo,\nEis-nos a testemunhar\nQue pertencem \u00e0 Igreja,\nOnde os vimos alistar:\nQue pertencem ao Senhor\nSeu amigo, Salvador.", "Nesta hora t\u00e3o solene,\nQuando a \u00e1gua batismal\nSe derrama nos infantes\nCom valor sacramental,\nTe pedimos, grande Deus,\nAben\u00e7oa-os l\u00e1 dos c\u00e9us. Am\u00e9m."]'::json, NULL)
        ,
        ('333'::text, ' O batismo'::text, '["N\u00f3s, Senhor, nos alegremos\nAo teu mando obedecer.\nPois tu mesmo nos mandaste\nO batismo receber.\nVem, agora, Deus de amor,\nConceder-nos teu favor.", "Este selo nos recorda\nMandamento do Senhor.\n\u00c9 Figura que nos fala\nDo poder renovador.\nVem por ele a Igreja unir\nQuem te pode aqui seguir.", "Morte ao mundo declaramos,\nMorte ao vil pecado sim!\nCom Jesus ao nosso lado\nA vit\u00f3ria \u00e9 certa, enfim!\n\u00d3 Senhor, vem consagrar\nA quem vem se batizar.", "Mortos para o mundo estamos,\nDesejando a Deus servir.\nVivos em Jesus queremos\nSua imagem refletir.\nVem, Senhor, aqui fazer\nTua gra\u00e7a em n\u00f3s crescer. Am\u00e9m."]'::json, NULL)
        ,
        ('334'::text, ' A convers�o'::text, '["Em cegueira eu andei e perdido vaguei\nLonge, longe do meu Salvador!\nMas da gl\u00f3ria desceu, o seu sangue verteu\nE salvou este pobre pecador.", "Foi na cruz, foi na cruz que um dia eu vi\nMeu pecado castigado em Jesus!\nFoi ali pela f\u00e9, que meus olhos abri\nE agora me alegro em sua luz!", "J\u00e1 ouvia falar dessa gra\u00e7a sem par,\nQue do c\u00e9u trouxe Cristo Jesus!\nMas eu surdo me fiz, converter-me n\u00e3o quis\nAo Senhor que por mim morreu na cruz.", "Mas um dia senti meu pecado, e vi\nSobre mim o castigo da Lei!\nApressado fugi, em Jesus me escondi,\nE abrigo seguro nele achei.", "Que ditoso, ent\u00e3o, foi o meu cora\u00e7\u00e3o,\nConhecer o excelso amor,\nQue levou meu Jesus a sofrer l\u00e1 na cruz,\nE salvar este pobre pecador."]'::json, NULL)
        ,
        ('335'::text, ' J�bilo no c�u'::text, '["Oh, que belos hinos cantam l\u00e1 no c\u00e9u,\nPois do mundo o filho mau voltou!\nVede o Pai celeste prestes a abra\u00e7ar\nEsse filho que ele tanto amou!", "Gl\u00f3ria! Gl\u00f3ria! Os anjos cantam l\u00e1!\nGl\u00f3ria! Gl\u00f3ria! As harpas tocam j\u00e1!\n\u00c9 o santo coro dando gl\u00f3ria a Deus,\nPor mais um remido entrar nos c\u00e9us!", "Oh que belos hinos cantam l\u00e1 no c\u00e9u!\n\u00c9 que j\u00e1 se reconciliou\nA rebelde alma que, rendida a Deus,\nRenascida, para l\u00e1 voltou!", "Este arrependido vamos festejar,\nComo os anjos fazem com fervor.\nE anunciemos, com real prazer,\nQue se resgatou um pecador."]'::json, NULL)
        ,
        ('336'::text, ' Transforma��o'::text, '["Eu, perdido pecador,\nLonge do meu Jesus\nMe encontrava, sem vigor,\nA perecer sem luz.\nMeu estado Cristo viu,\nDando-me sua m\u00e3o,\nE salvar-me conseguiu\nDa perdi\u00e7\u00e3o.", "Cristo me amou e me livrou!\nO seu imenso amor me transformou!\nFoi seu poder, o seu querer!\nSim, Cristo, Salvador, me transformou!", "Minha vida, todo o ser,\nQuero lhe consagrar!\nAo seu lado vou viver,\nO seu amor cantar.\nA mensagem transmitir\nAos que perdidos s\u00e3o!\nVenham todos j\u00e1 fruir\nA salva\u00e7\u00e3o."]'::json, NULL)
        ,
        ('337'::text, ' Profiss�o de f�'::text, '["O C\u00e9u festeja a convers\u00e3o\nDe mais um pecador!\nExulta a Igreja de prazer;\n\u00c9 dos de esplendor!", "Sustenta \u00f3 Deus o pecador\nQue o mundo quis deixar!\nAcolhe o nosso novo irm\u00e3o\nQue a f\u00e9 vem professar.", "Confirma em teu poder, Senhor,\nA santa profiss\u00e3o,\nE a cerim\u00f4nia a relembrar\nO sangue da aspers\u00e3o", "Consolador divino, vem\nAs almas converter!\nVem muitas almas batizar,\nExerce o teu poder! Am\u00e9m."]'::json, NULL)
        ,
        ('338'::text, ' Dia da profiss�o de f�'::text, '["Salve, dia de festa e canto\nQuando venho a f\u00e9 professar\nEm Deus Pai, no Esp\u00edrito Santo,\nE em Jesus que veio me salvar", "Salve, tempo que evoca o passado,\nQuando a m\u00e3o do meu Deus me tomou!\nEle, Amigo sem par, ao meu lado\nCom amor fraternal me guiou!", "Salve, data de alegre mem\u00f3ria,\nDe conv\u00edvio, de amor fraternal!\nSalve, dia em que canto vit\u00f3ria\nContra as f\u00farias tremendas do mal."]'::json, NULL)
        ,
        ('339'::text, ' Dia feliz'::text, '["Oh, dia alegre em que aceitei\nJesus e nele a salva\u00e7\u00e3o!\nO gozo deste cora\u00e7\u00e3o\nEu mais e mais publicarei.", "Oh, feliz, bem feliz,\nO dia em que me converti!\nJesus me ensina a vigiar\nE, confiado nele, a orar!\nOh, feliz, bem feliz,\nO dia em que me converti1", "Completa a grande expia\u00e7\u00e3o,\nPerten\u00e7o agora ao meu Senhor!\nChamou-me a voz do seu amor\nE nele achei real perd\u00e3o!", "Me sacro voto, \u00f3 Salvador,\nDe dia em dia afirmarei!\nE al\u00e9m da morte exultarei,\nLouvando sempre a ti, Senhor"]'::json, NULL)
        ,
        ('34'::text, ' Convite ao louvor'::text, '["Demos com alegre som\nGl\u00f3ria a Deus, porque ele \u00e9 bom.", "Sua gra\u00e7a Deus nos d\u00e1\nE jamais nos deixar\u00e1", "Com poder ele ordenou\nE no mundo a luz raiou.", "Com olhar de compaix\u00e3o\nViu a nossa perdi\u00e7\u00e3o.", "O seu povo aben\u00e7oou,\nNo deserto ele o guiou.", "Demos com alegre som\nGl\u00f3ria a Deus, porque ele \u00e9 bom."]'::json, NULL)
        ,
        ('340'::text, ' Santa comunh�o'::text, '["Disposta a mesa, \u00f3 Salvador,\nEst\u00e1s presente aqui!\nMinistra o vinho, parte o p\u00e3o,\nTipos, Jesus, de ti.", "Juntos, lembramos tua cruz;\nPor n\u00f3s sofreste ali.\nPor tua gra\u00e7a divinal,\nVivemos para ti.", "Desperta, anima, enleva os teus,\nFazendo-os discernir\nQue Tu presente, \u00f3 Cristo, est\u00e1s\nTeu povo a dirigir.", "Na Santa Ceia, \u00f3 grande Deus,\nBuscamos comunh\u00e3o\nContigo, nosso Benfeitor,\nCom todo vero irm\u00e3o.", "Sabemos que regressar\u00e1s\nEm majestade e luz!\nJuiz Supremo, eterno Rei,\nOh, vem, Senhor Jesus! Am\u00e9m."]'::json, NULL)
        ,
        ('341'::text, ' Vera p�scoa'::text, '["\u00d3 Jesus, \u00f3 vera P\u00e1scoa\nSuspirada dos antigos!\n\u00d3 Cordeiro eterno e meigo,\nDigna-te assistir aqui!", "Bom Jesus, \u00f3 P\u00e3o divino,\nPela f\u00e9 te recebemos.\n\u00c9s nas almas o alimento\nQue sustenta o nosso amor.", "Bom Jesus, \u00f3 Vinho puro\nDe perene gozo a fonte,\nFaze que nossa alma viva\nPara ti, sempre de ti. Am\u00e9m."]'::json, NULL)
        ,
        ('342'::text, ' Comunh�o'::text, '["Atendendo ao que mandaste,\nRelembramos, \u00f3 Jesus,\nO teu grande sacrif\u00edcio\nConsumado sobre a cruz.", "Monumento precioso,\nOnde esplende o teu amor,\nEsta ceia \u00e9 simbolismo\nDo teu gesto redentor.", "Recordando o que sofreste,\nAguardamos o porvir,\nQuando em majestade e gl\u00f3ria,\nComo o sol vais refulgir.", "Que por meio desta Ceia\nN\u00f3s cres\u00e7amos em poder;\nO Poder com que os remidos\nTodo o mal ir\u00e3o vencer. Am\u00e9m."]'::json, NULL)
        ,
        ('343'::text, ' Em mem�ria'::text, '["Levado pelo imenso amor\nQue Tu me tens, Senhor,\n\u00c0 tua Mesa, \u00f3 Salvador,\nLembrar-me-ei de ti!", "Por meu pecado, sobre a cruz,\nSofreste, meu Jesus,\nMeu Salvador e minha luz,\nLembrar-me-ei de ti!", "Na cruz teu corpo sucumbiu,\nNa morte que o feriu.\nA mim teu sangue redimiu!\nLembrar-me-ei de ti!", "E quando a morte, enfim, chegar,\nJesus, ao doce lar,\nOs teus fi\u00e9is vir\u00e1s chamar;\nLembrar-te-\u00e1s de mim. Am\u00e9m."]'::json, NULL)
        ,
        ('344'::text, ' A ceia do senhor'::text, '["Eis-me aqui, Senhor bondoso,\nTua Ceia a celebrar,\nE por ela neste instante,\nTua morte anunciar.", "De teu sangue e tua carne,\nPela f\u00e9 j\u00e1 me nutri,\nPois da vida o p\u00e3o me deste,\nQuando em ti, Jesus, eu cri.", "Proclamando a tua morte,\nEu relembro o grande amor\nQue inspirou teu sacrif\u00edcio\nPelo pobre pecador.", "Que o amor aqui lembrado\nVenha encher o cora\u00e7\u00e3o\nDos que jazem separados\nDesta doce comunh\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('345'::text, ' O p�o do mundo'::text, '["\u00d3 P\u00e3o, s\u00f3 por amor partido!\n\u00d3 Vinho, dado por amor!\n\u00d3 Cristo, vida nos tens sido,\nPor tua morte em grande dor.", "Ao pecador atribulado\nVens, mesmo agora, consolar.\nDe tua Ceia, \u00f3 Cristo amado,\nNossa alma vem alimentar. Am\u00e9m."]'::json, NULL)
        ,
        ('346'::text, ' A ceia do Senhor'::text, '["N\u00e3o nas m\u00e3os, mas em minha alma,\nTomo o corpo de Jesus,\nE, em figura, bebo o sangue\nDerramado sobre a cruz.", "Do Senhor, o excelso Mestre,\nComemoro o grande amor,\nProclamando a sua morte\nPelo pobre pecador.", "Vem Jesus, Senhor bondoso,\nMeu esp\u00edrito instruir,\nPara que, nos dois emblemas,\nEu te possa discernir.", "E permite que hoje eu tenha,\nNo fruir da salva\u00e7\u00e3o,\nCom os crentes e contigo\nVerdadeira comunh\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('347'::text, ' O justo pelos injustos'::text, '["\u00d3 crentes, cantai entoando o louvor\nDe quem nos remiu com t\u00e3o grande amor!\nOs crimes do mundo, levando na cruz,\nPor nossos pecados foi morto Jesus.", "O pre\u00e7o completo o Justo pagou!\nFoi morto e da morte os la\u00e7os quebrou;\nE as trevas da noite tornaram-se m luz,\nNo dia bendito de nosso Jesus.", "Imagem do c\u00e9u! Oh, dia primor!\nMerc\u00ea divinal do grande Senhor!\nQu\u00e3o doce descanso Jesus nos legou,\nNo santo Domingo em que ressuscitou!", "Agora se cumpre em n\u00f3s, bom Senhor,\nA linda promessa de teu amor:\nDe que, congregados, Tu sempre estar\u00e1s\nPresente, trazendo-nos b\u00ean\u00e7\u00e3os e paz. Am\u00e9m."]'::json, NULL)
        ,
        ('348'::text, ' � este o dia'::text, '["\u00c9 este o dia que o bom Deus\nCriou com sua m\u00e3o!\nA terra inteira, os altos c\u00e9us,\nLouvor alegres d\u00e3o.", "Foi neste dia que o Senhor\nDa tumba ressurgiu!\nDe Satan\u00e1s, o atroz furor\nCom for\u00e7a repeliu!", "Vinde, louvai ao Rei - Senhor,\nAo Filho de Davi!\nVem ajudar-nos, Salvador,\nE habita em n\u00f3s aqui.", "Gl\u00f3ria louvor e adora\u00e7\u00e3o\nA Cristo vimos dar!\nOs altos c\u00e9us se alegrar\u00e3o,\nLouvando-o sem cessar!"]'::json, NULL)
        ,
        ('349'::text, ' Dia do Senhor'::text, '["Hoje \u00e9 dia do Senhor!\nEntoai o seu louvor\nE adorai-o com fervor.\nGl\u00f3ria ao nosso Deus!", "Hoje a casa de ora\u00e7\u00e3o\nProcuramos, em uni\u00e3o,\nE gozamos comunh\u00e3o\nCom o nosso Deus!\nHoje Cristo, ao pregador\nQue anuncia o seu amor,\nD\u00ea a prova do favor:\nO poder de Deus!", "Hoje \u00e9 dia de perd\u00e3o!\nDeus convida o cora\u00e7\u00e3o\nA aceitar a Reden\u00e7\u00e3o;\nGl\u00f3ria ao nosso Deus!"]'::json, NULL)
        ,
        ('35'::text, ' Adora��o e s�plica'::text, '["Teu culto agora, aqui, Senhor,\nN\u00f3s vimos celebrar,\nCantando, juntos, teu louvor,\nTeu livro a meditar.", "\u00c0 nossa vera adora\u00e7\u00e3o\nPresente est\u00e1s, Senhor.\nFazendo o nosso cora\u00e7\u00e3o\nSentir teu grande amor.", "Atende nossas peti\u00e7\u00f5es,\n\u00d3 poderoso Deus!\nConcede aos nossos cora\u00e7\u00f5es\nAntegozar os c\u00e9us. Am\u00e9m."]'::json, NULL)
        ,
        ('350'::text, ' A palavra da vida'::text, '["Fonte da Celeste Vida,\nManifesta o teu poder.\nVivifica os sem alento,\nFaze os mortos renascer!\nVida eterna, vida eterna\nNos vieste conceder.", "Ao abrirmos o teu Livro,\nDele emane a luz dos c\u00e9us!\nEsclarece todo engano\nE dos erros livra os teus,\nIlumina, ilumina\nNossas almas grande Deus.", "Na leitura desta b\u00edblia,\nD\u00e1-nos gozo, \u00f3 Senhor!\nTendo, pelo teu ensino,\nComunh\u00e3o em santo amor.\nExultamos, exultamos\nEntoando o teu louvor.", "Pelo estudo da Palavra\nAprendemos de Jesus.\nOh, concede os belos frutos\nQue esta instru\u00e7\u00e3o produz!\nE gozamos, e gozamos\nAlegria, vida e luz."]'::json, NULL)
        ,
        ('351'::text, ' Belas palavras de vida'::text, '["Quero ouvi-las mais outra vez,\nBelas palavras de vida!\nNarram tudo o que Cristo fez,\nBelas palavras de vida!\nElas v\u00eam de cima,\nSeu poder anima.", "Que alegres s\u00e3o! Que alegres s\u00e3o!\nEssas palavras de vida!\nQue alegres s\u00e3o! Que alegres s\u00e3o!\nEssas palavras de vida!", "S\u00f3 Jesus Cristo a todos d\u00e1\nBelas palavras de vida!\nSem Jesus salva\u00e7\u00e3o n\u00e3o h\u00e1;\nBelas palavras de vida!\nCom amor conclama,\nPara o c\u00e9u te chama.", "S\u00f3 Jesus Cristo \u00e9 Salvador!\nBelas palavras de vida!\nQuer salvar todo pecador!\nBelas palavras de vida!\nEle vivifica\nE nos purifica."]'::json, NULL)
        ,
        ('352'::text, ' Leitura bendita'::text, '["Enquanto, \u00f3 Salvador, teu Livro ler,\nDe aux\u00edlio necessito para ver\nDa mera letra, al\u00e9m, a ti, Senhor,\nE meditar no teu excelso amor.", "\u00c0 beira-mar, Jesus, partiste o p\u00e3o,\nAlimentando a imensa multid\u00e3o.\nDa vida o p\u00e3o \u00e9s Tu ; podes assim\nSatisfazer, Senhor, tamb\u00e9m a mim. Am\u00e9m."]'::json, NULL)
        ,
        ('353'::text, ' Ao fim dos estudos'::text, '["Findo o tempo dos estudos,\nEis-nos grande Instruidor!\nLevantamos nossas vozes,\nTributamos-te louvor.\nE pedimos, e pedimos\nB\u00ean\u00e7\u00e3os de celeste amor.", "Oh, concede o crescimento\nNa ci\u00eancia e no vigor!\nImprimindo na mem\u00f3ria\nTeus preceitos, \u00f3 Senhor.\nEste ensino, este ensino\n\u00c9 de mui real valor.", "\u00d3 Senhor, em nossos lares\nManifesta o teu poder.\nE que o teu divino livro\nMais possamos conhecer!\nDesejamos, desejamos\nSempre em tua luz viver. Am\u00e9m."]'::json, NULL)
        ,
        ('354-A'::text, ' Escola dominical'::text, '["Vamos todos \u00e0 Escola Sagrada\nA Palavra de Deus aprender.\nA verdade dos c\u00e9us revelada\nD\u00e1-nos luz! D\u00e1-nos vida e poder!", "Trabalhemos pela Escola,\nEstudando com amor!\nE pregando Jesus Cristo,\nNosso Mestre e Salvador.", "Vamos todos, crian\u00e7as e adultos,\nPalmilhando o caminho da cruz.\nEstudar a grandeza dos vultos,\nEntre os quais predomina Jesus.", "Propaguemos com f\u00e9, corajosos,\nDesta Escola o sublime ideal.\nE veremos um dia, ditosos,\nA vit\u00f3ria do bem sobre o mal."]'::json, NULL)
        ,
        ('354'::text, ' A escola dominical'::text, '["Dominical \u00e9 a grande e antiga Escola\nEm que se estuda o Livro do Senhor;\nA vida aqui se exalta e se acrisola\nE alcan\u00e7a em Cristo seu real valor,\nE alcan\u00e7a em Cristo seu real valor.", "\u00c0 Escola, pois, Dominical, irm\u00e3os,\nReceber celeste luz!\nO santo livro, aberto em nossas m\u00e3os,\nAprendamos de Jesus.", "De grandes vultos e eras memor\u00e1veis\nLi\u00e7\u00f5es sublimes vimos aprender!\nLi\u00e7\u00f5es inscritas nalma, inapag\u00e1veis,\nQue moldam nossas vidas com poder,\nQue moldam nossas vidas com poder.", "O Mestre insigne est\u00e1 presente - Cristo -,\nQue luz e vida \u00e0 vida plena traz.\nO seu ensino igual jamais foi visto!\nTesouros mostra, de alegria e paz,\nTesouros mostra, de alegria e paz.", "Universal, bendita Escola \u00e9 esta,\nQue os seus alunos conta por milh\u00f5es;\nNa qual o estudo \u00e9 verdadeira festa,\nQue empolga crian\u00e7as, jovens e anci\u00e3os,\nQue empolga crian\u00e7as, jovens e anci\u00e3os,"]'::json, NULL)
        ,
        ('355'::text, ' Ora��o infantil'::text, '["Amigo dos meninos,\nBenigno Salvador!\nConosco est\u00e1 presente,\nQuerido e Bom Pastor!\nTeus cordeirinhos guia\nCom tua compaix\u00e3o,\nE d\u00e1-nos para sempre\nUm reto cora\u00e7\u00e3o.", "Teus santos mandamentos,\nOh, faze-nos amar!\nE tudo que \u00e9 pecado\nDe n\u00f3s vem afastar.\nConcede nos estudos\nQue temos hoje aqui,\nSejamos instru\u00eddos,\n\u00d3 grande Deus, por ti. Am\u00e9m."]'::json, NULL)
        ,
        ('356'::text, ' Jesus menino'::text, '["Foi tamb\u00e9m Jesus, um dia,\nComo eu, pequeno, assim,\nMas em tudo se fazia\nUm modelo para mim.\nNa bondade e no cuidado,\nNo constante obedecer,\nComo foi o mestre amado\nEu tamb\u00e9m desejo ser.", "Sou pequeno mui fraquinho,\nE estou sempre a errar!\nMas Jesus, em meu caminho,\nBem me pode auxiliar.\nTernamente me corrige,\nMe recorda o que conv\u00e9m.\nSeu amor meus p\u00e9s dirige\nPara andar em todo bem.", "Teus ensinos Mestre amado,\nQuero atento aqui ouvir,\nE assim, por ti guiado,\nTua lei, fiel, cumprir.\nAo teu lado sempre estando,\nQuero andar na tua luz,\nMinha vida consagrando\nS\u00f3 a ti, Senhor Jesus! Am\u00e9m."]'::json, NULL)
        ,
        ('357'::text, ' Louvor infantil'::text, '["Filhos de Jerusal\u00e9m\nDavam a Jesus louvor!\nCantaremos n\u00f3s, tamb\u00e9m,\nSeu excelso e doce amor!", "Ouve! Ouve! Ouve! Os meninos d\u00e3o louvor!\nOuve! Ouve! Ouve! Os meninos d\u00e3o louvor!\nAleluia! Aleluia! Aleluia ao Salvador!", "Gra\u00e7as ao divino Rei\nQue, no mundo, quis viver!\nGra\u00e7as pela santa Lei\nQue declara o seu querer!", "Grande \u00e9 o nosso Salvador,\nToda a d\u00edvida pagou!\nPela morte o bom Pastor\nSeu rebanho resgatou."]'::json, NULL)
        ,
        ('358'::text, ' Jesus e as crian�as'::text, '["Congrega\u00e7\u00e3o\nA Jesus crian\u00e7as vinham\nSua b\u00ean\u00e7\u00e3o suplicar.\nV\u00f3s, que agora sois crian\u00e7as,\nVinde a ele, pois, louvar.", "Se crian\u00e7as de outro tempo\nAceitou com terno amor,\nHoje a todas as crian\u00e7as\nSalva, ainda,com favor.", "Crian\u00e7as\nMeus pecados no Calv\u00e1rio,\nSobre a cruz Jesus pagou!\nA maior das maravilhas\n\u00c9 que Cristo me amou", "Minhas m\u00e3os, t\u00e3o pequeninas,\nSe ergam sempre em teu louvor,\n\u00d3 Jesus, Senhor bendito,\nPor salvar-nos com amor! Am\u00e9m."]'::json, NULL)
        ,
        ('359'::text, ' Jesus me quer bem'::text, '["Sei que Cristo me quer bem,\nPois na B\u00edblia assim o diz.\nFr\u00e1gil sou, mas for\u00e7a tem;\nQuer levar-me ao bom Pa\u00eds.", "Sei que me quer bem,\nQuer ver-me feliz,\nSei que me quer bem\nA B\u00edblia assim o diz.", "Tudo fez Jesus por mim,\nS\u00f3 por ele vou viver.\nE porque me quer assim,\nHei de am\u00e1-lo at\u00e9 morrer!"]'::json, NULL)
        ,
        ('36'::text, ' Exalta��o'::text, '["De toda a terra e das na\u00e7\u00f5es,\nLouvor a Cristo entoai!\nEm alta voz, dos cora\u00e7\u00f5es\nO Nome de Jesus cantai.", "O dom supremo, divinal,\nJusti\u00e7a eterna e santo amor,\nDe litoral em litoral,\nSer\u00e3o cantados ao Senhor", "Em toda l\u00edngua bendizei\nA Deus, Autor da Cria\u00e7\u00e3o!\nEm toda terra engrandecei\nJesus que trouxe a Reden\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('360'::text, ' Pequena luz'::text, '["No mundo, pequenina luz\nDe Deus eu quero ser,\nA refletir, do meu Jesus,\nSeu brilho, seu poder.", "Em casa pequenina flor,\nQue aos pais possa alegrar,\nE do celestial Cultor\nO aroma exalar.", "Na escola, pequenina m\u00e3o\nQue aceite com prazer\nO farto, nutritivo p\u00e3o,\nDa ci\u00eancia e do saber.", "Na Igreja, pequenina voz\nQue louve ao Salvador,\nE nunca, ao fim da vida at\u00e9,\nMe afaste do Senhor"]'::json, NULL)
        ,
        ('361'::text, ' Brilhando com Jesus'::text, '["Manda-nos luzir o Senhor Jesus,\nComo vela acesa d\u00e1 de noite a luz!\nQuer que n\u00f3s brilhemos como a luz do c\u00e9u:\nTu no teu cantinho e eu no meu!", "Cristo sempre a luz para si requer,\nPercebendo logo se ela enfraquecer.\nSempre a luz mostremos, que Jesus nos deu:\nTu no teu cantinho e eu no meu!", "Ao redor, ent\u00e3o, manda a luz brilhar\nPara as densas trevas hoje dissipar.\nCom Jesus brilhemos, pois nos escolheu:\nTu no teu cantinho e eu no meu!"]'::json, NULL)
        ,
        ('362'::text, ' Brilhando por Jesus'::text, '["Vejo no c\u00e9u, esplendente,\nDo sol a clara luz!\nQuero viver t\u00e3o somente\nBrilhando por Jesus.", "Brilhando, brilhando,\nBrilhando qual doce luz!\nBrilhando, brilhando,\nBrilhando por meu Jesus!", "Quero na vida exalt\u00e1-lo\nNa escola e no estudar.\nQuero tamb\u00e9m imit\u00e1-lo\nEm casa e no brincar.", "Am\u00e1vel com toda gente\nAssim me quer Jesus!\nDe rosto alegre e contente\nBrilhando como a luz.", "O feio e triste pecado\nAjuda-me a vencer!\nTendo Jesus ao meu lado,\nEu quero aqui viver."]'::json, NULL)
        ,
        ('363'::text, ' Venham as crian�as'::text, '["Venham, venham as crian\u00e7as\nAo bendito Salvador,\nQue na cruz, ao resgat\u00e1-las,\nRevelou-lhes seu favor.\nCristo agora, Cristo sempre\nNos concede seu amor.", "Venham, venham as crian\u00e7as,\nPois Jesus as convidou!\nFoi tamb\u00e9m por seus pecados\nQue na amarga cruz penou.\nCristo agora, Cristo sempre\nCom ternura nos amou.", "Venham, venham as crian\u00e7as\nAo Senhor Jesus servir!\nReceber os seus conselhos,\nSua santa Lei ouvir.\nCristo agora, Cristo sempre\nQuer na luz nos conduzir."]'::json, NULL)
        ,
        ('364'::text, ' Gra�as a Deus'::text, '["De manh\u00e3, deixando o leito,\nFa\u00e7o a Deus minha ora\u00e7\u00e3o.\nDou-lhe gra\u00e7as pela noite,\nPelo sono e prote\u00e7\u00e3o.", "Mas tamb\u00e9m, durante o dia,\nSou mui grato ao meu Senhor,\nPelo p\u00e3o, o lar e a vida,\nQue me provam seu amor.", "E depois vindo a noitinha,\nNovamente ao me deitar,\nOro a Deus, agradecido,\nDurmo em paz sem recear."]'::json, NULL)
        ,
        ('365'::text, ' Convite �s crian�as'::text, '["Oh, vinde crian\u00e7as!\nCantai a linda hist\u00f3ria\nDo bom Messias, Rei dos reis,\nJesus, o Salvador.\nE repeti, com gratid\u00e3o,\nA eterna e meiga exclama\u00e7\u00e3o:\n\u201cDeixai as crian\u00e7as que venham a mim.\u201d", "Pais crentes traziam\nA Cristo seus filhinhos,\nMas quando algu\u00e9m os impediu,\nTentando os afastar\nOuviu-se a voz do Mestre, ent\u00e3o,\nDizer com grande compaix\u00e3o:\n\u201cDeixai as crian\u00e7as que venham a mim.\u201d", "Oh, vinde crian\u00e7as!\nJesus deseja a todas\nA sua b\u00ean\u00e7\u00e3o conceder,\nE sua salva\u00e7\u00e3o!\nA voz de Cristo, o Bom Pastor,\nRepete ainda com amor:\n\u201cDeixai as crian\u00e7as que venham a mim.\u201d"]'::json, NULL)
        ,
        ('366'::text, ' J�ias preciosas'::text, '["Quando, \u00f3 Cristo, aqui vieres\nAs j\u00f3ias buscar,\nEntre as j\u00f3ias, ricas j\u00f3ias,\nTamb\u00e9m quero estar.", "Como a bela estrela da alva,\nDesejo brilhar!\nCom Jesus, na sua gl\u00f3ria,\nTamb\u00e9m quero estar!", "Vem olh\u00e1-las, vem guard\u00e1-las\nCom amor, \u00f3 Senhor!\nTodas ricas, todas lindas\nE em pleno fulgor!", "Criancinhas, criancinhas\nQue amais a Jesus,\nSois as j\u00f3ias, ricas j\u00f3ias\nCompradas na cruz."]'::json, NULL)
        ,
        ('367'::text, ' Convite aos meninos'::text, '["Vinde meninos! Vinde a Jesus!\nEle ganhou-vos b\u00ean\u00e7\u00e3os na cruz!\nOs pequeninos ele conduz,\nOh, vinde aos Salvador!", "Que alegria, sem pecado ou mal,\nReunir-nos todos afinal,\nNa santa P\u00e1tria celestial,\nCom Cristo, o Salvador!", "J\u00e1, sem demora, a todos conv\u00e9m\nIr caminhando \u00e0 gl\u00f3ria do al\u00e9m!\nCristo vos chama, quer vosso bem,\nOh, vinde ao Salvador!", "\u201cVinde meninos\u201d - ele vos diz;\nQuer receber-vos no bom Pa\u00eds;\nQuer conceder-vos vida feliz.\nOh, vinde ao Salvador!", "Eis a chamada : \u201cVinde hoje a mim!\u201d\nN\u00e3o h\u00e1 no mundo quem ame assim!\nSeu grande amor por v\u00f3s n\u00e3o tem fim.\nOh, vinde ao Salvador!"]'::json, NULL)
        ,
        ('368'::text, ' Despedida'::text, '["Deus vos guarde pelo seu poder\nPermane\u00e7a ao vosso lado,\nVos dispense o seu cuidado,\nDeus vos guarde pelo seu poder!", "Pelo seu poder e no seu amor,\nEstaremos todos com Jesus\nPelo seu poder e no seu amor,\nOh, que Deus vos guarde em sua luz.", "Deus vos guarde para o seu louvor,\nConsolados e contentes,\nAchegados sempre aos crentes,\nDeus vos guarde para o seu louvor!", "Deus vos guarde bem no seu amor.\nNo trabalho glorioso,\nPara o dia venturoso,\nDeus vos guarde bem no seu amor!"]'::json, NULL)
        ,
        ('369'::text, ' A b�blia para todos'::text, '["Eis a B\u00edblia, a Palavra\nQue de Deus prov\u00e9m!\nQuem com ela vive e lavra,\nVive para o bem!", "Anunciai a B\u00edblia aos povos!\nEla \u00e9 vida e luz!\nSeus ensinos, sempre novos,\nFalam de Jesus!", "Dessa B\u00edblia, a semente\nIde semear!\nAnunciando a toda gente\nQue Deus quer salvar!", "Mocidade, homens, velhos,\nPonde-vos de p\u00e9!\nE as li\u00e7\u00f5es dos Evangelhos\nPropagai com f\u00e9."]'::json, NULL)
        ,
        ('37'::text, ' O Santo Nome'::text, '["Jesus, teu nome \u00e9 santo,\nAm\u00e1vel teu querer.\nLouvor supremo e puro amor\nQueremos te render!\nPoder e honra e gl\u00f3ria a ti\nN\u00f3s vimos tributar,\nE com sincera devo\u00e7\u00e3o,\nTeu culto celebrar!", "Jesus, teu nome \u00e9 santo,\nMerece o nosso amor!\n\u00c9s nosso Deus, que est\u00e1s nos c\u00e9us,\nE nosso protetor.\nIncompar\u00e1vel \u00e9s, Senhor,\nEm tua compaix\u00e3o;\nQuiseste, para o mal vencer,\nFazer-te nosso irm\u00e3o!", "Jesus, teu nome \u00e9 santo,\nVenceste sobre a cruz!\nO teu sofrer, o teu penar,\n\u00c0 vida nos conduz.\nGlorificado est\u00e1s nos c\u00e9us,\nAtento \u00e0 adora\u00e7\u00e3o\nQue vimos n\u00f3s, fi\u00e9is, prestar\nCom grato cora\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('370'::text, ' Proclama��o'::text, '["A lei de Deus aos homens vamos dar.\nA cada povo, a todo cora\u00e7\u00e3o\nPregai a Cristo, seu divino amor,\nVerdade eterna, paz e retid\u00e3o.", "Queremos tua Lei, Senhor, levar\nAos que sem ti est\u00e3o a perecer!\nTeu Livro eterno o tempo atravessou,\nE o mundo inteiro, hoje, o pode ler!", "Na voz de muitos mensageiros teus,\nNo livro impresso em muitas edi\u00e7\u00f5es,\nA divinal Palavra faz-se ouvir\nAos frios, desolados cora\u00e7\u00f5es!", "O mundo a voz de Deus escutar\u00e1,\nSe com esfor\u00e7o e com dedica\u00e7\u00e3o\nNos empenharmos todos no ideal\nDe anunciar a Luz da Salva\u00e7\u00e3o!"]'::json, NULL)
        ,
        ('371'::text, ' A b�blia'::text, '["Da B\u00edblia a luz celeste\nLampeje aqui, Senhor,\nA luz que vem de Cristo,\nO Mestre e Salvador.\nTal como o sol no espa\u00e7o\nDifunde a sua luz,\nTeu livro aqui resplenda\nA gl\u00f3ria de Jesus.", "Senhor, que a tua Igreja\nRebrilhe qual fanal,\nDa B\u00edblia refletindo\nA luz celestial.\nNum proceloso mundo\nRumando \u00e0 perdi\u00e7\u00e3o,\nQue a B\u00edblia a todos mostre\nEm Cristo a salva\u00e7\u00e3o", "Concede intelig\u00eancia!\nQueremos perceber\nO que, Senhor, na B\u00edblia,\nMandaste-nos dizer.\nAdestra-nos, \u00f3 Mestre,\nCom teu Verbo eficaz!\nInstrui e repreende,\nDestr\u00f3i o que \u00e9 falaz. Am\u00e9m."]'::json, NULL)
        ,
        ('372'::text, ' O crente e a b�blia'::text, '["N\u00e3o abandono a B\u00edblia,\nPois \u00e9 a voz de Deus.\nDos crentes o tesouro,\nSeu guia para os c\u00e9us.\n\u00c9 luz divina intensa,\nNas trevas a brilhar!\n\u00c9 a voz do Pai celeste\nQue a todos vem chamar.", "N\u00e3o abandono a B\u00edblia,\nPois \u00e9 a voz de Deus!\nDos crentes o tesouro,\nSeu guia para os c\u00e9us.", "N\u00e3o abandono a B\u00edblia,\nO Livro que me diz\nQue salvo estou em Cristo,\nO que me faz feliz!\nE tenho, pois certeza\nDe l\u00e1 no c\u00e9u entrar,\nPois ele, com seu sangue,\nMe quis purificar.", "\u201cN\u00e3o abandono a B\u00edblia\u201d,\nEu sempre afirmarei.\nQuem dera ser ouvido\nPor toda a \u00edmpia grei!\nQue saiba todo o mundo,\nQue a Cristandade tem\nA f\u00e9, robusta e pura,\nQue pela B\u00edblia vem."]'::json, NULL)
        ,
        ('373'::text, ' Ano novo'::text, '["Rompe a aurora! Vai-se embora\nMais um ano e outro vem!\nN\u00e3o temamos: Prossigamos,\nCaminhando em todo o bem.", "Ano findo nunca mais veremos!\nAno novo hoje recebemos!\nV\u00ea, v\u00ea o belo dom que\nDeus nos d\u00e1.", "Raia o dia! Que alegria!\nNovo ano eis a surgir!\nBem guiados e amparados,\nCristo vai nos conduzir.\nOs talentos, nos momentos\nDeste ano a come\u00e7ar,\nConsagremos e usemos\nPara Deus glorificar."]'::json, NULL)
        ,
        ('374'::text, ' Saudando o ano novo'::text, '["Ano velho j\u00e1 findado,\nFoste o dom do Criador!\nNovo ano, que come\u00e7as,\nVens do mesmo Benfeitor.\nTestemunham,testemunham\nDesse Deus o grande amor.", "Novo ano, a tua vinda\nCelebramos com festim.\nMas teus dias fugitivos\nPrestes voam para o fim.\nIgnoramos, ignoramos,\nSe veremos outro assim.", "Tua gra\u00e7a, \u00f3 Deus, cantamos\nCom acorde e suave som!\nE com grande regozijo\nBendizendo o excelso dom\nAo saudarmos ao saudarmos\nAno novo, ano bom."]'::json, NULL)
        ,
        ('375'::text, ' Ano velho'::text, '["Ano velho j\u00e1 termina;\nDamos a Jesus louvor,\nPois do mal nos tem guardado\nNesse ano, com amor.\nNesse ano, nesse ano com amor.", "Filho eterno, a ti rogamos\nQue, por tua compaix\u00e3o,\nContinues concedendo\nAos teus servos prote\u00e7\u00e3o.\nAos teus servos, aos teus servos prote\u00e7\u00e3o.", "A Palavra preciosa\nFaze em n\u00f3s frutificar!\nDas doutrinas do Evangelho\nN\u00e3o nos deixes afastar;\nN\u00e3o nos deixes, n\u00e3o nos deixes afastar.", "O teu povo aqui dirige\nA cumprir o seu dever,\nE alegre, consagrado,\nAo teu mando obedecer,\nAo teu mando, ao teu mando obedecer.", "Do pecado nos afasta,\nNossos passos vem guiar!\nE esquecidas nossas culpas,\nUm bom ano vem nos dar.\nUm bom ano, um bom ano vem nos dar. Am\u00e9m."]'::json, NULL)
        ,
        ('376'::text, ' Intercess�o pela p�tria'::text, '["Divino Salvador,\nContempla com favor\nNosso Pa\u00eds!\nD\u00e1-nos interna paz,\nGoverno bom, capaz,\nDita que satisfaz,\nSorte feliz.", "Confiamos s\u00f3 em ti,\nVem dominar aqui,\n\u00d3 Rei dos reis!\nDirige o P\u00e1trio lar,\nEnsina a governar\nConforme o teu mandar,\nPor justas leis.", "Ao presidente, \u00f3 Deus,\nInspira desde os c\u00e9us\nO teu temor!\nQue possa bem cumprir\nO seu mandato e ouvir,\nDe todo povo aqui,\nReal louvor.", "A nossa P\u00e1tria tem\nSustento e todo bem\nDe ti Senhor!\nAos pobres d\u00e1 comer,\nAos ricos faze ver\nComo conv\u00e9m viver\nEm m\u00fatuo amor!", "Do crime e rebeli\u00e3o\nConcede a prote\u00e7\u00e3o\nQue \u00e9 divinal.\nGuardar-nos vem, Senhor,\nDe guerras e terror!\nS\u00ea nosso defensor,\nDesvia o mal.", "Poder supremo tens!\nDepara os altos bens\nDa salva\u00e7\u00e3o!", "Brilhe a benigna luz\nQue o teu favor produz!\nReine o Senhor Jesus\nSobre a na\u00e7\u00e3o! Am\u00e9m."]'::json, NULL)
        ,
        ('377'::text, ' A na��o para Cristo'::text, '["Uma voz, nos prim\u00f3rdios da hist\u00f3ria,\nFez-se ouvir com solene vigor,\nProclamando a estupenda vit\u00f3ria\nSobre a morte, o pecado e a dor!\nFoi Jesus, com palavras candentes\nDo seu verbo divino e veraz,\nOrdenando que a todas as gentes\nSe pregasse o Evangelho da paz.", "Eia, crentes, com santa ousadia\nCom nobreza e real voca\u00e7\u00e3o,\nVamos todos cantar a harmonia\nDo evangelho de amor e perd\u00e3o!\nExaltando esse amor t\u00e3o profundo,\nQue d\u00e1 vida imortal e feliz.\nPara Cristo, a esperan\u00e7a do mundo,\nConquistemos o nosso Pa\u00eds.", "Gloriosas miss\u00e3o nos foi dada\nDe ganhar para Cristo os incr\u00e9us!\nComo \u00e9 nobre esta grande cruzada\nDe elevar a Na\u00e7\u00e3o para os c\u00e9us!\nH\u00e1 no entanto, uma for\u00e7a incontida\nQue preside este af\u00e3 singular:\n\u00c9 o amor que votamos \u00e0 vi-da\nDos perdidos que Deus quer salvar.", "\u201cA Na\u00e7\u00e3o para Cristo\u201d - eis o lema,\nO anelo sublime e imortal,\nA divisa bendita e suprema,\nExpress\u00e3o do mais alto ideal!\nVamos, pois, incessantes na lida,\nEm socorro dos nossos irm\u00e3os,\nTransformar nossa P\u00e1tria querida\nNum reinado feliz de Crist\u00e3o!", "Por nossa P\u00e1tria oramos\nA ti, supremo Deus!\nPor nosso lar clamamos\nA ti, \u00f3 Rei dos C\u00e9us!\nBendize a vida pastoril,\nGoverna o brio senhoril,\nModera a lida mercantil,", "Deus salve a P\u00e1tria!", "Da P\u00e1tria que nos deste,\nDesvie tua m\u00e3o\nDesgra\u00e7as, fome e peste,\nPerf\u00eddia e sedi\u00e7\u00e3o!\nSustenta a ordem nacional,\nO bom governo imparcial,\nE d\u00e1-nos gra\u00e7a divinal:\nDeus salve a P\u00e1tria!", "D\u00e1-nos real civismo,\nFiel, constante, audaz!\nPromove o cristianismo\nDo Pr\u00edncipe da Paz!\nDa P\u00e1tria afasta as cren\u00e7as v\u00e3s,\nDerrama b\u00ean\u00e7\u00e3os tempor\u00e3s,\nDominem s\u00f3 doutrinas s\u00e3s:\nDeus salve a P\u00e1tria!", "A tua Igreja inflama\nCom zelo e terno amor.\nE seja o teu programa\nCumprido com vigor.\nEnt\u00e3o os salvos de Jesus,\nLutando firmes pela cruz,\nDifundir\u00e3o de Cristo a luz\nPor toda a P\u00e1tria! Am\u00e9m"]'::json, NULL)
        ,
        ('379'::text, ' Peti��o pela p�tria'::text, '["Minha P\u00e1tria para Cristo!\nEis a minha peti\u00e7\u00e3o.\nMinha P\u00e1tria t\u00e3o querida,\nEu te dei meu cora\u00e7\u00e3o.\nLar prezado, lar formoso,\n\u00c9 por ti o meu amor;\nQue meu Deus de excelsa gra\u00e7a\nTe conceda seu favor.", "Salve Deus a minha P\u00e1tria,\nMinha P\u00e1tria varonil!\nSalve Deus a minha terra,\nEsta terra do Brasil.", "Quero pois, com alegria\nVer feliz a m\u00e3e gentil,\nPor vencer o Evangelho\nNesta terra do Brasil.\nBrava gente brasileira,\nLonge v\u00e1 temor servil:\nOu ficar a P\u00e1tria salva,\nOu morrer pelo Brasil."]'::json, NULL)
        ,
        ('38'::text, ' Louvores sem fim'::text, '["Mil vezes mil louvores rendamos a Jesus,\nQue da mais alta gl\u00f3ria desceu at\u00e9 \u00e0 cruz!\nPor sua imensa gra\u00e7a, por seu insigne amor,\nPor todos seja sempre louvado o Salvador.", "Mil vezes, sim, mil vezes, sim,\nLouvores ao Senhor\nQue nos amou e nos salvou,\nBendito Salvador!", "Eis ao redor do trono do Redentor Jesus,\nMilhares de milhares em refulgente luz!\nCom rever\u00eancia adoram o grande Salvador,\nE ao bom Cordeiro rendem a honra e o louvor.", "Oh! Vinde agora, todos, tamb\u00e9m a celebrar\nAs gl\u00f3rias sempiternas do Redentor sem par!\nCom vozes de vit\u00f3ria seu Nome proclamai!\nMil vezes mil louvores a Cristo tributai!"]'::json, NULL)
        ,
        ('380'::text, ' Jesus proteja a p�tria'::text, '["Oh, minha P\u00e1tria amada,\nBrasil dos sonhos meus!\nDirija o teu destino\nA m\u00e3o do eterno Deus!\nQue brilhe em teu caminho\nA refulgente luz\nDo amor e da verdade,\nDa gl\u00f3ria de Jesus!", "Que o Pai dirija e guarde\nA vida nacional!\nNos livre dos perigos,\nPecado e todo mal!\nA quantos que governam\nConceda seu favor,\nE guie em paz o povo\nNas sendas do amor!", "Jesus proteja sempre\nO povo do Brasil,\nE sobre a nossa terra\nDerrame b\u00ean\u00e7\u00e3os mil!\nA gratid\u00e3o nos fa\u00e7a\nErguer o cora\u00e7\u00e3o\nEm culto fervoroso,\nEm santa adora\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('381'::text, ' Intercess�o pela cidade'::text, '["A cidade, \u00f3 Deus, protege,\nCada rua, cada lar,\nSeus cansados transeuntes,\nTodo o humano labutar.", "Guarda a todos os governantes\nE aos que fazem nossa lei;\nE que gra\u00e7as toda gente\nRenda a ti, supremo Rei!", "Aben\u00e7oa a nossa ind\u00fastria\nE os que lutam pelo p\u00e3o.\nE que n\u00f3s, Senhor, cantemos:\n\u201cTudo vem de tua m\u00e3o!\u201d", "Aben\u00e7oa aos que trabalham,\nAo fiel batalhador!\nEficientes, produtivos,\nFaze-os sempre, bom Senhor!", "Aben\u00e7oa nossa Igreja,\nNossos dias, nosso lar;\nE que assim, por toda a vida,\nN\u00f3s possamos te louvar! Am\u00e9m."]'::json, NULL)
        ,
        ('382'::text, ' Mocidade presbiteriana'::text, '["Somos jovens num mundo velho\nA pregar vivos ideais\nDo santo Evangelho\nQue pregaram nossos pais.\nO mundo muda, mas Cristo n\u00e3o!\nImporta que preguemos a salva\u00e7\u00e3o!", "Rapazes\nMocidade Presbiteriana,\nSomos testemunhas de Jesus!\nTemos que dizer ao nosso mundo\nQue a solu\u00e7\u00e3o est\u00e1 na cruz!", "Mo\u00e7as\nMocidade, testemunhas de Jesus!\nSim, a solu\u00e7\u00e3o de tudo est\u00e1 na cruz!", "Nossas m\u00e3os estar\u00e3o unidas,\nCombatendo a escravid\u00e3o\nDe preciosas vidas\nSem Jesus, sem dire\u00e7\u00e3o.\nN\u00e3o temeremos, o tentador!\nClamemos pelo Cristo Libertador!", "Juventude crist\u00e3, avante!\nEmpunhando o pend\u00e3o real,\nCom f\u00e9 no Comandante,\nVenceremos todo o mal!\n\u201cS\u00ea testemunha\u201d - disse o Senhor.\nFalemos sempre de Jesus, sem temor!"]'::json, NULL)
        ,
        ('383'::text, ' Um novo mundo'::text, '["Jovens fortes chama-nos Jesus,\nPara um mundo novo construir.\nTrevas, que hoje tentam destruir,\nH\u00e3o de ser clara luz,\nAlvo resplendor!", "Levantemos sobre a terra\nNossos bra\u00e7os libertados,\nAo servi\u00e7o convocados\nPor Jesus;\nPelos que s\u00e3o oprimidos,\nPelos que s\u00e3o perseguidos!\nEia! Que n\u00f3s venceremos\nNo poder da cruz!", "Ao combate, enquanto n\u00e3o soou\nO momento em que h\u00e1 de raiar\nNovo mundo, com Jesus, Senhor,\nOnde a paz e o amor\nSempre h\u00e3o de reinar."]'::json, NULL)
        ,
        ('384'::text, ' Vamos com Jesus'::text, '["\u00d3 jovens, atendei! Oh, que lindo pavilh\u00e3o\nCristo h\u00e1 desfraldado j\u00e1 sobre a na\u00e7\u00e3o.\nE quer-vos nas fileiras a todos receber\nE levar-vos, sem demora, para combater.", "Vamos com Jesus e marchemos sem temor,\nVamos ao combate inflamados de valor\n\u00c2nimo, lutemos todos contra o mal;\n\u00c9 Jesus o nosso grande General!", "\u00d3 jovens, avan\u00e7ai! O divino Vencedor\nQuer juntar-vos todos hoje ao seu redor.\nDispostos \u00e0 batalha, sa\u00ed sem vacilar!\nVamos todos, companheiros, vamos a lutar!", "As armas invenc\u00edveis do Chefe guiador\nS\u00e3o seu Evangelho e seu grande amor.\nCom elas revestidos e cheios de poder,\nCompanheiros, com coragem, vamos a vencer!", "Quem entra nessa guerra,\nCom Deus sempre andar\u00e1,\nE o Senhor vit\u00f3ria lhe conceder\u00e1!\nSim, vamos, companheiros,\nPois, sendo aqui fi\u00e9is,\nCom Jesus conquistaremos imortais laur\u00e9is!"]'::json, NULL)
        ,
        ('385'::text, ' O estudante e a B�blia'::text, '["Toda luz que a raz\u00e3o irradia\nN\u00f3s podemos nos livros achar.\nMas s\u00f3 Cristo nossa alma alumia,\nPois s\u00f3 ele d\u00e1 calma e alegria,\nE s\u00f3 ele nos pode guiar\nE s\u00f3 ele nos pode guiar", "Muito mais do que bons estudantes\nMuito mais do que bons cidad\u00e3os,\nPrecisamos de andar vigilantes\nPara sermos da f\u00e9 bandeirantes;\nPois, colegas, n\u00f3s somos crist\u00e3os!\nPois, colegas, n\u00f3s somos crist\u00e3os!", "N\u00e3o busquemos apenas a gl\u00f3ria\nDe alcan\u00e7ar quase todo saber,\nQuer da ci\u00eancia, quer da arte ou da hist\u00f3ria;\nA raz\u00e3o \u00e9 falaz e ilus\u00f3ria!\nSem Jesus nada pode valer!\nSem Jesus nada pode valer!", "\u00c0 pesquisa da pura verdade\nConsagremos o nosso labor.\nTodo engano enfraquece a vontade\nA mentira n\u00e3o traz liberdade\nE n\u00e3o cabe ao engano o louvor!\nE n\u00e3o cabe ao engano o louvor!", "S\u00f3 em Cristo esta P\u00e1tria querida\nAchar\u00e1 seu futuro feliz.\nNele est\u00e1 toda a luz, toda a vida.\nProcuremos tornar difundida\nA verdade em nosso Pa\u00eds.\nA verdade em nosso Pa\u00eds."]'::json, NULL)
        ,
        ('386'::text, ' Testemunho'::text, '["Direi ao mundo que sou crente\nN\u00e3o me envergonho de o dizer!\nDirei ao mundo que sou crente,\nE que por Cristo vou viver.\nDirei ao mundo que ele salva,\nE nesta vida o seguirei;\nE eu bem sei que, crendo nele,\nTer\u00e1s as b\u00ean\u00e7\u00e3os que eu encontrei.\nDirei ao mundo que ele ama,\nQue Cristo d\u00e1 perd\u00e3o e paz.\nA minha vida eu lhe entrego.\nJesus, meu guia, satisfaz.", "Direi ao mundo que ele volta,\nSe tarde ou cedo, eu n\u00e3o sei!\nMas viverei sempre esperando,\nE preparado estarei.\nNa sua vinda gloriosa,\nAos que o amarem Jesus dar\u00e1\nMaravilhas infinitas,\nE ao lar celeste nos levar\u00e1.\nOh, dize ao mundo que \u00e9s crente,\nN\u00e3o te envergonhes de o dizer.\nOh, dize ao mundo que \u00e9s crente,\nE que por Cristo vais viver."]'::json, NULL)
        ,
        ('387'::text, ' Combate'::text, '["Mo\u00e7os, declarai guerra contra o mal,\nExaltai a cruz do Salvador!\nFirmes empunhai armas n\u00e3o carnais,\nSempre confiai em seu amor!", "Todos juntos ao redor da cruz,\nProntos, firmes estejamos n\u00f3s!\nSim, avante, alegres, corajosos,\nSempre ouvindo de Jesus a voz.", "Mo\u00e7os, avan\u00e7ai! Fortes vos tornais,\nSe o valor da causa conheceis.\nTremulante em luz, erguei o pend\u00e3o,\nGarantia de que vencereis!", "Nosso Deus e Pai, ouve com favor!\nVem nos ajudar a combater!\nE vencida, enfim, a luta final,\nA coroa vem nos conceder."]'::json, NULL)
        ,
        ('388'::text, ' Mocidade, avante'::text, '["Mocidade crist\u00e3, eia, avante!\nReuni vossas for\u00e7as: Lutai!\nO inimigo potente se mostra,\nMas com Cristo sois fortes: Marchai!", "Mocidade crist\u00e3, eis, avante!\nContra o mal, contra o erro lutai!\nTendo o santo Evangelho por arma,\nA verdade da Cruz proclamai!", "Mocidade crist\u00e3, vede o abismo\nOnde muitos est\u00e3o a cair,\nPor faltar-lhes a luz do Evangelho,\nE n\u00e3o virem a Cristo seguir.", "Eia, pois, mocidade pujante,\nPor Jesus, pela f\u00e9, avan\u00e7ai!\nIde aos povos levar o Evangelho,\nPara a gl\u00f3ria de Deus trabalhai!"]'::json, NULL)
        ,
        ('389'::text, ' Avante, mocidade!'::text, '["Eia, avante, mocidade,\nVamos por Jesus lutar!\nA peleja \u00e9 gloriosa,\nDeus nos h\u00e1 de auxiliar.\nEia, avante, companheiros,\nDe olhos postos em Jesus!\nCaminhemos destemidos,\nEspalhando sempre a luz.", "Por Jesus, com zelo santo,\nVinde, \u00f3 jovens, combater!\nA mensagem do Evangelho\nProclamai at\u00e9 morrer!", "Eia, avante, mocidade!\nNunca, nunca recuar!\nNo caminho reto e santo,\nEia, jovens, avan\u00e7ar!\nEia, avante, companheiros!\nSoem tais como um clarim\nAs palavras do convite:\n\u201cVinde todos, vinde a mim!\u201d", "Eia, avante, mocidade,\nConfiando no Senhor!\nOnde h\u00e1 f\u00e9 ningu\u00e9m vacila,\nPois h\u00e1 vida, luz, vigor!\nEia, avante, companheiros,\nVamos firmes batalhar.\nSempre alegres na esperan\u00e7a,\nSempre unidos trabalhar."]'::json, NULL)
        ,
        ('39'::text, ' Exalta��o e louvor'::text, '["Oh, vinde crentes dar louvor ao grande Rei Jesus,\nQue para a nossa Reden\u00e7\u00e3o morreu na amarga cruz;\nSeu sangue derramou, de tudo me lavou:\nMais alvo do que a neve me tornou.", "O sangue de Jesus me lavou, me lavou;\nO sangue de Jesus me lavou, me lavou!\nAlegre, cantarei louvores ao meu Rei,\nAo meu Senhor Jesus que me salvou!", "Oh, vinde unir-vos a Jesus na luta contra o mal,\nE, com o grande Salvador em marcha triunfal,\nA todos proclamar que seu amor sem par\nO fez na cruz seu sangue derramar.", "O chefe da mil\u00edcia \u00e9 Jesus, meu Salvador,\nO Rei dos reis, o Redentor, o eterno e bom Senhor,\nA tudo vencer\u00e1, vit\u00f3ria nos dar\u00e1,\nE \u00e0 gl\u00f3ria eterna os seus conduzir\u00e1."]'::json, NULL)
        ,
        ('390'::text, ' Fi�is soldados'::text, '["De novo a combater\nPor ti, Jesus chamados\nFi\u00e9is e bons soldados,\nAgora eis-nos aqui,\nUnidos no dever\nDe pelejar por ti.", "\u00c0s armas, pois, e com valor!\nFi\u00e9is soldados do Senhor,\nFi\u00e9is soldados do Senhor!\n\u00c0s armas, pois, e com valor!", "Encher-nos vem de amor,\nDe f\u00e9 e de ousadia,\nPoder, sabedoria,\nValor, resolu\u00e7\u00e3o!\nQue andemos, \u00f3 Senhor,\nEm fraternal uni\u00e3o.", "Da cruz do Salvador\nErgamos o estandarte!\nAqui, em toda parte,\nLutemos pela cruz!\nOremos com fervor!\nSirvamos a Jesus!", "Lutar, orar, servir,\nQue certa \u00e9 a vit\u00f3ria!\nE ap\u00f3s a luta a gl\u00f3ria\nDe l\u00e1 nos c\u00e9us estar!\nLutar, orar, servir!\nE com Jesus reinar.!"]'::json, NULL)
        ,
        ('391'::text, ' Mocidade fiel'::text, '["Mocidade fiel, redimida,\nMocidade que Cristo salvou,\nInflamada de amor, vossa vida\nConsagrai a quem vos libertou!\nConsagrai a quem vos libertou!", "Mocidade! Mocidade!\nEscutai o convite do Rei!\nEle \u00e9 a Vida, o Caminho,\nA Verdade ! Mocidade!\nMocidade valente, atendei!", "Aos que vivem na sombra da morte,\nNa mis\u00e9ria, doen\u00e7a e afli\u00e7\u00e3o,\nProclamai bem vibrante, bem forte,\nProclamai em Jesus salva\u00e7\u00e3o!\nProclamai em Jesus salva\u00e7\u00e3o!", "Eis a luta! Marchando altaneira,\nTendo o peito escudado na f\u00e9,\nPor Jesus, pela sua bandeira,\nMocidade valente, de p\u00e9.\nMocidade valente, de p\u00e9."]'::json, NULL)
        ,
        ('392'::text, ' Vida vitoriosa'::text, '["Cristo amado, sei que na for\u00e7a do mal\nTu, meu Mestre, sempre ser\u00e1s protetor.\nTu me guardas, dando-me paz divinal!\nEu contigo sempre serei vencedor!", "Cristo, Mestre,\nSei que contigo eu sou vencedor!\nD\u00e1-me gra\u00e7a,\nD\u00e1-me do teu poder, Redentor!", "Que alegria tenho no meu Salvador;\nTenho gra\u00e7a, vida de amor paternal!\nTudo posso, tudo por ti, meu Senhor;\nDeste mundo sou vencedor, afinal!", "N\u00e3o duvido, Cristo, meu Mestre, de ti;\nCreio em tua rica promessa, Jesus!\nTu n\u00e3o deixas, nem abandonas aqui\nOs que buscam ver tua face de luz.", "Oh! Que b\u00ean\u00e7\u00e3o ter a certeza do bem,\nTer na vida paz e perd\u00e3o do Senhor!\nMui alegre vou para a P\u00e1tria de al\u00e9m,\nOnde reina Cristo Jesus, Rei de amor!"]'::json, NULL)
        ,
        ('393'::text, ' Uni�o vital'::text, '["Duas vidas, Senhor, se unem num s\u00f3 ser;\nDuas almas e dois nobres cora\u00e7\u00f5es.\nPelo amor e afei\u00e7\u00e3o querem j\u00e1 viver\nSempre juntos na paz ou nas afli\u00e7\u00f5es.", "Aben\u00e7oa, Senhor, esta santa uni\u00e3o,\nDando gra\u00e7a e favor, faze-a prosperar\nNa alegria, na f\u00e9, na consagra\u00e7\u00e3o!\nE este amor verdadeiro vem confirmar.", "Mais um lar que se faz cheio de vigor\nDo car\u00e1ter crist\u00e3o, base principal.\nD\u00e1-lhe vida feliz numa uni\u00e3o de amor,\nO mais forte, o maior la\u00e7o conjugal.", "Tu criaste, Senhor, Para o nosso bem,\nA uni\u00e3o que adorna esta vida aqui.\nDe uma uni\u00e3o mui feliz quantas b\u00ean\u00e7\u00e3os v\u00eam\n\u00c0 fam\u00edlia dos teus filhos, gl\u00f3ria a ti! Am\u00e9m"]'::json, NULL)
        ,
        ('394'::text, ' Perfeito amor'::text, '["Perfeito amor, al\u00e9m do entendimento,\nCom devo\u00e7\u00e3o buscamos teu favor.\nFaze perfeito, pois, o casamento\nDos filhos teus que se unem pelo amor.", "D\u00e1-lhes, Senhor, a m\u00fatua confian\u00e7a\nE a f\u00e9 constante em Cristo, o Salvador.\nAs suas almas nutre na esperan\u00e7a\nDe conservarem o mais puro amor.", "D\u00e1 que eles tenham for\u00e7as e alegria\nNos dissabores, lutas, prova\u00e7\u00f5es!\nQue assim conservem juntos a harmonia,\nPerfeitos tendo sempre os cora\u00e7\u00f5es. Am\u00e9m."]'::json, NULL)
        ,
        ('395'::text, ' Amor no lar'::text, '["Mui felizes nos correm os dias\nE depressa se esvai nossa dor!\nS\u00e3o benditas as s\u00e3s alegrias,\nQuando reina no lar doce amor!", "Os caminhos pisamos juncados,\nSim, juncados de ramos em flor!\nSurgem b\u00ean\u00e7\u00e3os de todos os lados,\nQuando reina no lar doce amor!", "Saboroso \u00e9 o p\u00e3o que fru\u00edmos,\nSe o fru\u00edmos de nosso labor!\nSim, contente, em tudo sorrimos,\nQuando reina no lar doce amor!", "Os pais crentes aos filhos afirmam\nAs verdades da Lei do Senhor!\nE com obras o ensino confirmam,\nQuando reina no lar doce amor!", "Se sentimos em casa a pobreza,\nSe h\u00e1 pobreza tamb\u00e9m ao redor,\nSuport\u00e1vel ser\u00e1, com certeza,\nQuando reina no lar doce amor!"]'::json, NULL)
        ,
        ('396'::text, ' Gra�as pelo anivers�rio'::text, '["Um ano mais de vida\nGuardou-vos o Senhor!\nE deu-vos fiel guarida\nNo seu divino amor.", "De cora\u00e7\u00e3o dai gra\u00e7as\nAo vosso eterno Pai!\nPois mais um ano passa,\nA Deus mil gra\u00e7as dai!", "De noite, em claro dia,\nNo inverno e no ver\u00e3o,\nNa dor e na alegria\nGozastes prote\u00e7\u00e3o.", "No cora\u00e7\u00e3o que ama\nO terno Salvador,\nExiste um canto alegre\nQue espalha o teu louvor.", "Ensina-nos, \u00f3 Cristo,\nO que conv\u00e9m lembrar,\nE todo o nosso tempo\nNo bem aproveitar. Am\u00e9m."]'::json, NULL)
        ,
        ('397'::text, ' Por minha boa m�e'::text, '["Por minha boa m\u00e3e\nE pelo seu amor,\nNa terra, sem igual,\nEu louvo-te, \u00f3 Senhor.", "\u00c9 grande o bem\nQue na alma tem\nQuem pode algu\u00e9m\nChamar de m\u00e3e!", "Pois ela me cuidou\nDa vida ao despontar.\nE aos p\u00e9s do bom Jesus\nFeliz me fez andar.", "Se pobre ou rico eu for,\nJamais hei de esquecer\nA minha boa m\u00e3e,\nEnquanto aqui viver.", "\u00c9 grato aqui sentir\nDe m\u00e3e o puro amor.\nPor ela o cora\u00e7\u00e3o\nBendize ao Criador."]'::json, NULL)
        ,
        ('398'::text, ' Outra vez cantamos'::text, '["Pai, outra vez cantamos teu louvor\nA uma voz, ao templo teu deixar.\nBemdito \u00e9s Tu, conosco est\u00e1s, Senhor,\nCom tua paz vem nos aben\u00e7oar.", "Que tua paz levemos para o lar;\nS\u00ea Tu conosco at\u00e9 ao anoitecer!\nE do pecado, \u00f3 Pai, vem nos guardar\nAo virmos n\u00f3s teu Nome engrandecer.", "Que tua paz nos guie, qual fanal,\nDurante a noite, para nos guardar\nNa escurid\u00e3o da vida e contra o mal,\nPois luz e trevas podes dominar.", "D\u00e1-nos, Senhor, a tua paz fruir\nA cada instante, em nosso labutar!\nE adentrando a P\u00e1tria do porvir,\nA paz perfeita iremos desfrutar. Am\u00e9m."]'::json, NULL)
        ,
        ('399'::text, ' T�rmino do culto'::text, '["Findado agora o culto, \u00f3 Deus,\nPrestado em teu louvor,\nDespede em paz e guia os teus\nAt\u00e9 chegarmos l\u00e1 nos c\u00e9us,\n\u00d3 Pai de vero amor.", "De Cristo d\u00e1-nos sempre ter\nA gra\u00e7a singular!\nNo teu amor permanecer,\nNo Santo Esp\u00edrito viver,\nEm comunh\u00e3o sem par. Am\u00e9m."]'::json, NULL)
        ,
        ('4'::text, ' Culto � Trindade'::text, '["Deus est\u00e1 no templo!\nPai Onipotente!\nA seus p\u00e9s nos humilhamos.\nServos consagrados,\nReverentemente,\nAo Deus santo adoramos.\nPor favor, com amor,\nEspiritualmente\nDeus est\u00e1 no templo!", "Cristo est\u00e1 no templo!\nSumo benef\u00edcio\nPor seu sangue nos foi dado.\nEle, o bom Cordeiro,\nFoi o sacrif\u00edcio\nQue expiou o vil pecado.\nEscolheu e sofreu\nO cabal supl\u00edcio;\nCristo est\u00e1 no templo!", "Tu, que est\u00e1s no templo,\nPreceptor divino,\nE os cora\u00e7\u00f5es habitas;\nTu, paciente Mestre,\nD\u00e1-nos teu ensino,\nAclarando as leis benditas.\nQue prazer conhecer\nA gra\u00e7a infinita!\nSim, est\u00e1 no templo!"]'::json, NULL)
        ,
        ('40'::text, ' C�ntico ao salvador'::text, '["Cantai a Cristo, o Salvador,\nQue tanto nos amou,\nE, para nossa Reden\u00e7\u00e3o,\nSeu sangue derramou!", "Salva\u00e7\u00e3o! Salva\u00e7\u00e3o!\nAlcan\u00e7ou-nos o Filho de Deus;\nSalva\u00e7\u00e3o! Salva\u00e7\u00e3o!\nAleluia ao Filho de Deus!", "Cantai o amor celestial,\nAmor do grande Deus,\nDivina flama a consumir\nOs vis pecados meus!", "De Cristo, o excelso, infindo amor,\nOh, vinde publicai!\nE dele a grande salva\u00e7\u00e3o\nA todos proclamai!"]'::json, NULL)
        ,
        ('400'::text, ' Ora��o por prote��o'::text, '["Grande Deus! Em paz,agora,\nDespedimo-nos, Senhor.\nCertos de fruir as b\u00ean\u00e7\u00e3os\nQue prov\u00e9m do teu amor.\nD\u00e1-nos for\u00e7as, d\u00e1-nos for\u00e7as,\nNeste mundo de amargor.", "Gra\u00e7as, gra\u00e7as n\u00f3s rendemos\nPela tua Reden\u00e7\u00e3o.\nE termos jubilosos,\nTua santa prote\u00e7\u00e3o.\nDeus eterno, Deus eterno,\nReina em cada cora\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('41'::text, ' Louvor pela gra�a divina'::text, '["Louvai, louvai Cristo, o bom Mestre divino!\nPor n\u00f3s na cruz ele sofreu, morreu;\nPerd\u00e3o, perd\u00e3o hoje aos contritos outorga,\nPois precioso sangue na cruz verteu.\nSim, louvai-o! Ei-lo t\u00e3o exaltado,\nMediador que nunca nos faltar\u00e1.", "Louvai, louvai e proclamai a grandeza\nDo perd\u00e3o, da gra\u00e7a que a todos d\u00e1.", "Louvai, louvai Cristo, o bom Mestre divino!\nConselhos bons d\u00e1 ele ao pecador;\nAnunciai as b\u00ean\u00e7\u00e3os maravilhosas\nConcedidas por nosso Salvador.\nIde todos, servos de Jesus Cristo,\nE ele nunca vos abandonar\u00e1.", "Louvai,,louvai Cristo, o bom Mestre divino!\nCantai, cantai, seu grande amor cantai;\nFi\u00e9is, cantai de cora\u00e7\u00e3o, bem unidos,\nSeu poder e gl\u00f3ria louvai, louvai!\nQual pastor que cuida do seu rebanho,\nCristo, assim, os crentes proteger\u00e1."]'::json, NULL)
        ,
        ('42'::text, ' O grande amor de Deus'::text, '["A Deus demos gl\u00f3ria, por seu grande amor,\nO Filho bendito por n\u00f3s todos deu,\nE gra\u00e7a concede ao mais vil pecador,\nAbrindo-lhe a porta de entrada no c\u00e9u.", "Exultai! Exultai! Vinde todos louvar\nA Jesus, Salvador, a Jesus, Redentor!\nA Deus demos gl\u00f3ria, porquanto do c\u00e9u\nSeu Filho bendito por n\u00f3s todos deu!", "Oh, gra\u00e7a real! Foi assim que Jesus,\nMorrendo, seu sangue por n\u00f3s derramou.\nHeran\u00e7a nos c\u00e9us, com os santos em luz,\nLegou-nos Aquele que o pre\u00e7o pagou.", "Tal prova de amor nos persuade a confiar\nNos merecimentos do Filho de Deus!\nE quem, a Jesus pela f\u00e9 se entregar,\nVai v\u00ea-lo na gl\u00f3ria eterna dos c\u00e9us."]'::json, NULL)
        ,
        ('43'::text, ' O Deus de amor'::text, '["Desperta j\u00e1 meu cora\u00e7\u00e3o,\nE louva ao Salvador;\nCantando em hinos triunfais\nQue Deus \u00e9 o Deus de amor.\nImenso amor! Amor sem par!\nPreenche os vastos c\u00e9us\nAlcan\u00e7a a terra e a todos n\u00f3s\nInsigne amor de Deus!", "Desperta j\u00e1, meu cora\u00e7\u00e3o,\nE louva ao grande Salvador;\nCantando em hinos triunfais\nQue Deus \u00e9 o Deus de amor.", "Amor que um dia se encarnou\nA fim de me salvar,\nE que subindo \u00e0 dura cruz\nMorreu em meu lugar!\nAmor que exclama: \u201cVinde a mim!\u201d\nQue busca o pecador.\nAmor eterno, sem igual,\nDe Cristo, Salvador.", "Remido a pre\u00e7o t\u00e3o real\nO sangue do Senhor,\nCom que pagar eu poderei\nTal gra\u00e7a, tal amor?\nQuando ele a mim, um pecador,\nSe digna de aceitar,\nE me levar com seus fi\u00e9is\nAo santo e eterno lar?"]'::json, NULL)
        ,
        ('44'::text, ' Louvores ao Senhor'::text, '["Louvemos ao Senhor, ao Pai da eternidade,\nQue mostra tanto amor \u00e0 pobre humanidade!\nSeu Filho aqui sofreu por nossa reden\u00e7\u00e3o\nE com o Pai nos deu bendita comunh\u00e3o!"]'::json, NULL)
        ,
        ('45'::text, ' Grande reden��o'::text, '["Milhares de milhares ou\u00e7o\nCantando a Deus com gratid\u00e3o,\nA Cristo, o Salvador, louvando\nPor sua grande reden\u00e7\u00e3o.\n\u201cA Deus louvai!\u201d - em coro clamam;\n\u201cA Deus louvai - pois nos amou!\nE pelo sangue do Cordeiro,\nDe toda mancha nos lavou!\u201d", "O mesmo salmo triunfante,\nEm tons de santa exulta\u00e7\u00e3o,\nPor todo o mundo vai subindo\nA Deus, Autor da Reden\u00e7\u00e3o.\n\u201cA Deus louvai\u201d - nos vales soa\nE os montes fazem ecoar:\n\u201cA Deus louvai, o Deus da Gra\u00e7a,\nQue aos pecadores quer salvar!", "E n\u00f3s, teus santos p\u00e9s cercando,\nEnquanto militando aqui\nAs nossas vozes elevamos\nPara exaltar, Senhor, a ti.\n\u201cA Deus louvai\u201d - tamb\u00e9m clamamos!\n\u201cA Deus louvai, supremo Rei!\nE gl\u00f3ria, honra, majestade\nA Cristo, o Salvador, rendei!\u201d", "Das trevas fomos n\u00f3s chamados\n\u00c0 tua pura e santa luz!\nDa escravid\u00e3o e do pecado\nNos libertaste pela cruz.\n\u201cA Deus louvai!\u201d cantemos todos!\n\u201cA Deus louvai, que nos remiu,\nE filhos seus e seus herdeiros,\nEm Cristo, nos constituiu!\u201d"]'::json, NULL)
        ,
        ('46'::text, ' Altos louvores'::text, '["Altos louvores a quem triunfou!\nJesus, padecendo, seu povo salvou.\nMorto na cruz pelos crimes do mundo,\nDotou aos in\u00edquos de vida e perd\u00e3o.\nQu\u00e3o grande esta gra\u00e7a! Favor t\u00e3o profundo!\nAmor indiz\u00edvel! Real compaix\u00e3o!\nAltos louvores a quem triunfou!\nJesus, padecendo, seu povo salvou. (bis)", "Gl\u00f3ria rendemos ao bom Salvador,\nExcelso em justi\u00e7a, supremo em amor!\nCristo quebrou as cadeias do forte,\nSeu cetro arrancando com r\u00e9gio poder.\nAgora, onde est\u00e3o teus terrores, \u00f3 morte?\nSepulcro! Teus presos ir\u00e3o reviver!\nGl\u00f3ria rendamos ao bom Salvador,\nExcelso em justi\u00e7a, supremo em amor! (bis)", "Gra\u00e7as te damos, divino Senhor,\nAmparo constante, fiel protetor!\nNunca nos deixas, Pastor incans\u00e1vel!\nTeu bra\u00e7o n\u00e3o falha, nem perde o poder.\nConosco presente em bondade imut\u00e1vel,\nTeu povo diriges com alto saber,\nGra\u00e7as te damos, divino Senhor,\nAmparo constante, fiel protetor. (bis)"]'::json, NULL)
        ,
        ('47'::text, ' Louvor e gl�ria'::text, '["Louvamos-te \u00f3 Deus, pelo dom de Jesus\nQue, por n\u00f3s, pecadores, morreu sobre a cruz,", "Aleluia! Toda a gl\u00f3ria te rendemos, Senhor!\nAleluia! Tua gra\u00e7a imploramos. Am\u00e9m.", "Louvamos-te, \u00f3 Deus, pois nos mandas a luz\nQue, brilhando nas trevas, a Cristo conduz.", "Louvamos-te, \u00f3 Deus, por teu Filho, Jesus,\nQue foi morto, mas vive mo Reino da luz.", "Louvamos-te, \u00f3 Deus, e rogamos, Senhor\nD\u00e1-nos sempre sentir teu poder, teu amor."]'::json, NULL)
        ,
        ('48'::text, ' Amor e gratid�o'::text, '["Jesus, qu\u00e3o infinito \u00e9 teu divino amor!\nAl\u00e9m do nosso alcance, profundo \u00e9 seu valor!\nOs c\u00e9us por n\u00f3s deixaste, vieste, aqui, morrer,\nA fim de que possamos contigo, al\u00e9m, viver.", "Por isso, alegremente, buscamos sempre a ti,\nVisando obedecer-te na vida breve aqui.\nEmbora desprezados, com afli\u00e7\u00f5es e dor,\n\u00c9 suave e bom servir-te, bendito Salvador! Am\u00e9m."]'::json, NULL)
        ,
        ('49'::text, ' Sempre vencendo'::text, '["Sempre vencendo, mui vitorioso,\nCristo Jesus, o Senhor!\n\u00c9 Soberano, Chefe bendito,\nEm tudo ele \u00e9 vencedor!\nEi-lo supremo, guiando,\nCom seu imenso poder!\nTodos avante, pois, crentes,\nTodos lutar e vencer.", "N\u00e3o \u00e9 dos fortes a vit\u00f3ria,\nNem dos que correm melhor.\nMas dos fi\u00e9is e sinceros\nQue seguem junto ao Senhor", "Sempre vencendo, mui vitorioso,\nCristo Jesus, o Senhor!\nEis suas hostes inumer\u00e1veis,\nSeu grande imp\u00e9rio e fulgor;\nEm seu governo demonstra\nCuidado e amor sem igual;\nSempre nos ama e protege\nCom seu poder eternal", "Sempre vencendo, mui vitorioso,\nCristo Jesus, o Senhor!\nReis e vassalos, servos e chefes\nQuerem tamb\u00e9m seu favor.\nSenhor, desejo e te imploro\nQue me permitas lutar\nSempre ao teu lado, invenc\u00edvel,\nAt\u00e9 meus dias findar!"]'::json, NULL)
        ,
        ('5'::text, ' Trindade adorada'::text, '["Gl\u00f3ria seja ao Pai,\nAo Filho e ao Santo Esp\u00edrito,\nComo era no princ\u00edpio,\n\u00c9 hoje e para sempre,\nEternamente! Am\u00e9m! Am\u00e9m!"]'::json, NULL)
        ,
        ('50'::text, ' Sangue precioso'::text, '["Cantarei a Cristo e seu excelso amor;\nPor n\u00f3s baixou \u00e0 terra o grande Salvador", "O sangue precioso\nDe Cristo tem valor\nDas penas da justi\u00e7a\nLiberta o pecador.", "Cantarei a Cristo! Por n\u00f3s morreu na cruz.\nAos pobres pecadores quem salva \u00e9 s\u00f3 Jesus!", "Cantarei a Cristo! Por n\u00f3s cumpriu a lei!\nSeu manto de justi\u00e7a, alegre, vestirei.", "Cantarei a Cristo! Nas nuvens voltar\u00e1,\nE na celeste gl\u00f3ria os seus receber\u00e1."]'::json, NULL)
        ,
        ('51'::text, ' Cristo coroado'::text, '["A Cristo coroai, Cordeiro vencedor!\nOuvi das hostes celestiais, dos anjos o louvor!\nEleva a tua voz e entoa, cora\u00e7\u00e3o\nLouvando \u00c0quele que morreu e deu-te a salva\u00e7\u00e3o.", "A Cristo coroai, seu lado e m\u00e3os olhai!\nDas suas chagas o esplendor e a gl\u00f3ria contemplai!\nNem anjos l\u00e1 do c\u00e9u o podem suportar;\nPerante o m\u00edstico esplendor abaixam seu olhar", "A Cristo coroai! A vida nos doou,\nE a fim de dar-nos salva\u00e7\u00e3o da tumba triunfou!\nCantemos seu poder: morreu, mas ressurgiu!\nA vida eterna ali nos deu e a morte destruiu.", "A Cristo coroai, das eras o Senhor,\nDos mundos e astros da amplid\u00e3o eterno Criador.\nAo grande Redentor, que nos deu salva\u00e7\u00e3o,\nEternamente tributai louvor e adora\u00e7\u00e3o."]'::json, NULL)
        ,
        ('52'::text, ' Gl�ria e coroa��o'::text, '["Saudai o nome de Jesus!\nArcanjos, adorai!\nAo Rei que se humilhou na cruz,\nCom gl\u00f3ria, gl\u00f3ria, gl\u00f3ria, gl\u00f3ria,\nCom gl\u00f3ria coroai!", "\u00d3 escolhida gera\u00e7\u00e3o\nDe Deus, o eterno Pai,\nAo grande autor da salva\u00e7\u00e3o\nCom gl\u00f3ria, gl\u00f3ria, gl\u00f3ria, gl\u00f3ria,\nCom gl\u00f3ria coroai!", "Remidos todos, com fervor,\nLouvores entoai!\nAo que da morte \u00e9 vencedor,\nCom gl\u00f3ria, gl\u00f3ria, gl\u00f3ria, gl\u00f3ria,\nCom gl\u00f3ria coroai!", "\u00d3 ra\u00e7as, povos e na\u00e7\u00f5es\nAo Rei divino honrai!\nA quem quebrou os vis grilh\u00f5es,\nCom gl\u00f3ria, gl\u00f3ria, gl\u00f3ria, gl\u00f3ria,\nCom gl\u00f3ria coroai!"]'::json, NULL)
        ,
        ('53'::text, ' Honra, poder, majestade'::text, '["Honra, poder, majestade, riqueza,\nSabedoria, dom\u00ednio e grandeza,\nAo vencedor da batalha \u00e9 cantado,\nAo ser no trono do c\u00e9u, coroado.", "Soa nos c\u00e9us o triunfo, a vit\u00f3ria;\nVibra na terra do seu Nome a gl\u00f3ria;\nMontes, oceanos, florestas e flores,\nDo seu dom\u00ednio ecoam louvores.", "Sobe em canto constante alegria;\nB\u00ean\u00e7\u00e3os do alto nos v\u00eam cada dia;\nHonra, poder, majestade entoamos\nEm nossos hinos que a Deus elevamos.", "Dai ao Cordeiro o louvor que \u00e9 devido;\nNo trono reina de gl\u00f3ria vestido.\nFoi imolado - aparente fraqueza!\nMas triunfante, j\u00e1 vive em grandeza."]'::json, NULL)
        ,
        ('54'::text, ' A chegada do Messias'::text, '["Cantai! Exultai! O Messias chegou!\nDissiparam-se as trevas, a aurora raiou!", "Dai louvores, celebrai-o!\nFoi morto na cruz!\nDai louvores, publicai-o;\nJ\u00e1 vive Jesus!", "Cantai! Exultai! Pelos \u00edmpios sofreu!\nSatisfez a justi\u00e7a e o sangue verteu!", "Cantai! Exultai! Temos pleno perd\u00e3o,\nPois Jesus nos concede real salva\u00e7\u00e3o!", "Cantai! Exultai! Jesus Cristo, nos c\u00e9us,\nPelos seus intercede \u00e0 destra de Deus", "Cantai! Exultai! O Senhor voltar\u00e1!\nTriunfante, glorioso, nas nuvens vir\u00e1!"]'::json, NULL)
        ,
        ('55'::text, ' Alegria e gratid�o'::text, '["Cantai alegremente, cantai a Deus louvor;\nTrazei a ele um canto de exulta\u00e7\u00e3o e amor\n\u00c9 Deus quem faz a terra seus frutos produzir,\nE as esta\u00e7\u00f5es do ano em ordem prosseguir.", "\u00c9 Deus que envia as chuvas e os montes faz florir;\nQue o vale exuberante de trigo faz cobrir.\nDeus tudo multiplica em sua compaix\u00e3o,\nFartura concedendo com dadivosa m\u00e3o.", "Trazei aos seus altares dos frutos que ele deu,\nE as almas da colheita por quem, Jesus morreu.\nProstrai-vos diante dele, humildes, com louvor,\nE assim, em vossas vidas, dai gra\u00e7as ao Senhor!"]'::json, NULL)
        ,
        ('56'::text, ' A��es de gra�a e s�plica'::text, '["Gra\u00e7as te rendemos, Deus de luz e amor,\nPelo dom de Cristo, nosso Salvador;\nPelo que nos deste com bondosa m\u00e3o,\nPelos dons da vida: Veste, abrigo e p\u00e3o.", "Pelos bons amigos que, Senhor, te apraz\nCongregar conosco, nesta doce paz.\nD\u00e1-nos sempre a gra\u00e7a de contigo estar,\nD\u00e1-nos sempre o gozo de teu Nome honrar!"]'::json, NULL)
        ,
        ('57'::text, ' Fonte de todo bem'::text, '["A terra semeamos a fim de nos dar p\u00e3o,\nMas Deus \u00e9 quem a nutre com benfazeja m\u00e3o.\n\u00c9 Deus quem manda o frio, o inverno e o ver\u00e3o,\nA chuva, o doce orvalho e a fresca vira\u00e7\u00e3o.", "De todo bem a fonte\n\u00c9 o nosso bom Senhor!\nLouvai a Deus, louvai a Deus\nPor todo o seu amor!", "O Criador de tudo, que em toda parte est\u00e1\nColore a flor silvestre e \u00e0 estrela brilho d\u00e1\nOs ventos lhe obedecem, e o bravo mar tamb\u00e9m;\nAs fr\u00e1geis avezinhas o seu cuidado tem!", "A n\u00f3s, por\u00e9m, seus filhos revela mais amor,\nMandando a este mundo o Filho Salvador!\nA n\u00f3s fazendo herdeiros de tudo quanto tem,\nNa reden\u00e7\u00e3o perfeita que dele nos prov\u00e9m!"]'::json, NULL)
        ,
        ('58'::text, ' Ofertas'::text, '["A minha vida, com meus bens,\nProv\u00e9m de ti, Senhor!\nSa\u00fade, veste, abrigo e p\u00e3o\nS\u00e3o dons do teu amor!", "Oh! N\u00e3o permitas, Pai de amor\nQue aquilo que me d\u00e1s\nEu ouse, incauto, desviar\nEm causas v\u00e3s ou m\u00e1s!", "N\u00e3o deixes que meu cora\u00e7\u00e3o\nSe engolfe no prazer,\nE no fruir de muitos dons\nEu venha te esquecer!", "Mordomo teu, eu quero dar\n\u00c0 Igreja, em propor\u00e7\u00e3o,\nA quantas b\u00ean\u00e7\u00e3os recebi\nDe tuas pr\u00f3prias m\u00e3os!", "Aceita a minha oferta, \u00f3 Deus,\nE b\u00ean\u00e7\u00e3os d\u00e1-me a flux.\nSim, d\u00e1-me sempre o que ofertar\n\u00c0 causa de Jesus! Am\u00e9m."]'::json, NULL)
        ,
        ('59'::text, ' Gratid�o'::text, '["Ao Deus eterno, Criador,\nMil gra\u00e7as tributemos,\nPor tantos anos de labor\nNo p\u00e1trio lar que temos.", "Ao Deus eterno, Criador,\nAo Deus da Reden\u00e7\u00e3o,\nPor tantos anos de labor,\nA nossa gratid\u00e3o!", "Provaste,\u00f3 Deus, o teu amor\nA n\u00f3s, os brasileiros,\nNa voz t\u00e3o plena de fervor\nDos grandes pioneiros.", "Por toda parte \u00f3 Redentor,\nAgora \u00e9 anunciado\nTeu nome ao pobre pecador\nQue jaz no vil pecado.", "Que grandes coisas, \u00f3 Senhor,\nA todos n\u00f3s legaste!\nS\u00e3o provas desse grande amor\nCom que Tu nos amaste. Am\u00e9m."]'::json, NULL)
        ,
        ('6'::text, ' Doxologia'::text, '["A Deus, supremo Benfeitor,\nAnjos e homens d\u00eaem louvor;\nA Deus o Filho, a Deus o Pai,\nE a Deus Esp\u00edrito, gl\u00f3ria dai. Am\u00e9m."]'::json, NULL)
        ,
        ('60'::text, ' Ofert�rio'::text, '["Em tudo as tuas m\u00e3os, Senhor,\nNos t\u00eam enriquecido,\nE \u00e0s provis\u00f5es do teu amor,\nBondosas, conduzido.", "De ti procede a salva\u00e7\u00e3o\nE as b\u00ean\u00e7\u00e3os incont\u00e1veis\nQue, dia a dia, \u00f3 Pai, nos d\u00e3o\nAs tuas m\u00e3os am\u00e1veis.", "De ti vivemos n\u00f3s, Senhor,\nE em ti nos alegramos;\nNa comunh\u00e3o do puro amor\nQue em Cristo desfrutamos.", "Com mui sincero cora\u00e7\u00e3o\nOferta aqui trazemos;\nRecebe como gratid\u00e3o\nDe quanto a ti devemos. Am\u00e9m."]'::json, NULL)
        ,
        ('61'::text, ' A��es de gra�a'::text, '["Gra\u00e7as dou por esta vida,\nPelo bem que revelou\nGra\u00e7as dou pelo futuro\nE por tudo que passou.\nPelas b\u00ean\u00e7\u00e3os derramadas,\nPelo amor pela afli\u00e7\u00e3o,\nPelas gra\u00e7as reveladas,\nGra\u00e7as dou pelo perd\u00e3o.", "Gra\u00e7as pelo azul celeste\nE por nuvens que h\u00e1 tamb\u00e9m,\nPelas rosas do caminho\nE os espinhos que elas t\u00eam.\nPelas noites desta vida,\nPela estrela que brilhou,\nPela prece respondida\nE a esperan\u00e7a que falhou.", "Pela cruz e o sofrimento,\nE, afinal, ressurrei\u00e7\u00e3o,\nPelo amor, que \u00e9 sem medida,\nPela paz no cora\u00e7\u00e3o;\nPela l\u00e1grima vertida\nE o consolo que \u00e9 sem par,\nPelo dom da eterna vida,\nSempre gra\u00e7as hei de dar."]'::json, NULL)
        ,
        ('62'::text, ' Hino de gratid�o'::text, '["Gra\u00e7as te dou por tudo que me deste,\nPrimeiro a Salva\u00e7\u00e3o em meu Jesus.\nGra\u00e7as te dou por tudo que fizeste\nPor este pecador salvo na cruz!", "Gra\u00e7as, gra\u00e7as, mil gra\u00e7as\nA ti, meu Salvador!\nGra\u00e7as, gra\u00e7as, mil gra\u00e7as\nPor teu precioso amor!", "Eu te agrade\u00e7o a b\u00ean\u00e7\u00e3o do trabalho\nE do meu lar, que alegra o meu viver;\nA corre\u00e7\u00e3o paterna quando falho,\nProvando teu amor pelo meu ser.", "Eu te agrade\u00e7o o p\u00e3o de cada dia,\nA \u00e1gua que alivia a sede dura,\nE o sol que aquece a pobre gente fria,\nS\u00e3o b\u00ean\u00e7\u00e3os que enchem a terra de fartura.", "Eu te agrade\u00e7o, \u00f3 Pai, os meus amigos,\nE os inimigos tamb\u00e9m te agrade\u00e7o.\nSou grato por vit\u00f3rias em perigos,\nE outras b\u00ean\u00e7\u00e3os que eu n\u00e3o mere\u00e7o."]'::json, NULL)
        ,
        ('63'::text, ' As muitas b�n��os'::text, '["Se da vida as vagas procelosas s\u00e3o,\nSe, com desalento, julgas tudo v\u00e3o,\nConta as muitas b\u00ean\u00e7\u00e3os, dize-as de uma vez,\nE ver\u00e1s, surpreso, quanto Deus j\u00e1 fez.", "Conta as b\u00ean\u00e7\u00e3os, dize-as quantas s\u00e3o,\nRecebidas da divina m\u00e3o\nVem diz\u00ea-las, todas de uma vez,\nE ver\u00e1s, surpreso, quanto Deus j\u00e1 fez!", "Tens acaso m\u00e1goas, triste \u00e9 teu lidar?\n\u00c9 a cruz pesada que tens de levar?\nConta as muitas b\u00ean\u00e7\u00e3os! Logo exultar\u00e1s,\nE, fortalecido, tudo vencer\u00e1s!", "Quando vires outros com seu ouro e bens,\nLembra que tesouros prometidos tens.\nNunca os bens da terra poder\u00e3o comprar\nA mans\u00e3o celeste que vais habitar.", "Seja o teu combate longo ou breve aqui,\nN\u00e3o te desanimes Deus ser\u00e1 por ti!\nSeu divino aux\u00edlio minorando o mal,\nTe dar\u00e1 consolo e galard\u00e3o final"]'::json, NULL)
        ,
        ('64'::text, ' Grata mem�ria'::text, '["Nunca meus l\u00e1bios cessar\u00e3o, \u00f3 Cristo,\nDe bendizer-te, de cantar-te gl\u00f3ria;\nPois guardo na alma teu amor imenso:\nGrata mem\u00f3ria!", "Quando perdido vagueava aflito,\nE em densas trevas meu andar seguia,\nTu me buscaste, l\u00e1 dos c\u00e9us mandando\nLuz que me guia!", "Quando oprimido por mundano jugo,\nEm maus caminhos eu me angustiava,\nDeu-me descanso tua voz t\u00e3o terna,\nQue me chamava!", "Aos fortes bra\u00e7os eu corri confiante,\nMeigo e bondoso, n\u00e3o me recusaste;\nEm teu imenso, suave amor, t\u00e3o puro,\nMe agasalhaste!", "Oh! Nunca, nunca cessar\u00e3o meus l\u00e1bios\nDe bendizer-te, de cantar-te a gl\u00f3ria;\nPois em minha alma Tu \u00e9s sempre, \u00f3 Cristo,\nGrata mem\u00f3ria! Am\u00e9m."]'::json, NULL)
        ,
        ('65'::text, ' Louvor'::text, '["Dai gra\u00e7as, todos v\u00f3s, a Deus humildemente;\nMilagres fez por n\u00f3s neste universo ingente.\nPois v\u00f3s deveis saber que muitos bens nos deu,\nQue pelo seu poder nos vem a luz do c\u00e9u.", "O Deus da Reden\u00e7\u00e3o nos queira dar a vida\nAlegre cora\u00e7\u00e3o e aux\u00edlio em toda lida.\nMantenha-nos de p\u00e9, por seu imenso amor,\nE nos conserve a f\u00e9 em Cristo, o Salvador.", "Exalta\u00e7\u00e3o, louvor ao Pai, ao Filho amado\nE ao bom Consolador, que foi dos c\u00e9us mandado!\nAo grande e trino Deus, em canto triunfal,\nTribute os hinos seus a Igreja Universal."]'::json, NULL)
        ,
        ('66'::text, ' Cora��o quebrantado'::text, '["Sonda-me, \u00f3 Deus, o cora\u00e7\u00e3o;\nSonda-me, \u00f3 Deus, o cora\u00e7\u00e3o;\nProva e conhece os meus pensamentos;\nProva e conhece os meus pensamentos;\nV\u00ea se em mim h\u00e1 algum mau caminho.\nSonda-me, conhece-me\nE prova-me, \u00f3 Deus!\nConhece-me o cora\u00e7\u00e3o,\nConhece-me o cora\u00e7\u00e3o;\nGuia-me, Senhor,\nGuia-me Senhor,\nE mostra-me o caminho,\nE guia os meus passos,\nGuia meus passos,\n\u00d3 meu Salvador. Am\u00e9m."]'::json, NULL)
        ,
        ('67'::text, ' Cora��o quebrantado'::text, '["Sonda-me, \u00f3 Deus, pois v\u00eas meu cora\u00e7\u00e3o!\nProva-me, \u00f3 Pai, te pe\u00e7o em ora\u00e7\u00e3o.\nDe todo o mal liberta-me, Senhor,\nAt\u00e9 da transgress\u00e3o que oculta for.", "Vem me lavar dos vis pecados meus,\nConforme prometeste, meu bom Deus,\nFaze-me arder e consumir de amor,\nPois quero te magnificar, Senhor.", "Todo meu ser n\u00e3o considero meu;\nQuero gast\u00e1-lo no servi\u00e7o teu.\nMinhas paix\u00f5es Tu podes dominar,\nPois Tu, Senhor, vieste em mim morar.", "L\u00e1 do alto c\u00e9u o avivamento vem,\nE que comece em mim seguindo al\u00e9m.\nO teu poder, as b\u00ean\u00e7\u00e3os, teu favor\nConcede aos que s\u00e3o teus, \u00f3 Pai de amor. Am\u00e9m."]'::json, NULL)
        ,
        ('68'::text, ' Necessidade'::text, '["Eu creio, Senhor, na divina promessa,\nVit\u00f3rias j\u00e1 tive nas lutas aqui.\nContudo, \u00e9 mui certo que a gente trope\u00e7a;\nPor isso, Senhor, eu preciso de ti.", "A luz que me guia no escuro caminho\nFulgura de cima do sol criador;\nContudo, n\u00e3o posso segui-la sozinho;\nPor isso eu preciso de ti, meu Senhor.", "Bem sei que nas preces eu posso buscar-te,\nJamais dessa b\u00ean\u00e7\u00e3o na vida eu descri;\nContudo, \u00e9 poss\u00edvel que eu dela me afaste;\nPor isso, Senhor, eu preciso de ti.", "Esfor\u00e7os da terra, prec\u00e1rio destino,\nEmpenho dos homens, riqueza, o que for,\nN\u00e3o valem a b\u00ean\u00e7\u00e3o do reino divino;\nPor isso eu preciso de ti, meu Senhor. Am\u00e9m."]'::json, NULL)
        ,
        ('69'::text, ' S�plica'::text, '["Salvador, Jesus bendito\nDe minha alma a Reden\u00e7\u00e3o\nTua gra\u00e7a me convida\nA buscar-te em confiss\u00e3o.", "Por amor de mim morreste\nSobre a ensang\u00fcentada cruz!\nTu sofreste a minha pena,\nOh! Meu Salvador Jesus!", "A minha alma purifica\nEm teu sangue remidor;\nFaze que, leal e humilde,\nEu te sirva, meu Senhor.", "Tua ovelha, nos teus bra\u00e7os,\nBem segura guardar\u00e1s,\nVem livrar-me dos pecados\nE guardar-me em tua paz!"]'::json, NULL)
        ,
        ('7'::text, ' Gl�ria � Trindade'::text, '["A ti, meu Criador,\nDos altos c\u00e9us Senhor,\nEu quero honrar.\nAceita a adora\u00e7\u00e3o\nQue a voz e o cora\u00e7\u00e3o\nTe v\u00eam, bondoso Pai,\nAqui prestar.", "A ti, Emanuel,\nPor teu amor fiel\nRendo eu louvor.\nDo c\u00e9u trouxeste a luz,\nPor mim sofreste a cruz,\nBendito sejas, pois,\nMeu Redentor", "A ti, Consolador,\nDivino Preceptor,\nEu louvarei.\nCom teu poder sem par,\nOh, vem me iluminar,\nE assim, ao teu fulgor,\nEu brilharei. Am\u00e9m."]'::json, NULL)
        ,
        ('70'::text, ' Descanso em Jesus'::text, '["Todo meu t\u00e3o vil pecado\nLan\u00e7o, \u00f3 Cristo, sobre ti,\nPois, Cordeiro imaculado,\nTu me d\u00e1s perd\u00e3o aqui.", "Minha mancha t\u00e3o impura\nTu bem podes retirar;\nE deixar em ti segura\nA minha alma descansar.", "Pobre sou, angustiado,\nMas revivo em tua cruz;\nE meu cora\u00e7\u00e3o cansado\nPonho s\u00f3 em ti Jesus!", "Minhas m\u00e1goas carregaste\nNo madeiro em meu lugar;\nVida eterna me compraste,\nSalva\u00e7\u00e3o me vieste dar.", "E na gl\u00f3ria eterna, ao lado\nDos fi\u00e9is em multid\u00e3o,\nMeus louvores, Cristo amado,\nMais perfeito te ser\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('71'::text, ' Perd�o'::text, '["Sim, sofrimento te causei, \u00f3 Deus!\nE ao meu exemplo o fraco trope\u00e7ou,\nE eu n\u00e3o andei nos bons caminhos teus:\nPerd\u00e3o, Senhor!", "Sim, v\u00e3o e f\u00fatil foi o meu falar,\nE amor ao meu irm\u00e3o n\u00e3o demonstrei;\nAo sofredor eu n\u00e3o quis ajudar:\nPerd\u00e3o, Senhor!", "Sim, negligente foi o meu viver,\nSem me dispor a pelejar por ti,\nE firme em teu trabalho eu n\u00e3o quis ser:\nPerd\u00e3o, Senhor!", "Escuta, \u00f3 Deus, a minha ora\u00e7\u00e3o\nE vem livrar-me do pecado vil.\nRenova este pobre cora\u00e7\u00e3o!\nAm\u00e9m, Senhor!"]'::json, NULL)
        ,
        ('72'::text, ' Purifica��o'::text, '["A minha alma est\u00e1 manchada\nDe pecado e corrup\u00e7\u00e3o,\nPois em mim n\u00e3o h\u00e1 justi\u00e7a,\nSantidade ou retid\u00e3o.", "Minha origem reconhe\u00e7o:\nPecador, bem sei, nasci;\nDa maldade procedente,\nO seu fardo j\u00e1 senti.", "Vem, Jesus, e santifica\nEste pobre pecador\nD\u00e1-me a gra\u00e7a de sentir-me\nAmparado em teu amor.", "O teu sangue precioso\nDerramado sobre a cruz,\nMe redime do pecado\nDe uma vez, \u00f3 meu Jesus.", "Vem, Senhor, purificar-me,\nTeu perd\u00e3o receberei.\nEu bem sei que para sempre\nAo teu lado reinarei. Am\u00e9m."]'::json, NULL)
        ,
        ('73'::text, ' Compaix�o'::text, '["A ti, Senhor, Atrevo-me a chegar;\nBem sei que sou indigno de favor.\nContrito venho para te implorar:\nTem compaix\u00e3o de mim, sou pecador!", "Sim, pecador, a suplicar perd\u00e3o,\nEmbora n\u00e3o mere\u00e7a o teu favor;\nMas d\u00e1-me, pe\u00e7o a purifica\u00e7\u00e3o!\nTem compaix\u00e3o de mim, sou pecador!", "Fugindo eu quis, em v\u00e3o, me libertar\nDo meu pecado vil e opressor!\nE agora, exausto, aqui eis-me a clamar:\nTem compaix\u00e3o de mim, sou pecador!", "T\u00e3o infinito, \u00f3 Deus, \u00e9 teu poder!\nT\u00e3o grande, \u00f3 Pai, o teu excelso amor!\nAos rogos meus n\u00e3o deixes de atender:\nTem compaix\u00e3o de mim, sou pecador! Am\u00e9m."]'::json, NULL)
        ,
        ('74'::text, ' Sinceridade'::text, '["Jesus Senhor, me achego a ti,\nTua ira santa mereci;\nO teu favor estende aqui!\nAceita um pecador!", "Eu venho como estou!\nSim, venho como estou!\nPorque Jesus por mim morreu,\nEu venho como estou!", "As minhas culpas grandes s\u00e3o,\nMas Tu, que n\u00e3o morreste em v\u00e3o,\nMe podes conceder perd\u00e3o!\nAceita um pecador!", "Oh! Vem agora Salvador,\nLivrar-me por teu grande amor,\nPois Tu, Jesus, \u00e9s meu Senhor,\nAceita um pecador. Am\u00e9m."]'::json, NULL)
        ,
        ('75'::text, ' Arrependimento e s�plica'::text, '["Abatido em meu pecado,\nClamo a ti, \u00f3 bom Senhor!\nOlha o pranto, o desencanto,\nDeste pobre pecador.\nDeus clemente e paciente,\nOh, liberta-me do mal,\nPara amar-te e consagrar-te\nMinha vida at\u00e9 ao final.", "Cada dia andaria\nLado a lado com Jesus,\nAdorando, exaltando\nAo excelso Deus de luz.\nMas ligado ao pecado,\nComo posso andar, Senhor?\nDos contritos os delitos,\nVem tirar, \u00f3 Redentor.", "Deus bondoso, caridoso,\nDa verdade eterno Autor,\nMeu pecado confessado,\nOh, perdoa por amor!\nDeus clemente, Pai do crente,\nEsperan\u00e7a e eterna luz,\nS\u00ea meu guia, noite e dia,\nPara a gl\u00f3ria de Jesus. Am\u00e9m."]'::json, NULL)
        ,
        ('76'::text, ' Compaix�o'::text, '["Tem compaix\u00e3o de mim Senhor!\nOh, mostra o teu excelso amor,\nE, na infinita multid\u00e3o\nDe tuas gra\u00e7as d\u00e1 perd\u00e3o.", "T\u00e3o pecador me descobri!\nPerdoa o mal que cometi.\nMinha alma venho aqui lavar;\nOh, n\u00e3o me deixes mais pecar.", "A mim Tu podes perdoar,\nDe toda mancha me lavar;\nVem dar-me um novo cora-\u00e7\u00e3o,\n\u00d3 Deus de amor e compaix\u00e3o.", "Por gera\u00e7\u00e3o, \u00f3 bom Senhor,\nN\u00e3o sou sen\u00e3o um pecador.\nEm ti me posso levantar\nE, como a neve, puro estar.", "Vem, pois, a mim ent\u00e3o valer,\nA meus clamores atender,\nE o cora\u00e7\u00e3o que triste est\u00e1\nDe eterno gozo exultar\u00e1. Am\u00e9m."]'::json, NULL)
        ,
        ('77'::text, ' Divino Perd�o'::text, '["Qu\u00e3o aben\u00e7oado aquele\nQue Jesus na cruz salvou!\nSeu pecado foi coberto,\nSua d\u00edvida passou\nPara o Justo, para o Justo,\nQue, a salvar-nos, expirou.", "Triste e envolto no sil\u00eancio,\nMeus pecados escondi;\nQue pesar de consci\u00eancia,\nQue mis\u00e9rias padeci!\nNoite e dia, noite e dia\nTua indigna\u00e7\u00e3o senti", "Mas por fim, desesperado,\nDescobri minha afli\u00e7\u00e3o.\nMeus delitos confessando\nEm Jesus achei perd\u00e3o.\nEsta gra\u00e7a, esta gra\u00e7a\nPede eterna gratid\u00e3o!", "Deus bondoso, todo crente\nTeu socorro implorar\u00e1;\nDos remorsos, vero al\u00edvio\nEm Jesus encontrar\u00e1.\nSanto gozo, santo gozo\nNele sempre reinar\u00e1!", "Deus bondoso! Deus clemente!\nTeu aux\u00edlio me dar\u00e1s\nE, com teu olhar divino,\nOs meus p\u00e9s conduzir\u00e1s.\nSempre d\u00f3cil, sempre d\u00f3cil\nE submisso me far\u00e1s.", "Em receios e tristezas\nAnda aflito o pecador,\nMas em Cristo bem guardados,\nPara n\u00f3s n\u00e3o h\u00e1 temor:\nExultamos, exultamos\nNo divino Salvador!"]'::json, NULL)
        ,
        ('78'::text, ' Perfeita expia��o'::text, '["Sacrif\u00edcios imolados\nSobre o sanguinoso altar\nN\u00e3o tiravam os pecados;\nN\u00e3o podiam expiar\nNossas culpas, nossas culpas,\nNem remorsos dissipar.", "Mas o sangue precioso\nQue na cruz o Redentor\nDerramou, t\u00e3o generoso,\nTem poder expiador;\nPurifica, purifica\nO mais \u00edmpio pecador.", "Triste choro o meu pecado,\nMas Jesus me d\u00e1 perd\u00e3o;\nE por ele perdoado,\nN\u00e3o verei condena\u00e7\u00e3o.\nO Cordeiro, o Cordeiro\nD\u00e1 completa remiss\u00e3o.", "Todo o peso do castigo\nPuni\u00e7\u00e3o que mereci!\nL\u00e1 na cruz supremo Amigo,\nFoi lan\u00e7ado sobre ti!\nVou cantando, vou cantando:\nMinha culpa estava ali"]'::json, NULL)
        ,
        ('79'::text, ' Gl�ria ao salvador'::text, '["Chegado \u00e0 cruz do bom Salvador,\nProstrado aos p\u00e9s do meu Redentor,\nEle atendeu logo ao meu clamor;\nGl\u00f3ria ao Salvador", "Gl\u00f3ria ao Salvador!\nGl\u00f3ria ao Salvador!\nEu sei que Cristo j\u00e1 me salvou;\nGl\u00f3ria ao Salvador!", "Que maravilha Jesus me amou!\nTudo de gra\u00e7a me perdoou!\nDos meus grilh\u00f5es ele me livrou!\nGl\u00f3ria ao Salvador!"]'::json, NULL)
        ,
        ('8'::text, ' Adora��o � Trindade.'::text, '["Grande Deus, o teu louvor\nHoje, unidos, entoamos;\nTeu excelso e doce amor\nCom os anjos celebramos.\nE em adora\u00e7\u00e3o a ti,\nVimos bendizer-te aqui.", "Cristo, Salvador veraz,\nCom poder em n\u00f3s domina!\nTua gra\u00e7a e tua paz,\n\u00d3 Senhor, ao mundo ensina.\nRedimido, em tua luz\nVem faz\u00ea-lo andar, Jesus!", "Santo Esp\u00edrito eternal,\nOh, dirige as nossas mentes\nPara, em comunh\u00e3o real,\nTe buscarmos reverentes;\nE o nosso cora\u00e7\u00e3o\nSe encher\u00e1 de gratid\u00e3o.", "\u00d3 Deus Trino, pois a ti\nSejam, sem cessar, rendidas\nPelos filhos teus aqui,\nHonra e gl\u00f3ria sem medida.\nInfinito \u00e9 o teu amor!\nCantem todos teu louvor! Am\u00e9m."]'::json, NULL)
        ,
        ('80'::text, ' O amor de Jesus'::text, '["Cantarei o imenso amor\nDo Senhor - amor sem fim! -\nDe Jesus que aqui desceu\nE morreu na cruz por mim.", "Falarei do grande amor\nCom louvor e gratid\u00e3o!\nServirei a meu Jesus,\nQue na cruz me deu perd\u00e3o.", "Antes mesmo de eu orar\nOu chorar minha afli\u00e7\u00e3o,\nMeu Jesus por mim chorou\nE implorou na cruz perd\u00e3o.", "Ningu\u00e9m pode calcular\nNem sondar t\u00e3o grande amor,\nQue me faz limpo e feliz,\nDo Juiz sem ter pavor.", "N\u00e3o mere\u00e7o tal favor,\nMas Jesus me soube amar;\nQuero pois com devo\u00e7\u00e3o\nMeu amor lhe dedicar."]'::json, NULL)
        ,
        ('81'::text, ' S�plica ao Esp�rito Santo'::text, '["Esp\u00edrito de Deus conv\u00e9m\nAo teu aux\u00edlio recorrer;\nManancial de todo bem,\nUsufru\u00edmos teu poder.\nE no teu santo amor andar\n\u00c9 nosso anseio ao te adorar.", "Sem ti o nosso culto \u00e9 v\u00e3o\nE nulo em tudo nos ser\u00e1;\nSem teu ensino e dire\u00e7\u00e3o\nNossa alma luzes n\u00e3o ter\u00e1;\nE inutilmente o teu louvor\nEntoaremos, \u00f3 Senhor.", "\u00d3 grande e santo Instruidor,\nInspira as nossas peti\u00e7\u00f5es,\nEnsina a orar e d\u00e1 fervor\nAos reverentes cora\u00e7\u00f5es;\nE ao trono eterno junto a Deus\nConduze aqueles que s\u00e3o teus.", "Fonte \u00e9s, e Autor de todo bem,\nLuz e esperan\u00e7a no viver;\nOpera em n\u00f3s o que conv\u00e9m,\nEm tudo imprime o teu querer.\nPerante o c\u00e9u ser\u00e1, ent\u00e3o,\nPerfeita a nossa reden\u00e7\u00e3o. Am\u00e9m."]'::json, NULL)
        ,
        ('82'::text, ' Divino preceptor'::text, '["\u00d3 divino Preceptor, j\u00e1 sentimos teu favor!\n\u00d3 Consolador real, tua gra\u00e7a \u00e9 divinal.", "Grande Instruidor veraz, desfrutamos tua paz!\nEncha o mundo a tua luz, guia os homens a Jesus!", "Santo Esp\u00edrito de Deus, que desceste l\u00e1 dos c\u00e9us,\nRevestindo de poder, santifica o nosso ser. Am\u00e9m."]'::json, NULL)
        ,
        ('83'::text, ' Divino instruidor'::text, '["\u00d3 divinal Instruidor\nQue nos mostraste o bom Senhor,\nCom tua luz e teu poder\nReveste sempre o nosso ser.", "\u00d3 inef\u00e1vel Preceptor,\nPor tua gra\u00e7a e teu favor\nInflama o nosso cora\u00e7\u00e3o\nNa vera e santa adora\u00e7\u00e3o.", "Consolador, Senhor e Deus,\nDom inef\u00e1vel l\u00e1 dos c\u00e9us.\nRevela ao mundo a tua luz,\nA Reden\u00e7\u00e3o que vem da cruz. Am\u00e9m."]'::json, NULL)
        ,
        ('84'::text, ' Santo Esp�rito'::text, '["Santo Esp\u00edrito de amor,\nCobre-me com teu poder!\nPurifica-me, Senhor,\nQuero, \u00f3 Deus, mais santo ser!", "Santo Esp\u00edrito de Deus,\nD\u00e1-me gra\u00e7a em teu poder!\nDe ti procede todo o bem,\nFaze-me mais santo ser!", "Tu atendes, \u00f3 Senhor,\nQuem te implora em contri\u00e7\u00e3o.\nCom tua gra\u00e7a e teu favor,\nGuia-me na retid\u00e3o!", "Eu confio em ti, Senhor,\nMeu viver vem transformar!\nQuero, pois, com gratid\u00e3o\nMinha vida consagrar! Am\u00e9m."]'::json, NULL)
        ,
        ('85'::text, ' Esp�rito consolador'::text, '["Vero Consolador, Esp\u00edrito de Deus,\nA tua paz e o teu amor revela aos que s\u00e3o teus.", "Tal como o fogo a arder e todo o mal queimar,\nAquece as almas com poder, ensina-nos a te amar.", "Como \u00f3leo a ungir um povo s\u00f3 em ti,\nA todos n\u00f3s faze sentir a tua bondade aqui.", "Nas trevas a brilhar com verdadeira luz,\nO pecador faze encontrar o bom Salvador Jesus.", "Como \u00e1gua Tu ser\u00e1s o purificador;\nE viva fonte abrir\u00e1s nos teus \u00e1trios, \u00f3 Senhor!"]'::json, NULL)
        ,
        ('86'::text, ' Esp�rito do Eterno Deus'::text, '["Esp\u00edrito do Eterno Deus,\nOpera em n\u00f3s.\nEsp\u00edrito do Eterno Deus,\nOpera em n\u00f3s.", "Quebranta-nos, consola-nos,\nTransforma-nos, transborda-nos!\nEsp\u00edrito do Eterno Deus,\nOpera em n\u00f3s. Am\u00e9m"]'::json, NULL)
        ,
        ('87'::text, ' Alegria perene'::text, '["Desperta e canta ao grande Deus,\nSim, \u00f3 minha alma, com fervor,\nPois muitos e preciosos bens\nA ti tem dado com amor.", "Oh, que grande amor!\nOh, divino amor\nDe Cristo pelo pecador!", "Perdido, Cristo me encontrou\nE dispensou-me compaix\u00e3o;\nPor seu t\u00e3o grande amor de pai,\nTirou-me desta condi\u00e7\u00e3o.", "Eu sei que \u00e9 vil meu cora\u00e7\u00e3o;\nPropende sempre para o mal.\nMas ele em paz me guardar\u00e1\nCom seu amor que \u00e9 perenal."]'::json, NULL)
        ,
        ('88'::text, ' Amor perene'::text, '["Amavas-me, Senhor, ainda cintilante\nN\u00e3o irrompera a luz ao mando Criador!\nE nem o ardente sol, rompendo no levante,\nTrouxera \u00e0 terra e ao mar a for\u00e7a fecundante,\nMeu Deus, que amor!\nMeu Deus, que eterno amor!", "Amavas-me, Senhor, tamb\u00e9m quando, imolado,\nPor mim sofreu na cruz o meigo Salvador,\nO Santo de Israel, o teu Cordeiro amado,\nLevando sobre si a culpa do pecado.\nMeu Deus, que amor!\nMeu Deus, que antigo amor!", "Amavas-me, Senhor, quando atingiu meu peito\nO Esp\u00edrito de luz, o meu Consolador.\nE com tesouros mil, de teu favor perfeito,\nTrouxe \u00e0 minha alma a f\u00e9 em que hoje me deleito.\nMeu Deus, que amor!\nMeu Deus, que antigo amor!", "E sempre me amar\u00e1s, porque jamais inferno\nOu mundo poder\u00e3o ao teu querer se opor,\nAo teu decreto, \u00f3 Deus, ao teu decreto eterno,\nAo teu amor, \u00f3 Pai, ao teu amor superno.\nMeu Deus, que amor!\n\u00c9s sempre e todo amor! Am\u00e9m."]'::json, NULL)
        ,
        ('89'::text, ' Amor sacrificial'::text, '["O grande amor que meu Jesus\nPor mim mostrou na rude cruz,\nMorrendo, a fim de me salvar,\nQuem poder\u00e1 contar?", "Quem pode o seu amor contar?\nQuem pode o seu amor contar?\nO grande amor de meu Jesus,\nQuem poder\u00e1 contar?", "O c\u00e1lice que Jesus bebeu\nA maldi\u00e7\u00e3o que padeceu\nPor mim e para me salvar,\nQuem poder\u00e1 contar?", "A zombaria t\u00e3o cruel,\nA cruz horrenda o amargo fel,\nSofridos para me salvar,\nQuem poder\u00e1 contar?", "Incompar\u00e1vel Salvador,\nTeu inef\u00e1vel, santo amor\nQue \u00e9 imposs\u00edvel de sondar,\nQuem poder\u00e1 contar?"]'::json, NULL)
        ,
        ('9'::text, ' Aleluia ao Deus trino'::text, '["A ti, \u00f3 Deus, Alt\u00edssimo Senhor,\nEterno Pai, supremo Benfeitor,\nTeus filhos v\u00eam, alegres, dar louvor.\nAleluia! Aleluia!", "A ti, Deus Filho, Salvador Jesus,\nDa gra\u00e7a a fonte, da verdade a luz,\nPor teu amor mostrado sobre a cruz,\nAleluia! Aleluia!", "A ti, \u00f3 Deus, Esp\u00edrito de amor,\nDe nossas almas santificador.\nMestre divino, bom Consolador,\nAleluia! Aleluia!", "\u00d3 Deus Tri\u00fano, vem-nos conceder\nA plenitude do real poder,\nE as nossas almas vem, aqui, reger.\nAleluia! Aleluia! Am\u00e9m."]'::json, NULL)
        ,
        ('90'::text, ' Amor insond�vel'::text, '["Qu\u00e3o insond\u00e1vel \u00e9 o amor\nDo Onipotente Criador,\nO santo e eterno Deus!\nOs pecadores quis salvar,\nNo Filho amado aqui buscar\nUm povo para os c\u00e9us", "Inexaur\u00edvel esse amor\nIntraduz\u00edvel o favor\nDe Cristo, o Rei Jesus.\nOs desvalidos acudiu,\nOs transviados atraiu\n\u00c0 sua santa luz.", "Qu\u00e3o puro e santo o imenso amor\nQue Deus, ao mundo pecador,\nEm Cristo revelou!\nCom f\u00e9 meu pobre cora\u00e7\u00e3o\nRecebe a plena reden\u00e7\u00e3o\nQue nele me outorgou.", "\u00d3 Deus, qu\u00e3o pobre e frio amor,\nQu\u00e3o inconstante seguidor\nDescobrir\u00e1s em mim! Aviva-me\nCom teu poder, pois quero te obedecer\nE amar-te at\u00e9 ao fim! Am\u00e9m."]'::json, NULL)
        ,
        ('91'::text, ' A f� dos antigos'::text, '["Oh, viva f\u00e9 que nossos pais\nHonraram sempre com valor.\nN\u00f3s cantaremos mais e mais\nTeus grandes feitos com ardor!", "Sublime f\u00e9, clar\u00e3o de luz,\nInspira\u00e7\u00e3o que vem da cruz.", "Bravos, quiseram nossos pais\nPor ti as chamas afrontar;\nE tal como esses ancestrais,\nQueremos n\u00f3s tamb\u00e9m andar.", "Alto pend\u00e3o, sublime f\u00e9!\nPor ti queremos pelejar\nTudo sofrendo, a morte at\u00e9,\nE firmes sempre a ti honrar."]'::json, NULL)
        ,
        ('92'::text, ' A f� contemplada'::text, '["Deus promete grandes coisas conceder\nA qualquer que pe\u00e7a, crendo que h\u00e1 de obter,\nEsperando, sem na f\u00e9 enfraquecer.\nO Senhor a f\u00e9 contemplar\u00e1!", "Tua f\u00e9 Jesus contemplar\u00e1\nSim, o que Jesus promete, d\u00e1.\nEle v\u00ea o cora\u00e7\u00e3o\nE responde \u00e0 peti\u00e7\u00e3o;\nTua f\u00e9 Jesus contemplar\u00e1!", "Quantas maravilhas Deus realizou\nPor algu\u00e9m que firme nele confiou,\nE que da promessa em nada duvidou.\nO Senhor a f\u00e9 contemplar\u00e1!", "Sim creiamos nos que Deus nos prometeu,\nPois jamais desonrar\u00e1 o nome seu;\nEle cumprir\u00e1 promessas que nos deu.\nO Senhor a f\u00e9 contemplar\u00e1!"]'::json, NULL)
        ,
        ('93'::text, ' Firmeza na f�'::text, '["Somente ponho a minha f\u00e9\nNa gra\u00e7a excelsa de Jesus,\nNo sacrif\u00edcio remidor,\nNo sangue do bom Redentor.", "A minha f\u00e9 e o meu amor\nEst\u00e3o firmados no Senhor,\nEst\u00e3o firmados no Senhor.", "Se n\u00e3o lhe posso a face ver,\n\u00c9 pela f\u00e9 que vou viver;\nEm cada transe a suportar\nEu hei de nele confiar.", "A sua gra\u00e7a \u00e9 mui real,\nAbrigo traz no temporal;\nAo vir cercar-me a tenta\u00e7\u00e3o,\nMe fortalece a sua m\u00e3o.", "Quando a trombeta ressoar,\nIrei com ele me encontrar\nE com os salvos cantarei\nLouvor eterno ao grande Rei!"]'::json, NULL)
        ,
        ('94'::text, ' A porta da salva��o'::text, '["\u00c9 franca a porta divinal,\nAberta a todo o mundo.\nPor ela o pecador mortal\nAvista amor profundo", "Oh! Gra\u00e7a imensa! Pois assim,\nJesus convida: Vinde a mim!\nA mim, a mim;\nConvida: Vinde a mim!", "Entrai! Jesus vos d\u00e1 perd\u00e3o,\nAs culpas redimindo.\nEntrai, buscando a salva\u00e7\u00e3o,\nPois seu amor \u00e9 infindo.", "Aberta, sim, de par em par!\nEntrai com grande urg\u00eancia,\nPois Deus a todos quer mostrar\nReal munific\u00eancia."]'::json, NULL)
        ,
        ('95'::text, ' Somente Cristo'::text, '["Somente Cristo \u00e9 Salvador,\nAssim reconhecemos;\nMorreu por n\u00f3s, em santo amor,\nE vida nele temos.", "Jesus, Jesus \u00e9 o Salvador,\nE n\u00e3o h\u00e1 outro Mediador!\nFoi ele quem na cruz morreu,\nE vivo est\u00e1 nos altos c\u00e9us.", "Os homens santos e fi\u00e9is,\nN\u00f3s sempre respeitamos;\nMas a Jesus, o Rei dos reis,\nHumilde adoramos.", "A Deus fazemos ora\u00e7\u00e3o\nS\u00f3 ele quer e pode\nValer-nos na tribula\u00e7\u00e3o;\nAos seus Deus sempre acolhe.", "No santo sangue de Jesus\nConfia todo crente;\nO que ele fez na dura cruz\n\u00c9 sempre suficiente."]'::json, NULL)
        ,
        ('96'::text, ' Reden��o suprema'::text, '["O peso do pecado Jesus a si tomou;\nDe Deus a justa ira na rude cruz provou.\nPagou por teus pecados! Sofreu em teu lugar!\nPor ti, por mim, por todos, e assim nos quis salvar!", "Perfeito e bom resgate pagou o Salvador,\nAo derramar seu sangue remiu o pecador.\nOh, reden\u00e7\u00e3o suprema! Favor celestial!\nEntrada agora temos na casa paternal!", "Pois Deus ergueu da morte o Redentor Jesus!\n\u00c9 vivo, ressurreto, o que expirou na cruz!\nAgora, entronizado no c\u00e9u, o Salvador\n\u00c9 sempre o mesmo Amigo do pobre pecador."]'::json, NULL)
        ,
        ('97'::text, ' S�plica do redimido'::text, '["Meu pecado resgatado\nFoi na cruz, por teu amor!\nE da morte - triste sorte! -\nMe livraste, \u00f3 Redentor!", "Cristo, inflama viva chama\nEm meu peito, \u00f3 Salvador!\nPois te adora quem te implora:\nVem guiar-me, bom Senhor!", "Se hesitante, vacilante,\nOu\u00e7o a voz do tentador,\nTu me guias, me auxilias,\nE me tornas vencedor.", "Redimida s\u00f3 tem vida\nA minha alma em teu amor!\nCom apre\u00e7o reconhe\u00e7o\nQuanto devo a ti, Senhor."]'::json, NULL)
        ,
        ('98'::text, ' N�o h� condena��o'::text, '["\u201cN\u00e3o h\u00e1 condena\u00e7\u00e3o!\u201d\nAssim diz o Senhor.\nPois temos ante o tribunal\nPerfeito defensor.", "\u201cN\u00e3o h\u00e1 condena\u00e7\u00e3o!\u201d\nO justo e santo Deus\nAceita a Cristo, o Mediador,\nQue entrou por n\u00f3s nos c\u00e9us.", "\u201cN\u00e3o h\u00e1 condena\u00e7\u00e3o!\u201d\nRepousa, \u00f3 alma, aqui!\n\u00c9 Cristo, quem na cruz morreu,\nTeu advogado ali.", "\u201cN\u00e3o h\u00e1 condena\u00e7\u00e3o!\u201d\nO Cristo triunfou!\nPagando alto pre\u00e7o, ali\nNa cruz nos libertou!"]'::json, NULL)
        ,
        ('99'::text, ' Louvor ao Redentor'::text, '["Minha alma, louva ao Redentor,\nJesus, teu Rei, teu Salvador;\nMorreu na cruz, em teu lugar,\nE salva\u00e7\u00e3o te veio dar.", "Sim, sobre a cruz, em meu lugar,\nJesus morreu e me salvou!\nFirme e seguro posso andar,\nPorque seu sangue me lavou.", "Oh, qu\u00e3o perdido e longe andei,\nRebelde a Deus e \u00e0 sua lei!\nCom terno e t\u00e3o paciente amor,\nSeguiu-me sempre o Salvador.", "Bendito o dia quando enfim,\nVi Cristo sobre a cruz, por mim!\nVencido por t\u00e3o grande amor,\nRendi-me logo ao Salvador.", "Vem alma aflita descansar,\nJesus te pode perdoar!\nA ele entrega o cora\u00e7\u00e3o\nE teu ser\u00e1 o seu perd\u00e3o."]'::json, NULL);

